"use client";

import React, { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { AlertCircle, Brain, DollarSign, Edit, Plus, Settings, Zap } from 'lucide-react';
import { toast } from 'sonner';

interface AIModel {
  modelId: string;
  displayName: string;
  provider: string;
  isActive: boolean;
}

interface AIInteractionType {
  typeId: string;
  typeName: string;
  displayName: string;
  description?: string;
  defaultModelId?: string;
  defaultModel?: AIModel;
  createdAt: string;
  updatedAt: string;
}

interface TypeFormData {
  typeName: string;
  displayName: string;
  description: string;
  defaultModelId: string;
}

const initialFormData: TypeFormData = {
  typeName: '',
  displayName: '',
  description: '',
  defaultModelId: '',
};

export default function AIInteractionsPage() {
  const [interactionTypes, setInteractionTypes] = useState<AIInteractionType[]>([]);
  const [models, setModels] = useState<AIModel[]>([]);
  const [loading, setLoading] = useState(true);
  const [isCreateOpen, setIsCreateOpen] = useState(false);
  const [editingType, setEditingType] = useState<AIInteractionType | null>(null);
  const [formData, setFormData] = useState<TypeFormData>(initialFormData);
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [typesResponse, modelsResponse] = await Promise.all([
        fetch('/api/admin/ai-interaction-types'),
        fetch('/api/admin/ai-models')
      ]);

      if (typesResponse.ok && modelsResponse.ok) {
        const [typesData, modelsData] = await Promise.all([
          typesResponse.json(),
          modelsResponse.json()
        ]);
        setInteractionTypes(typesData);
        setModels(modelsData.filter((m: AIModel) => m.isActive));
      } else {
        toast.error('Failed to fetch data');
      }
    } catch (error) {
      toast.error('Error fetching data');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setSubmitting(true);

    try {
      const url = editingType 
        ? `/api/admin/ai-interaction-types/${editingType.typeId}`
        : '/api/admin/ai-interaction-types';

      const method = editingType ? 'PUT' : 'POST';

      const payload = {
        ...formData,
        defaultModelId: formData.defaultModelId === 'none' ? null : formData.defaultModelId || null,
      };

      const response = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });

      if (response.ok) {
        toast.success(editingType ? 'Interaction type updated successfully' : 'Interaction type created successfully');
        setIsCreateOpen(false);
        setEditingType(null);
        setFormData(initialFormData);
        fetchData();
      } else {
        const error = await response.json();
        toast.error(error.error || 'Failed to save interaction type');
      }
    } catch (error) {
      toast.error('Error saving interaction type');
    } finally {
      setSubmitting(false);
    }
  };

  const handleEdit = (type: AIInteractionType) => {
    setEditingType(type);
    setFormData({
      typeName: type.typeName,
      displayName: type.displayName,
      description: type.description || '',
      defaultModelId: type.defaultModelId || '',
    });
    setIsCreateOpen(true);
  };

  const handleDelete = async (typeId: string) => {
    if (!confirm('Are you sure you want to delete this interaction type?')) {
      return;
    }

    try {
      const response = await fetch(`/api/admin/ai-interaction-types/${typeId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        toast.success('Interaction type deleted successfully');
        fetchData();
      } else {
        const error = await response.json();
        toast.error(error.error || 'Failed to delete interaction type');
      }
    } catch (error) {
      toast.error('Error deleting interaction type');
    }
  };

  const resetForm = () => {
    setFormData(initialFormData);
    setEditingType(null);
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-orange-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">AI Interaction Types</h1>
          <p className="text-gray-600">Configure AI models for different types of interactions</p>
        </div>
        <Dialog open={isCreateOpen} onOpenChange={(open) => {
          setIsCreateOpen(open);
          if (!open) resetForm();
        }}>
          <DialogTrigger asChild>
            <Button>
              <Plus className="h-4 w-4 mr-2" />
              Add Interaction Type
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-lg">
            <DialogHeader>
              <DialogTitle>{editingType ? 'Edit Interaction Type' : 'Add New Interaction Type'}</DialogTitle>
              <DialogDescription>
                {editingType ? 'Update the interaction type configuration' : 'Configure a new type of AI interaction'}
              </DialogDescription>
            </DialogHeader>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <Label htmlFor="typeName">Type Name (Internal)</Label>
                <Input
                  id="typeName"
                  value={formData.typeName}
                  onChange={(e) => setFormData({ ...formData, typeName: e.target.value })}
                  placeholder="article_generation"
                  required
                  disabled={editingType !== null}
                  pattern="[a-z_]+"
                  title="Use lowercase letters and underscores only"
                />
                <p className="text-xs text-gray-500 mt-1">Used internally by the system</p>
              </div>

              <div>
                <Label htmlFor="displayName">Display Name</Label>
                <Input
                  id="displayName"
                  value={formData.displayName}
                  onChange={(e) => setFormData({ ...formData, displayName: e.target.value })}
                  placeholder="Article Generation"
                  required
                />
              </div>

              <div>
                <Label htmlFor="description">Description</Label>
                <Textarea
                  id="description"
                  value={formData.description}
                  onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                  placeholder="Generate full article content using AI"
                />
              </div>

              <div>
                <Label htmlFor="defaultModel">Default AI Model</Label>
                <Select 
                  value={formData.defaultModelId} 
                  onValueChange={(value) => setFormData({ ...formData, defaultModelId: value })}
                >
                  <SelectTrigger>
                    <SelectValue placeholder="Select default model" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="none">No default model</SelectItem>
                    {models.map((model) => (
                      <SelectItem key={model.modelId} value={model.modelId}>
                        {model.displayName} ({model.provider})
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
                <p className="text-xs text-gray-500 mt-1">Model to use for this type of interaction</p>
              </div>

              <div className="flex justify-end space-x-2 pt-4">
                <Button 
                  type="button" 
                  variant="outline" 
                  onClick={() => setIsCreateOpen(false)}
                  disabled={submitting}
                >
                  Cancel
                </Button>
                <Button type="submit" disabled={submitting}>
                  {submitting ? 'Saving...' : editingType ? 'Update Type' : 'Create Type'}
                </Button>
              </div>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      <div className="grid gap-6">
        {interactionTypes.length === 0 ? (
          <Card>
            <CardContent className="flex items-center justify-center h-32">
              <div className="text-center">
                <Zap className="h-8 w-8 text-gray-400 mx-auto mb-2" />
                <p className="text-gray-500">No interaction types configured yet</p>
              </div>
            </CardContent>
          </Card>
        ) : (
          interactionTypes.map((type) => (
            <Card key={type.typeId}>
              <CardHeader>
                <div className="flex items-center justify-between">
                  <div>
                    <CardTitle className="flex items-center gap-2">
                      <Zap className="h-5 w-5" />
                      {type.displayName}
                    </CardTitle>
                    <CardDescription>
                      {type.typeName}
                    </CardDescription>
                  </div>
                  <div className="flex items-center gap-2">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => handleEdit(type)}
                    >
                      <Edit className="h-4 w-4" />
                    </Button>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => handleDelete(type.typeId)}
                      className="text-red-600 hover:text-red-700"
                    >
                      <Settings className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              </CardHeader>
              <CardContent>
                {type.description && (
                  <p className="text-gray-600 mb-4">{type.description}</p>
                )}
                <div className="flex items-center justify-between">
                  <div>
                    <span className="font-medium text-gray-900">Default Model:</span>
                    {type.defaultModel ? (
                      <Badge variant="secondary" className="ml-2">
                        <Brain className="h-3 w-3 mr-1" />
                        {type.defaultModel.displayName}
                      </Badge>
                    ) : (
                      <span className="text-gray-500 ml-2">No default model</span>
                    )}
                  </div>
                  {!type.defaultModel?.isActive && type.defaultModel && (
                    <div className="flex items-center text-amber-600">
                      <AlertCircle className="h-4 w-4 mr-1" />
                      <span className="text-sm">Default model is inactive</span>
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          ))
        )}
      </div>

      {models.length === 0 && (
        <Card className="border-amber-200 bg-amber-50">
          <CardContent className="flex items-center justify-center p-6">
            <div className="text-center">
              <AlertCircle className="h-8 w-8 text-amber-600 mx-auto mb-2" />
              <p className="text-amber-800 font-medium">No active AI models available</p>
              <p className="text-amber-700 text-sm">Add and activate AI models before configuring interaction types.</p>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

"use client";

import React, { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Label } from '@/components/ui/label';
import { Switch } from '@/components/ui/switch';
import { AlertCircle, Brain, DollarSign, Edit, Plus, Trash2, Eye, EyeOff } from 'lucide-react';
import { toast } from 'sonner';

interface AIModel {
  modelId: string;
  modelName: string;
  provider: string;
  displayName: string;
  description?: string;
  inputTokenCostPer1M: number;
  outputTokenCostPer1M: number;
  maxTokens?: number;
  isActive: boolean;
  isDefault: boolean;
  createdAt: string;
  updatedAt: string;
}

interface ModelFormData {
  modelName: string;
  provider: string;
  displayName: string;
  description: string;
  apiKey: string;
  inputTokenCostPer1M: string;
  outputTokenCostPer1M: string;
  maxTokens: string;
  isDefault: boolean;
}

const initialFormData: ModelFormData = {
  modelName: '',
  provider: 'openai',
  displayName: '',
  description: '',
  apiKey: '',
  inputTokenCostPer1M: '',
  outputTokenCostPer1M: '',
  maxTokens: '',
  isDefault: false,
};

export default function AIModelsPage() {
  const [models, setModels] = useState<AIModel[]>([]);
  const [loading, setLoading] = useState(true);
  const [isCreateOpen, setIsCreateOpen] = useState(false);
  const [editingModel, setEditingModel] = useState<AIModel | null>(null);
  const [formData, setFormData] = useState<ModelFormData>(initialFormData);
  const [showApiKey, setShowApiKey] = useState(false);
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    fetchModels();
  }, []);

  const fetchModels = async () => {
    try {
      const response = await fetch('/api/admin/ai-models');
      if (response.ok) {
        const data = await response.json();
        setModels(data);
      } else {
        toast.error('Failed to fetch AI models');
      }
    } catch (error) {
      toast.error('Error fetching AI models');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setSubmitting(true);

    try {
      const url = editingModel 
        ? `/api/admin/ai-models/${editingModel.modelId}`
        : '/api/admin/ai-models';

      const method = editingModel ? 'PUT' : 'POST';

      const payload: any = {
        ...formData,
        inputTokenCostPer1M: parseFloat(formData.inputTokenCostPer1M),
        outputTokenCostPer1M: parseFloat(formData.outputTokenCostPer1M),
        maxTokens: formData.maxTokens ? parseInt(formData.maxTokens) : null,
      };

      // Only include API key if it's provided (for updates, empty means no change)
      if (!editingModel || formData.apiKey.trim()) {
        payload.apiKey = formData.apiKey;
      }

      const response = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });

      if (response.ok) {
        toast.success(editingModel ? 'Model updated successfully' : 'Model created successfully');
        setIsCreateOpen(false);
        setEditingModel(null);
        setFormData(initialFormData);
        fetchModels();
      } else {
        const error = await response.json();
        toast.error(error.error || 'Failed to save model');
      }
    } catch (error) {
      toast.error('Error saving model');
    } finally {
      setSubmitting(false);
    }
  };

  const handleEdit = (model: AIModel) => {
    setEditingModel(model);
    setFormData({
      modelName: model.modelName,
      provider: model.provider,
      displayName: model.displayName,
      description: model.description || '',
      apiKey: '', // Don't show existing API key
      inputTokenCostPer1M: model.inputTokenCostPer1M.toString(),
      outputTokenCostPer1M: model.outputTokenCostPer1M.toString(),
      maxTokens: model.maxTokens?.toString() || '',
      isDefault: model.isDefault,
    });
    setIsCreateOpen(true);
  };

  const handleDelete = async (modelId: string) => {
    if (!confirm('Are you sure you want to delete this model?')) {
      return;
    }

    try {
      const response = await fetch(`/api/admin/ai-models/${modelId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        toast.success('Model deleted successfully');
        fetchModels();
      } else {
        const error = await response.json();
        toast.error(error.error || 'Failed to delete model');
      }
    } catch (error) {
      toast.error('Error deleting model');
    }
  };

  const toggleModelStatus = async (modelId: string, isActive: boolean) => {
    try {
      const response = await fetch(`/api/admin/ai-models/${modelId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ isActive: !isActive }),
      });

      if (response.ok) {
        toast.success(`Model ${!isActive ? 'activated' : 'deactivated'} successfully`);
        fetchModels();
      } else {
        const error = await response.json();
        toast.error(error.error || 'Failed to update model status');
      }
    } catch (error) {
      toast.error('Error updating model status');
    }
  };

  const resetForm = () => {
    setFormData(initialFormData);
    setEditingModel(null);
    setShowApiKey(false);
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-orange-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">AI Models</h1>
          <p className="text-gray-600">Manage AI models and their configurations</p>
        </div>
        <Dialog open={isCreateOpen} onOpenChange={(open) => {
          setIsCreateOpen(open);
          if (!open) resetForm();
        }}>
          <DialogTrigger asChild>
            <Button>
              <Plus className="h-4 w-4 mr-2" />
              Add Model
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>{editingModel ? 'Edit Model' : 'Add New Model'}</DialogTitle>
              <DialogDescription>
                {editingModel ? 'Update the AI model configuration' : 'Configure a new AI model for the platform'}
              </DialogDescription>
            </DialogHeader>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="modelName">Model Name</Label>
                  <Input
                    id="modelName"
                    value={formData.modelName}
                    onChange={(e) => setFormData({ ...formData, modelName: e.target.value })}
                    placeholder="gpt-4-0125-preview"
                    required
                    disabled={editingModel !== null}
                  />
                </div>
                <div>
                  <Label htmlFor="provider">Provider</Label>
                  <Select 
                    value={formData.provider} 
                    onValueChange={(value) => setFormData({ ...formData, provider: value })}
                    disabled={editingModel !== null}
                  >
                    <SelectTrigger>
                      <SelectValue placeholder="Select provider" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="openai">OpenAI</SelectItem>
                      <SelectItem value="anthropic">Anthropic</SelectItem>
                      <SelectItem value="google">Google</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <div>
                <Label htmlFor="displayName">Display Name</Label>
                <Input
                  id="displayName"
                  value={formData.displayName}
                  onChange={(e) => setFormData({ ...formData, displayName: e.target.value })}
                  placeholder="GPT-4 Turbo"
                  required
                />
              </div>

              <div>
                <Label htmlFor="description">Description</Label>
                <Textarea
                  id="description"
                  value={formData.description}
                  onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                  placeholder="Description of this model's capabilities"
                />
              </div>

              <div>
                <Label htmlFor="apiKey">API Key</Label>
                <div className="relative">
                  <Input
                    id="apiKey"
                    type={showApiKey ? "text" : "password"}
                    value={formData.apiKey}
                    onChange={(e) => setFormData({ ...formData, apiKey: e.target.value })}
                    placeholder={editingModel ? "Leave empty to keep current key" : "Enter API key"}
                    required={!editingModel}
                  />
                  <Button
                    type="button"
                    variant="ghost"
                    size="sm"
                    className="absolute right-2 top-1/2 transform -translate-y-1/2 h-6 w-6 p-0"
                    onClick={() => setShowApiKey(!showApiKey)}
                  >
                    {showApiKey ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                  </Button>
                </div>
              </div>

              <div className="grid grid-cols-3 gap-4">
                <div>
                  <Label htmlFor="inputCost">Input Cost (per 1M tokens)</Label>
                  <Input
                    id="inputCost"
                    type="number"
                    step="0.000001"
                    value={formData.inputTokenCostPer1M}
                    onChange={(e) => setFormData({ ...formData, inputTokenCostPer1M: e.target.value })}
                    placeholder="10.0"
                    required
                  />
                </div>
                <div>
                  <Label htmlFor="outputCost">Output Cost (per 1M tokens)</Label>
                  <Input
                    id="outputCost"
                    type="number"
                    step="0.000001"
                    value={formData.outputTokenCostPer1M}
                    onChange={(e) => setFormData({ ...formData, outputTokenCostPer1M: e.target.value })}
                    placeholder="30.0"
                    required
                  />
                </div>
                <div>
                  <Label htmlFor="maxTokens">Max Tokens (optional)</Label>
                  <Input
                    id="maxTokens"
                    type="number"
                    value={formData.maxTokens}
                    onChange={(e) => setFormData({ ...formData, maxTokens: e.target.value })}
                    placeholder="4096"
                  />
                </div>
              </div>

              <div className="flex items-center space-x-2">
                <Switch
                  id="isDefault"
                  checked={formData.isDefault}
                  onCheckedChange={(checked) => setFormData({ ...formData, isDefault: checked })}
                />
                <Label htmlFor="isDefault">Set as default model</Label>
              </div>

              <div className="flex justify-end space-x-2 pt-4">
                <Button 
                  type="button" 
                  variant="outline" 
                  onClick={() => setIsCreateOpen(false)}
                  disabled={submitting}
                >
                  Cancel
                </Button>
                <Button type="submit" disabled={submitting}>
                  {submitting ? 'Saving...' : editingModel ? 'Update Model' : 'Create Model'}
                </Button>
              </div>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      <div className="grid gap-6">
        {models.length === 0 ? (
          <Card>
            <CardContent className="flex items-center justify-center h-32">
              <div className="text-center">
                <Brain className="h-8 w-8 text-gray-400 mx-auto mb-2" />
                <p className="text-gray-500">No AI models configured yet</p>
              </div>
            </CardContent>
          </Card>
        ) : (
          models.map((model) => (
            <Card key={model.modelId}>
              <CardHeader>
                <div className="flex items-center justify-between">
                  <div>
                    <CardTitle className="flex items-center gap-2">
                      <Brain className="h-5 w-5" />
                      {model.displayName}
                      {model.isDefault && <Badge variant="secondary">Default</Badge>}
                      {!model.isActive && <Badge variant="destructive">Inactive</Badge>}
                    </CardTitle>
                    <CardDescription>
                      {model.provider} â€¢ {model.modelName}
                    </CardDescription>
                  </div>
                  <div className="flex items-center gap-2">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => toggleModelStatus(model.modelId, model.isActive)}
                    >
                      {model.isActive ? 'Deactivate' : 'Activate'}
                    </Button>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => handleEdit(model)}
                    >
                      <Edit className="h-4 w-4" />
                    </Button>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => handleDelete(model.modelId)}
                      className="text-red-600 hover:text-red-700"
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              </CardHeader>
              <CardContent>
                {model.description && (
                  <p className="text-gray-600 mb-4">{model.description}</p>
                )}
                <div className="grid grid-cols-3 gap-4 text-sm">
                  <div>
                    <span className="font-medium text-gray-900">Input Cost:</span>
                    <div className="flex items-center text-green-600">
                      <DollarSign className="h-3 w-3 mr-1" />
                      {model.inputTokenCostPer1M}/1M tokens
                    </div>
                  </div>
                  <div>
                    <span className="font-medium text-gray-900">Output Cost:</span>
                    <div className="flex items-center text-green-600">
                      <DollarSign className="h-3 w-3 mr-1" />
                      {model.outputTokenCostPer1M}/1M tokens
                    </div>
                  </div>
                  <div>
                    <span className="font-medium text-gray-900">Max Tokens:</span>
                    <div className="text-gray-600">
                      {model.maxTokens ? model.maxTokens.toLocaleString() : 'Unlimited'}
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))
        )}
      </div>
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import {
  BarChart,
  Bar,
  LineChart,
  Line,
  PieChart,
  Pie,
  Cell,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer
} from 'recharts';

type TimePeriod = 'hour' | 'day' | 'week' | 'month' | 'year' | 'all';

interface ModelData {
  modelId: string;
  modelName: string;
  provider: string;
  interactions: number;
  inputTokens: number;
  outputTokens: number;
  totalTokens: number;
  inputCost: number;
  outputCost: number;
  totalCost: number;
}

interface InteractionTypeData {
  typeId: string;
  typeName: string;
  displayName: string;
  interactions: number;
  inputTokens: number;
  outputTokens: number;
  totalTokens: number;
  inputCost: number;
  outputCost: number;
  totalCost: number;
}

interface UserData {
  userId: string;
  email: string;
  displayName: string;
  interactions: number;
  inputTokens: number;
  outputTokens: number;
  totalTokens: number;
  inputCost: number;
  outputCost: number;
  totalCost: number;
}

interface TotalsData {
  totalInteractions: number;
  successfulInteractions: number;
  failedInteractions: number;
  successRate: string;
  uniqueUsers: number;
  totalInputTokens: number;
  totalOutputTokens: number;
  totalTokens: number;
  totalInputCost: number;
  totalOutputCost: number;
  totalCost: number;
  averageResponseTime: number;
  averageCostPerInteraction: number;
}

const COLORS = ['#3b82f6', '#10b981', '#f59e0b', '#ef4444', '#8b5cf6', '#ec4899'];

export default function AIReportsPage() {
  const [period, setPeriod] = useState<TimePeriod>('day');
  const [modelData, setModelData] = useState<ModelData[]>([]);
  const [interactionData, setInteractionData] = useState<InteractionTypeData[]>([]);
  const [userData, setUserData] = useState<UserData[]>([]);
  const [totalsData, setTotalsData] = useState<TotalsData | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchAllData();
  }, [period]);

  const fetchAllData = async () => {
    setLoading(true);
    try {
      const [modelsRes, interactionsRes, usersRes, totalsRes] = await Promise.all([
        fetch(`/api/admin/ai-reports/by-model?period=${period}`),
        fetch(`/api/admin/ai-reports/by-interaction?period=${period}`),
        fetch(`/api/admin/ai-reports/by-user?period=${period}`),
        fetch(`/api/admin/ai-reports/totals?period=${period}`)
      ]);

      if (modelsRes.ok) {
        const data = await modelsRes.json();
        setModelData(data.data);
      }

      if (interactionsRes.ok) {
        const data = await interactionsRes.json();
        setInteractionData(data.data);
      }

      if (usersRes.ok) {
        const data = await usersRes.json();
        setUserData(data.data);
      }

      if (totalsRes.ok) {
        const data = await totalsRes.json();
        setTotalsData(data.data);
      }
    } catch (error) {
      console.error('Error fetching AI reports:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (value: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 2,
      maximumFractionDigits: 4
    }).format(value);
  };

  const formatNumber = (value: number) => {
    return new Intl.NumberFormat('en-US').format(value);
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-lg text-gray-600">Loading AI reports...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 p-8">
      <div className="max-w-7xl mx-auto">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-4">AI Usage Reports</h1>

          {/* Time Period Selector */}
          <div className="flex space-x-2">
            {(['hour', 'day', 'week', 'month', 'year', 'all'] as TimePeriod[]).map((p) => (
              <button
                key={p}
                onClick={() => setPeriod(p)}
                className={`px-4 py-2 rounded-lg font-medium capitalize transition-colors ${
                  period === p
                    ? 'bg-blue-600 text-white'
                    : 'bg-white text-gray-700 hover:bg-gray-100'
                }`}
              >
                {p === 'all' ? 'All Time' : p}
              </button>
            ))}
          </div>
        </div>

        {/* Total Stats */}
        {totalsData && (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
            <div className="bg-white rounded-lg shadow p-6">
              <h3 className="text-sm font-medium text-gray-500 mb-2">Total Interactions</h3>
              <p className="text-2xl font-bold text-gray-900">{formatNumber(totalsData.totalInteractions)}</p>
              <p className="text-sm text-gray-600 mt-1">
                {totalsData.successRate}% success rate
              </p>
            </div>
            <div className="bg-white rounded-lg shadow p-6">
              <h3 className="text-sm font-medium text-gray-500 mb-2">Total Tokens</h3>
              <p className="text-2xl font-bold text-gray-900">{formatNumber(totalsData.totalTokens)}</p>
              <p className="text-sm text-gray-600 mt-1">
                {formatNumber(totalsData.totalInputTokens)} input / {formatNumber(totalsData.totalOutputTokens)} output
              </p>
            </div>
            <div className="bg-white rounded-lg shadow p-6">
              <h3 className="text-sm font-medium text-gray-500 mb-2">Total Cost</h3>
              <p className="text-2xl font-bold text-gray-900">{formatCurrency(totalsData.totalCost)}</p>
              <p className="text-sm text-gray-600 mt-1">
                {formatCurrency(totalsData.averageCostPerInteraction)} per interaction
              </p>
            </div>
            <div className="bg-white rounded-lg shadow p-6">
              <h3 className="text-sm font-medium text-gray-500 mb-2">Performance</h3>
              <p className="text-2xl font-bold text-gray-900">{totalsData.averageResponseTime}ms</p>
              <p className="text-sm text-gray-600 mt-1">
                Avg response time
              </p>
            </div>
          </div>
        )}

        {/* Usage by Model */}
        <div className="mb-8">
          <h2 className="text-xl font-bold text-gray-900 mb-4">Usage by AI Model</h2>
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {/* Model Chart */}
            <div className="bg-white rounded-lg shadow p-6">
              <h3 className="text-lg font-medium text-gray-900 mb-4">Cost Distribution</h3>
              <ResponsiveContainer width="100%" height={300}>
                <PieChart>
                  <Pie
                    data={modelData}
                    dataKey="totalCost"
                    nameKey="modelName"
                    cx="50%"
                    cy="50%"
                    outerRadius={100}
                    label={(entry) => `${entry.modelName}: ${formatCurrency(entry.totalCost)}`}
                  >
                    {modelData.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                    ))}
                  </Pie>
                  <Tooltip formatter={(value: number) => formatCurrency(value)} />
                </PieChart>
              </ResponsiveContainer>
            </div>

            {/* Model Table */}
            <div className="bg-white rounded-lg shadow overflow-hidden">
              <table className="min-w-full">
                <thead>
                  <tr className="bg-gray-50">
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Model
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Interactions
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Tokens
                    </th>
