  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [showPreview, setShowPreview] = useState(false);

  // Form state
  const [title, setTitle] = useState("");
  const [slug, setSlug] = useState("");
  const [content, setContent] = useState("");
  const [categoryId, setCategoryId] = useState("");
  const [selectedTagIds, setSelectedTagIds] = useState<string[]>([]);

  useEffect(() => {
    if (!isLoadingRole && !hasMinRole(Role.EDITOR)) {
      router.push("/dashboard");
    }
  }, [isLoadingRole, hasMinRole, router]);

  useEffect(() => {
    if (!isLoadingRole && hasMinRole(Role.EDITOR)) {
      fetchArticle();
      fetchCategories();
      fetchAllTags();
      fetchArticleTags();
    }
  }, [articleId, isLoadingRole]);

  async function fetchArticle() {
    try {
      console.log("Fetching article with ID:", articleId);
      const response = await fetch(`/api/admin/articles/${articleId}`);

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.error || `Failed to fetch article: ${response.status}`);
      }

      const data = await response.json();
      console.log("Article data:", data);

      setArticle(data);
      setTitle(data.articleTitle);
      setSlug(data.articleSlug);
      setContent(data.contentHtml || "");
      setCategoryId(data.categoryId || "");
    } catch (error) {
      console.error("Error fetching article:", error);
      setError(error instanceof Error ? error.message : "Failed to load article");
    } finally {
      setLoading(false);
    }
  }

  async function fetchCategories() {
    try {
      const response = await fetch("/api/categories");
      if (!response.ok) throw new Error("Failed to fetch categories");

      const data = await response.json();
      setCategories(data);
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  }

  async function fetchAllTags() {
    try {
      const response = await fetch("/api/admin/tags");
      if (!response.ok) throw new Error("Failed to fetch tags");

      const data = await response.json();
      setAllTags(data);
    } catch (error) {
      console.error("Error fetching tags:", error);
    }
  }

  async function fetchArticleTags() {
    try {
      const response = await fetch(`/api/admin/articles/${articleId}/tags`);
      if (!response.ok) throw new Error("Failed to fetch article tags");

      const data = await response.json();
      setArticleTags(data);
      setSelectedTagIds(data.map((tag: Tag) => tag.tagId));
    } catch (error) {
      console.error("Error fetching article tags:", error);
    }
  }

  async function handleSave() {
    setSaving(true);
    setError(null);

    try {
      // Save article details
      const articleResponse = await fetch(`/api/admin/articles/${articleId}`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          articleTitle: title,
          articleSlug: slug,
          contentHtml: content,
          categoryId: categoryId || null,
        }),
      });

      if (!articleResponse.ok) {
        const data = await articleResponse.json();
        throw new Error(data.error || "Failed to save article");
      }

      // Save tags
      const tagsResponse = await fetch(`/api/admin/articles/${articleId}/tags`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          tagIds: selectedTagIds,
        }),
      });

      if (!tagsResponse.ok) {
        const data = await tagsResponse.json();
        throw new Error(data.error || "Failed to save tags");
      }

      router.push("/admin/articles");
    } catch (error) {
      console.error("Error saving article:", error);
      setError(error instanceof Error ? error.message : "Failed to save article");
    } finally {
      setSaving(false);
    }
  }

  function generateSlug() {
    const newSlug = title
      .toLowerCase()
      .replace(/[^\w\s-]/g, "")
      .replace(/\s+/g, "-")
      .replace(/-+/g, "-")
      .trim();
    setSlug(newSlug);
  }

  function handleTagToggle(tagId: string) {
    setSelectedTagIds(prev => 
      prev.includes(tagId) 
        ? prev.filter(id => id !== tagId)
        : [...prev, tagId]
    );
  }

  if (isLoadingRole || loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <Loader2 className="h-8 w-8 animate-spin text-orange-600" />
      </div>
    );
  }

  if (!article) {
    return (
      <div className="text-center py-8">
        <p className="text-red-600">Article not found</p>
        <button
          onClick={() => router.push("/admin/articles")}
          className="mt-4 text-blue-600 hover:text-blue-800"
        >
          Back to articles
        </button>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto">
      <div className="mb-6 flex items-center justify-between">
        <button
          onClick={() => router.push("/admin/articles")}
          className="flex items-center gap-2 text-gray-600 hover:text-gray-900"
        >
          <ArrowLeft className="h-5 w-5" />
          Back to articles
        </button>

        <div className="flex gap-2">
          <button
            onClick={() => setShowPreview(!showPreview)}
            className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50"
          >
            {showPreview ? "Edit" : "Preview"}
          </button>
          <button
            onClick={handleSave}
            disabled={saving}
            className="flex items-center gap-2 px-4 py-2 bg-orange-600 text-white rounded-md hover:bg-orange-700 disabled:opacity-50"
          >
            {saving ? (
              <Loader2 className="h-4 w-4 animate-spin" />
            ) : (
              <Save className="h-4 w-4" />
            )}
            Save
          </button>
        </div>
      </div>

      <div className="bg-white rounded-lg shadow-sm p-6">
        <h1 className="text-2xl font-bold mb-6">Edit Article</h1>

        {error && (
          <div className="mb-4 bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded">
            {error}
          </div>
        )}

        {showPreview ? (
          <div className="prose prose-sm max-w-none">
            <h2 className="text-xl font-semibold mb-4">{title}</h2>
            <MarkdownViewer content={content} />
          </div>
        ) : (
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Title
              </label>
              <input
                type="text"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                className="w-full rounded-md border border-gray-300 px-3 py-2 focus:border-orange-500 focus:outline-none focus:ring-1 focus:ring-orange-500"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Slug
              </label>
              <div className="flex gap-2">
                <input
                  type="text"
                  value={slug}
                  onChange={(e) => setSlug(e.target.value)}
                  className="flex-1 rounded-md border border-gray-300 px-3 py-2 focus:border-orange-500 focus:outline-none focus:ring-1 focus:ring-orange-500"
                />
                <button
                  onClick={generateSlug}
                  className="px-3 py-2 text-sm bg-gray-100 text-gray-700 rounded-md hover:bg-gray-200"
                >
                  Generate
                </button>
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Category
              </label>
              <select
                value={categoryId}
                onChange={(e) => setCategoryId(e.target.value)}
                className="w-full rounded-md border border-gray-300 px-3 py-2 focus:border-orange-500 focus:outline-none focus:ring-1 focus:ring-orange-500"
              >
                <option value="">Uncategorized</option>
                {categories.map((category) => (
                  <option key={category.categoryId} value={category.categoryId}>
                    {category.categoryName}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Tags
              </label>
              <div className="border border-gray-300 rounded-md p-3 max-h-48 overflow-y-auto">
                {allTags.length === 0 ? (
                  <p className="text-gray-500 text-sm">No tags available</p>
                ) : (
                  <div className="space-y-2">
                    {allTags.map((tag) => (
                      <label key={tag.tagId} className="flex items-center space-x-2 cursor-pointer">
                        <input
                          type="checkbox"
                          checked={selectedTagIds.includes(tag.tagId)}
                          onChange={() => handleTagToggle(tag.tagId)}
                          className="rounded border-gray-300 text-orange-600 focus:ring-orange-500"
                        />
                        <span className="flex items-center space-x-2">
                          {tag.color && (
                            <div
                              className="w-3 h-3 rounded-full"
                              style={{ backgroundColor: tag.color }}
                            />
                          )}
                          <span className="text-sm">{tag.tagName}</span>
                          {tag.description && (
                            <span className="text-xs text-gray-500">({tag.description})</span>
                          )}
                        </span>
                      </label>
                    ))}
                  </div>
                )}
              </div>
              <p className="mt-1 text-xs text-gray-500">
                Select the tags that apply to this article
              </p>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Content (Markdown)
              </label>
              <textarea
                value={content}
                onChange={(e) => setContent(e.target.value)}
                rows={20}
                className="w-full rounded-md border border-gray-300 px-3 py-2 font-mono text-sm focus:border-orange-500 focus:outline-none focus:ring-1 focus:ring-orange-500"
                placeholder="Enter article content in Markdown format..."
              />
              <p className="mt-1 text-xs text-gray-500">
                Use Markdown syntax for formatting. Click Preview to see rendered content.
              </p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

"use client";

import { useState, useEffect } from "react";
import { useRouter } from "next/navigation";
import { Search, Edit, Trash2, Eye, Flag } from "lucide-react";
import { format } from "date-fns";

interface Article {
  articleId: string;
  articleTitle: string;
  articleSlug: string;
  categoryId: string | null;
  category: {
    categoryName: string;
  } | null;
  isContentGenerated: boolean;
  isFlagged: boolean;
  createdAt: string;
  updatedAt: string;
  _count: {
    likes: number;
    comments: number;
  };
}

export default function AdminArticlesPage() {
  const router = useRouter();
  const [articles, setArticles] = useState<Article[]>([]);
  const [loading, setLoading] = useState(true);
  const [search, setSearch] = useState("");
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [filter, setFilter] = useState<"all" | "flagged" | "generated">("all");

  useEffect(() => {
    fetchArticles();
  }, [page, search, filter]);

  async function fetchArticles() {
    try {
      setLoading(true);
      const params = new URLSearchParams({
        page: page.toString(),
        search,
        filter,
      });

      const response = await fetch(`/api/admin/articles?${params}`);
      if (!response.ok) throw new Error("Failed to fetch articles");

      const data = await response.json();
      setArticles(data.articles);
      setTotalPages(data.totalPages);
    } catch (error) {
      console.error("Error fetching articles:", error);
    } finally {
      setLoading(false);
    }
  }

  async function handleDelete(articleId: string) {
    if (!confirm("Are you sure you want to delete this article?")) return;

    try {
      const response = await fetch(`/api/admin/articles/${articleId}`, {
        method: "DELETE",
      });

      if (!response.ok) throw new Error("Failed to delete article");

      await fetchArticles();
    } catch (error) {
      console.error("Error deleting article:", error);
      alert("Failed to delete article");
    }
  }

  async function handleToggleFlag(articleId: string, currentFlag: boolean) {
    try {
      const response = await fetch(`/api/admin/articles/${articleId}/flag`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ isFlagged: !currentFlag }),
      });

      if (!response.ok) throw new Error("Failed to update flag");

      await fetchArticles();
    } catch (error) {
      console.error("Error updating flag:", error);
      alert("Failed to update flag");
    }
  }

  return (
    <div>
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Article Management</h1>
        <p className="mt-2 text-gray-600">
          Search, edit, and manage all articles in the system
        </p>
      </div>

      <div className="mb-6 flex flex-col sm:flex-row gap-4">
        <div className="flex-1">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 h-5 w-5 -translate-y-1/2 text-gray-400" />
            <input
              type="text"
              value={search}
              onChange={(e) => {
                setSearch(e.target.value);
                setPage(1);
              }}
              placeholder="Search articles..."
              className="w-full rounded-lg border border-gray-300 py-2 pl-10 pr-4 focus:border-orange-500 focus:outline-none focus:ring-1 focus:ring-orange-500"
            />
          </div>
        </div>

        <select
          value={filter}
          onChange={(e) => {
            setFilter(e.target.value as typeof filter);
            setPage(1);
          }}
          className="rounded-lg border border-gray-300 px-4 py-2 focus:border-orange-500 focus:outline-none focus:ring-1 focus:ring-orange-500"
        >
          <option value="all">All Articles</option>
          <option value="flagged">Flagged Only</option>
          <option value="generated">AI Generated</option>
        </select>
      </div>

      {loading ? (
        <div className="text-center py-8">Loading...</div>
      ) : articles.length === 0 ? (
        <div className="text-center py-8 text-gray-500">No articles found</div>
      ) : (
        <>
          <div className="bg-white shadow-sm rounded-lg overflow-hidden">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Title
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Category
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Stats
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Status
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Updated
                  </th>
                  <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Actions
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {articles.map((article) => (
                  <tr key={article.articleId} className="hover:bg-gray-50">
                    <td className="px-6 py-4">
                      <div>
                        <div className="text-sm font-medium text-gray-900">
                          {article.articleTitle}
                        </div>
                        <div className="text-sm text-gray-500">
                          {article.articleSlug}
                        </div>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span className="text-sm text-gray-900">
                        {article.category?.categoryName || "Uncategorized"}
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="text-sm text-gray-900">
                        {article._count.likes} likes, {article._count.comments} comments
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex gap-2">
                        {article.isContentGenerated && (
                          <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                            AI Generated
                          </span>
                        )}
                        {article.isFlagged && (
                          <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-red-100 text-red-800">
                            Flagged
                          </span>
                        )}
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {format(new Date(article.updatedAt), "MMM d, yyyy")}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                      <div className="flex justify-end gap-2">
                        <button
                          onClick={() => router.push(`/articles/${article.articleSlug}`)}
                          className="text-gray-600 hover:text-gray-900"
                          title="View"
                        >
                          <Eye className="h-5 w-5" />
                        </button>
                        <button
                          onClick={() => router.push(`/admin/articles/${article.articleId}/edit`)}
                          className="text-blue-600 hover:text-blue-900"
                          title="Edit"
                        >
                          <Edit className="h-5 w-5" />
                        </button>
                        <button
                          onClick={() => handleToggleFlag(article.articleId, article.isFlagged)}
                          className={article.isFlagged ? "text-green-600 hover:text-green-900" : "text-orange-600 hover:text-orange-900"}
                          title={article.isFlagged ? "Unflag" : "Flag"}
                        >
                          <Flag className="h-5 w-5" />
                        </button>
                        <button
                          onClick={() => handleDelete(article.articleId)}
                          className="text-red-600 hover:text-red-900"
                          title="Delete"
                        >
                          <Trash2 className="h-5 w-5" />
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {totalPages > 1 && (
            <div className="mt-6 flex justify-center gap-2">
              <button
                onClick={() => setPage(Math.max(1, page - 1))}
                disabled={page === 1}
                className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Previous
              </button>
              <span className="px-4 py-2 text-sm text-gray-700">
                Page {page} of {totalPages}
              </span>
              <button
                onClick={() => setPage(Math.min(totalPages, page + 1))}
                disabled={page === totalPages}
                className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Next
              </button>
            </div>
          )}
        </>
      )}
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import Link from 'next/link';
import { ArrowLeft, Search, FileText, Calendar, Eye, Heart, MessageSquare, MoveHorizontal, X } from 'lucide-react';
import { useAuth } from '@/hooks/use-auth';
import { Role } from '@prisma/client';
import { useRouter } from 'next/navigation';

interface Article {
  id: string;
  title: string;
  slug: string;
  createdAt: string;
  viewCount: number;
  _count: {
    likes: number;
    comments: number;
  };
}

interface Category {
  id: string;
  categoryName: string;
  description: string | null;
  _count: {
    articles: number;
  };
}

interface SearchArticle extends Article {
  categories: Array<{
    category: {
      id: string;
      categoryName: string;
    };
  }>;
}

export default function CategoryArticlesClientPage({ categoryId }: { categoryId: string }) {
  const router = useRouter();
  const { hasMinRole, userRole } = useAuth();
  const [category, setCategory] = useState<Category | null>(null);
  const [articles, setArticles] = useState<Article[]>([]);
  const [categories, setCategories] = useState<Category[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedArticles, setSelectedArticles] = useState<{ [key: string]: string }>({});
  const [moving, setMoving] = useState<string | null>(null);
  const [showSearchModal, setShowSearchModal] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState<SearchArticle[]>([]);
  const [searching, setSearching] = useState(false);

  // Start fetching data immediately
  useEffect(() => {
    fetchCategoryAndArticles();
    fetchCategories();
  }, [categoryId]);

  // Handle authorization separately
  useEffect(() => {
    if (userRole && !hasMinRole(Role.ADMIN)) {
      router.push('/dashboard');
    }
  }, [userRole, hasMinRole, router]);

  async function fetchCategoryAndArticles() {
    setLoading(true);
    setError(null);

    try {
      // Fetch both in parallel
      const [categoryResponse, articlesResponse] = await Promise.all([
        fetch(`/api/admin/categories/${categoryId}`),
        fetch(`/api/admin/categories/${categoryId}/articles`)
      ]);

      if (!categoryResponse.ok) throw new Error('Failed to fetch category');
      if (!articlesResponse.ok) throw new Error('Failed to fetch articles');

      const [categoryData, articlesData] = await Promise.all([
        categoryResponse.json(),
        articlesResponse.json()
      ]);

      setCategory(categoryData);
      setArticles(articlesData);
    } catch (err) {
      console.error('Error fetching data:', err);
      setError(err instanceof Error ? err.message : 'An error occurred');
    } finally {
      setLoading(false);
    }
  }

  async function fetchCategories() {
    try {
      const response = await fetch('/api/admin/categories');
      if (!response.ok) throw new Error('Failed to fetch categories');
      const data = await response.json();
      setCategories(data);
    } catch (err) {
      console.error('Failed to fetch categories:', err);
    }
  }

  async function moveArticle(articleId: string, newCategoryId: string) {
    if (!newCategoryId || newCategoryId === categoryId) return;

    setMoving(articleId);
    setError(null);

    try {
      const response = await fetch(`/api/admin/articles/${articleId}/move`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ categoryId: newCategoryId }),
      });

      if (!response.ok) throw new Error('Failed to move article');

      // Refresh the articles list
      await fetchCategoryAndArticles();
      setSelectedArticles({ ...selectedArticles, [articleId]: '' });
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to move article');
    } finally {
      setMoving(null);
    }
  }

  async function searchArticles() {
    if (!searchQuery.trim()) return;

    setSearching(true);
    try {
      const response = await fetch(`/api/admin/articles/search?q=${encodeURIComponent(searchQuery)}&excludeCategory=${categoryId}`);
      if (!response.ok) throw new Error('Failed to search articles');
      const data = await response.json();
      setSearchResults(data);
    } catch (err) {
      console.error('Search error:', err);
    } finally {
      setSearching(false);
    }
  }

  async function moveArticleFromSearch(articleId: string) {
    setMoving(articleId);
    setError(null);

    try {
      const response = await fetch(`/api/admin/articles/${articleId}/move`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ categoryId }),
      });

      if (!response.ok) throw new Error('Failed to move article');

      // Remove from search results and refresh articles
      setSearchResults(searchResults.filter(a => a.id !== articleId));
      await fetchCategoryAndArticles();
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to move article');
    } finally {
      setMoving(null);
    }
  }

  useEffect(() => {
    const debounceTimer = setTimeout(() => {
      if (showSearchModal && searchQuery) {
        searchArticles();
      }
    }, 300);

    return () => clearTimeout(debounceTimer);
  }, [searchQuery, showSearchModal]);

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-[60vh]">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-orange-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading category and articles...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="max-w-4xl mx-auto p-8">
        <div className="bg-red-50 border border-red-200 rounded-lg p-4">
          <h2 className="text-red-800 font-semibold mb-2">Error loading data</h2>
          <p className="text-red-600">{error}</p>
          <button 
            onClick={() => {
              setError(null);
              fetchCategoryAndArticles();
            }}
            className="mt-4 px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  if (!category) {
    return (
      <div className="text-center py-8">
        <p className="text-gray-600">Category not found</p>
        <Link href="/admin/categories" className="text-blue-600 hover:text-blue-800 mt-2 inline-block">
          â† Back to Categories
