                <Input
                  id="fromEmail"
                  type="email"
                  value={formData.fromEmail}
                  onChange={(e) => setFormData({ ...formData, fromEmail: e.target.value })}
                  placeholder="noreply@example.com"
                />
              </div>
              <div>
                <Label htmlFor="fromName">From Name (Optional)</Label>
                <Input
                  id="fromName"
                  value={formData.fromName}
                  onChange={(e) => setFormData({ ...formData, fromName: e.target.value })}
                  placeholder="IT Learning Platform"
                />
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="mt-6">
          <CardHeader>
            <CardTitle>Email Content</CardTitle>
            <CardDescription>The subject and body of the email</CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <Label htmlFor="subject">Subject</Label>
              <Input
                id="subject"
                value={formData.subject}
                onChange={(e) => setFormData({ ...formData, subject: e.target.value })}
                placeholder="Welcome to IT Learning Platform, {{firstName}}!"
                required
              />
            </div>

            <div>
              <Label htmlFor="htmlContent">HTML Content</Label>
              <Textarea
                id="htmlContent"
                value={formData.htmlContent}
                onChange={(e) => setFormData({ ...formData, htmlContent: e.target.value })}
                placeholder="<h1>Welcome {{firstName}}!</h1><p>Thanks for joining...</p>"
                rows={10}
                required
              />
              <p className="text-xs text-gray-500 mt-1">Use {"{{variable}}"} syntax for dynamic content</p>
            </div>

            <div>
              <Label htmlFor="textContent">Text Content (Optional)</Label>
              <Textarea
                id="textContent"
                value={formData.textContent}
                onChange={(e) => setFormData({ ...formData, textContent: e.target.value })}
                placeholder="Welcome {{firstName}}! Thanks for joining..."
                rows={6}
              />
              <p className="text-xs text-gray-500 mt-1">Plain text version for email clients that don't support HTML</p>
            </div>
          </CardContent>
        </Card>

        <Card className="mt-6">
          <CardHeader>
            <div className="flex justify-between items-center">
              <div>
                <CardTitle>Template Variables</CardTitle>
                <CardDescription>Define variables that can be used in the template</CardDescription>
              </div>
              <Button type="button" onClick={addVariable} size="sm">
                Add Variable
              </Button>
            </div>
          </CardHeader>
          <CardContent>
            {variables.map((variable, index) => (
              <div key={index} className="flex gap-2 mb-2">
                <Input
                  placeholder="Variable name"
                  value={variable.name}
                  onChange={(e) => updateVariable(index, "name", e.target.value)}
                />
                <Input
                  placeholder="Description"
                  value={variable.description}
                  onChange={(e) => updateVariable(index, "description", e.target.value)}
                />
                <Input
                  placeholder="Default value"
                  value={variable.defaultValue}
                  onChange={(e) => updateVariable(index, "defaultValue", e.target.value)}
                />
                <Button
                  type="button"
                  variant="destructive"
                  size="sm"
                  onClick={() => removeVariable(index)}
                >
                  Remove
                </Button>
              </div>
            ))}
            {variables.length === 0 && (
              <p className="text-sm text-gray-500">No variables defined</p>
            )}
          </CardContent>
        </Card>

        <div className="flex justify-between mt-6">
          <Link href="/admin/email-templates">
            <Button type="button" variant="outline">Cancel</Button>
          </Link>
          <Button type="submit" disabled={saving}>
            {saving ? "Creating..." : "Create Template"}
          </Button>
        </div>
      </form>
    </div>
  );
}

"use client";

import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { useRouter } from "next/navigation";
import { toast } from "sonner";
import Link from "next/link";

interface EmailTemplate {
  templateId: string;
  templateKey: string;
  templateName: string;
  description: string | null;
  subject: string;
  isActive: boolean;
  createdAt: string;
  updatedAt: string;
  _count: {
    emailLogs: number;
  };
}

export default function EmailTemplatesPage() {
  const [templates, setTemplates] = useState<EmailTemplate[]>([]);
  const [loading, setLoading] = useState(true);
  const router = useRouter();

  useEffect(() => {
    fetchTemplates();
  }, []);

  const fetchTemplates = async () => {
    try {
      const response = await fetch("/api/admin/email-templates");
      if (!response.ok) throw new Error("Failed to fetch templates");
      const data = await response.json();
      setTemplates(data);
    } catch (error) {
      toast.error("Failed to load email templates");
      console.error("Error:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (templateId: string) => {
    if (!confirm("Are you sure you want to delete this template?")) return;

    try {
      const response = await fetch(`/api/admin/email-templates/${templateId}`, {
        method: "DELETE",
      });

      if (!response.ok) throw new Error("Failed to delete template");

      toast.success("Template deleted successfully");
      fetchTemplates();
    } catch (error) {
      toast.error("Failed to delete template");
      console.error("Error:", error);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <p>Loading templates...</p>
      </div>
    );
  }

  return (
    <div className="container mx-auto py-8">
      <div className="flex justify-between items-center mb-8">
        <div>
          <h1 className="text-3xl font-bold">Email Templates</h1>
          <p className="text-gray-600 mt-2">Manage email templates for the platform</p>
        </div>
        <div className="space-x-4">
          <Link href="/admin/email-logs">
            <Button variant="outline">View Email Logs</Button>
          </Link>
          <Link href="/admin/email-templates/new">
            <Button>Create Template</Button>
          </Link>
        </div>
      </div>

      <div className="grid gap-4">
        {templates.map((template) => (
          <Card key={template.templateId}>
            <CardHeader>
              <div className="flex justify-between items-start">
                <div>
                  <CardTitle className="text-xl">{template.templateName}</CardTitle>
                  <CardDescription className="mt-1">
                    Key: <code className="text-sm bg-gray-100 px-2 py-1 rounded">{template.templateKey}</code>
                  </CardDescription>
                  {template.description && (
                    <p className="text-sm text-gray-600 mt-2">{template.description}</p>
                  )}
                </div>
                <div className="flex items-center gap-2">
                  <Badge variant={template.isActive ? "default" : "secondary"}>
                    {template.isActive ? "Active" : "Inactive"}
                  </Badge>
                  <Badge variant="outline">
                    {template._count.emailLogs} sent
                  </Badge>
                </div>
              </div>
            </CardHeader>
            <CardContent>
              <div className="mb-4">
                <p className="text-sm text-gray-600">Subject:</p>
                <p className="font-medium">{template.subject}</p>
              </div>
              <div className="flex gap-2">
                <Link href={`/admin/email-templates/${template.templateId}/edit`}>
                  <Button size="sm">Edit</Button>
                </Link>
                <Link href={`/admin/email-templates/${template.templateId}/test`}>
                  <Button size="sm" variant="outline">Send Test</Button>
                </Link>
                <Button
                  size="sm"
                  variant="destructive"
                  onClick={() => handleDelete(template.templateId)}
                >
                  Delete
                </Button>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {templates.length === 0 && (
        <Card>
          <CardContent className="text-center py-12">
            <p className="text-gray-600 mb-4">No email templates found</p>
            <Link href="/admin/email-templates/new">
              <Button>Create Your First Template</Button>
            </Link>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

"use client";

import { useState, useEffect } from "react";
import { format } from "date-fns";
import { Flag, Check, X, MessageSquare, FileText } from "lucide-react";

interface FlaggedItem {
  id: string;
  type: "article" | "comment";
  content: string;
  title?: string;
  flaggedAt: string;
  flagReason: string | null;
  flaggedBy: {
    username: string | null;
    email: string;
  };
  author: {
    username: string | null;
    email: string;
  };
  articleInfo?: {
    articleId: string;
    articleTitle: string;
    articleSlug: string;
  };
}

export default function AdminFlaggedPage() {
  const [flaggedItems, setFlaggedItems] = useState<FlaggedItem[]>([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState<"all" | "articles" | "comments">("all");

  useEffect(() => {
    fetchFlaggedContent();
  }, [filter]);

  async function fetchFlaggedContent() {
    try {
      setLoading(true);
      const response = await fetch(`/api/admin/flagged?type=${filter}`);
      if (!response.ok) throw new Error("Failed to fetch flagged content");

      const data = await response.json();
      setFlaggedItems(data.items);
    } catch (error) {
      console.error("Error fetching flagged content:", error);
    } finally {
      setLoading(false);
    }
  }

  async function handleResolve(id: string, type: "article" | "comment", action: "approve" | "remove") {
    try {
      const response = await fetch(`/api/admin/flagged/${type}/${id}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ action }),
      });

      if (!response.ok) throw new Error("Failed to resolve flagged item");

      await fetchFlaggedContent();
    } catch (error) {
      console.error("Error resolving flagged item:", error);
      alert("Failed to resolve flagged item");
    }
  }

  return (
    <div>
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Flagged Content</h1>
        <p className="mt-2 text-gray-600">
          Review and moderate flagged articles and comments
        </p>
      </div>

      <div className="mb-6">
        <div className="flex gap-2">
          <button
            onClick={() => setFilter("all")}
            className={`px-4 py-2 rounded-lg font-medium transition-colors ${
              filter === "all"
                ? "bg-orange-600 text-white"
                : "bg-gray-200 text-gray-700 hover:bg-gray-300"
            }`}
          >
            All Content
          </button>
          <button
            onClick={() => setFilter("articles")}
            className={`px-4 py-2 rounded-lg font-medium transition-colors ${
              filter === "articles"
                ? "bg-orange-600 text-white"
                : "bg-gray-200 text-gray-700 hover:bg-gray-300"
            }`}
          >
            Articles Only
          </button>
          <button
            onClick={() => setFilter("comments")}
            className={`px-4 py-2 rounded-lg font-medium transition-colors ${
              filter === "comments"
                ? "bg-orange-600 text-white"
                : "bg-gray-200 text-gray-700 hover:bg-gray-300"
            }`}
          >
            Comments Only
          </button>
        </div>
      </div>

      {loading ? (
        <div className="text-center py-8">Loading...</div>
      ) : flaggedItems.length === 0 ? (
        <div className="text-center py-8 text-gray-500">
          <Flag className="h-12 w-12 mx-auto mb-4 text-gray-300" />
          <p>No flagged content to review</p>
        </div>
      ) : (
        <div className="space-y-4">
          {flaggedItems.map((item) => (
            <div
              key={item.id}
              className="bg-white rounded-lg shadow-sm border border-gray-200 p-6"
            >
              <div className="flex items-start justify-between">
                <div className="flex-1">
                  <div className="flex items-center gap-3 mb-3">
                    {item.type === "article" ? (
                      <FileText className="h-5 w-5 text-blue-600" />
                    ) : (
                      <MessageSquare className="h-5 w-5 text-green-600" />
                    )}
                    <span className="text-sm font-medium text-gray-500 uppercase">
                      {item.type}
                    </span>
                    <span className="text-sm text-gray-500">
                      Flagged {format(new Date(item.flaggedAt), "MMM d, yyyy 'at' h:mm a")}
                    </span>
                  </div>

                  {item.type === "article" && item.title && (
                    <h3 className="text-lg font-semibold text-gray-900 mb-2">
                      {item.title}
                    </h3>
                  )}

                  {item.type === "comment" && item.articleInfo && (
                    <div className="mb-2">
                      <span className="text-sm text-gray-500">On article: </span>
                      <a
                        href={`/articles/${item.articleInfo.articleSlug}`}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-sm text-blue-600 hover:text-blue-800"
                      >
                        {item.articleInfo.articleTitle}
                      </a>
                    </div>
                  )}

                  <div className="prose prose-sm max-w-none mb-4">
                    <p className="text-gray-700 line-clamp-3">{item.content}</p>
                  </div>

                  {item.flagReason && (
                    <div className="mb-4 p-3 bg-red-50 rounded-md">
                      <p className="text-sm text-red-800">
                        <span className="font-medium">Flag reason:</span> {item.flagReason}
                      </p>
                    </div>
                  )}

                  <div className="flex flex-wrap gap-4 text-sm text-gray-500">
                    <div>
                      <span className="font-medium">Author:</span>{" "}
                      {item.author.username || item.author.email}
                    </div>
                    <div>
                      <span className="font-medium">Flagged by:</span>{" "}
                      {item.flaggedBy.username || item.flaggedBy.email}
                    </div>
                  </div>
                </div>

                <div className="flex gap-2 ml-4">
                  <button
                    onClick={() => handleResolve(item.id, item.type, "approve")}
                    className="flex items-center gap-1 px-3 py-2 bg-green-600 text-white rounded-md hover:bg-green-700 transition-colors"
                    title="Approve (Remove flag)"
                  >
                    <Check className="h-4 w-4" />
                    <span>Approve</span>
                  </button>
                  <button
                    onClick={() => handleResolve(item.id, item.type, "remove")}
                    className="flex items-center gap-1 px-3 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 transition-colors"
                    title="Remove content"
                  >
                    <X className="h-4 w-4" />
                    <span>Remove</span>
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

"use client";

import { useState, useEffect } from "react";
import { DollarSign, Edit2, Save, X, Plus, Trash2 } from "lucide-react";
import { SubscriptionTier } from "@prisma/client";

interface PricingData {
  pricingId: string;
  tier: SubscriptionTier;
  stripePriceId: string;
  monthlyPriceCents: number;
  yearlyPriceCents: number;
  features: string[];
  isActive: boolean;
}

export default function AdminPricingPage() {
  const [pricingData, setPricingData] = useState<PricingData[]>([]);
  const [loading, setLoading] = useState(true);
  const [editingId, setEditingId] = useState<string | null>(null);
  const [editForm, setEditForm] = useState<Partial<PricingData>>({});
  const [isCreating, setIsCreating] = useState(false);
  const [newForm, setNewForm] = useState<Partial<PricingData>>({
    tier: SubscriptionTier.STANDARD,
    stripePriceId: "",
    monthlyPriceCents: 0,
    yearlyPriceCents: 0,
    features: [],
    isActive: true,
  });

  useEffect(() => {
    fetchPricing();
  }, []);

  async function fetchPricing() {
    try {
      setLoading(true);
      const response = await fetch("/api/admin/pricing");
      if (!response.ok) throw new Error("Failed to fetch pricing");

      const data = await response.json();
      setPricingData(data.pricing);
    } catch (error) {
      console.error("Error fetching pricing:", error);
    } finally {
      setLoading(false);
    }
  }

  async function handleSave(pricingId: string) {
    try {
      const response = await fetch(`/api/admin/pricing/${pricingId}`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(editForm),
      });

      if (!response.ok) throw new Error("Failed to update pricing");

      setEditingId(null);
      await fetchPricing();
    } catch (error) {
      console.error("Error updating pricing:", error);
      alert("Failed to update pricing");
    }
  }

  async function handleCreate() {
    try {
      const response = await fetch("/api/admin/pricing", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newForm),
      });

      if (!response.ok) throw new Error("Failed to create pricing");

      setIsCreating(false);
      setNewForm({
        tier: SubscriptionTier.STANDARD,
        stripePriceId: "",
        monthlyPriceCents: 0,
        yearlyPriceCents: 0,
        features: [],
        isActive: true,
      });
      await fetchPricing();
    } catch (error) {
      console.error("Error creating pricing:", error);
      alert("Failed to create pricing");
    }
  }

  async function handleDelete(pricingId: string) {
    if (!confirm("Are you sure you want to delete this pricing tier?")) return;

    try {
      const response = await fetch(`/api/admin/pricing/${pricingId}`, {
        method: "DELETE",
      });

      if (!response.ok) throw new Error("Failed to delete pricing");

      await fetchPricing();
    } catch (error) {
      console.error("Error deleting pricing:", error);
      alert("Failed to delete pricing");
    }
  }

  function formatPrice(cents: number): string {
    return `$${(cents / 100).toFixed(2)}`;
  }

  return (
    <div>
      <div className="mb-8 flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Subscription Pricing</h1>
          <p className="mt-2 text-gray-600">
            Manage subscription tiers and pricing
          </p>
        </div>
        {!isCreating && (
          <button
            onClick={() => setIsCreating(true)}
            className="flex items-center gap-2 px-4 py-2 bg-orange-600 text-white rounded-lg hover:bg-orange-700 transition-colors"
          >
            <Plus className="h-5 w-5" />
            Add Tier
          </button>
        )}
      </div>

      {loading ? (
        <div className="text-center py-8">Loading...</div>
      ) : (
        <div className="space-y-6">
          {isCreating && (
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
              <h3 className="text-lg font-semibold mb-4">New Pricing Tier</h3>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Tier
                  </label>
                  <select
                    value={newForm.tier}
                    onChange={(e) => setNewForm({ ...newForm, tier: e.target.value as SubscriptionTier })}
                    className="w-full rounded-md border border-gray-300 px-3 py-2"
                  >
                    <option value={SubscriptionTier.FREE}>FREE</option>
                    <option value={SubscriptionTier.STANDARD}>STANDARD</option>
                    <option value={SubscriptionTier.MAX}>MAX</option>
