                    <button
                      onClick={() => handleSave(pricing.pricingId)}
                      className="px-4 py-2 bg-green-600 text-white rounded-md hover:bg-green-700"
                    >
                      <Save className="h-5 w-5" />
                    </button>
                  </div>
                </div>
              ) : (
                <div>
                  <div className="flex items-start justify-between">
                    <div>
                      <div className="flex items-center gap-3 mb-2">
                        <h3 className="text-xl font-semibold text-gray-900">{pricing.tier}</h3>
                        {!pricing.isActive && (
                          <span className="px-2 py-1 bg-gray-100 text-gray-600 text-xs rounded-full">
                            Inactive
                          </span>
                        )}
                      </div>
                      <p className="text-sm text-gray-500 mb-4">
                        Stripe Price ID: {pricing.stripePriceId}
                      </p>
                      <div className="flex gap-6 mb-4">
                        <div>
                          <p className="text-2xl font-bold text-gray-900">
                            {formatPrice(pricing.monthlyPriceCents)}
                          </p>
                          <p className="text-sm text-gray-500">per month</p>
                        </div>
                        <div>
                          <p className="text-2xl font-bold text-gray-900">
                            {formatPrice(pricing.yearlyPriceCents)}
                          </p>
                          <p className="text-sm text-gray-500">per year</p>
                        </div>
                      </div>
                      <ul className="space-y-2">
                        {pricing.features.map((feature, index) => (
                          <li key={index} className="flex items-start gap-2">
                            <span className="text-green-600 mt-0.5">âœ“</span>
                            <span className="text-gray-700">{feature}</span>
                          </li>
                        ))}
                      </ul>
                    </div>
                    <div className="flex gap-2">
                      <button
                        onClick={() => {
                          setEditingId(pricing.pricingId);
                          setEditForm(pricing);
                        }}
                        className="p-2 text-blue-600 hover:text-blue-800"
                      >
                        <Edit2 className="h-5 w-5" />
                      </button>
                      <button
                        onClick={() => handleDelete(pricing.pricingId)}
                        className="p-2 text-red-600 hover:text-red-800"
                      >
                        <Trash2 className="h-5 w-5" />
                      </button>
                    </div>
                  </div>
                </div>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

'use client';

import { useEffect, useState } from 'react';
import { useRouter } from 'next/navigation';
import { use } from 'react';
import { useAuth } from '@/hooks/use-auth';
import { Role } from '@prisma/client';
import { ArrowLeft, Search, Plus, X } from 'lucide-react';
import Link from 'next/link';

interface Tag {
  tagId: string;
  tagName: string;
  description: string | null;
  color: string | null;
}

interface Article {
  id: string;
  title: string;
  slug: string;
  createdAt: string;
  isContentGenerated: boolean;
  category: {
    categoryName: string;
  } | null;
  _count: {
    likes: number;
    comments: number;
  };
}

interface TagArticlesData {
  tag: Tag;
  articles: Article[];
}

export default function TagArticlesPage({
  params,
}: {
  params: Promise<{ tagId: string }>;
}) {
  const { tagId } = use(params);
  const router = useRouter();
  const { hasMinRole, isLoadingRole } = useAuth();
  const [data, setData] = useState<TagArticlesData | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [showSearchModal, setShowSearchModal] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState<Article[]>([]);
  const [searchLoading, setSearchLoading] = useState(false);

  useEffect(() => {
    fetchTagArticles();
  }, [tagId]);

  useEffect(() => {
    if (!isLoadingRole && !hasMinRole(Role.ADMIN)) {
      router.push('/dashboard');
    }
  }, [isLoadingRole, hasMinRole, router]);

  const fetchTagArticles = async () => {
    try {
      const response = await fetch(`/api/admin/tags/${tagId}/articles`);

      if (!response.ok) {
        throw new Error('Failed to fetch tag articles');
      }

      const result = await response.json();
      setData(result);
    } catch (error) {
      console.error('Error fetching tag articles:', error);
      setError('Failed to fetch tag articles');
    } finally {
      setLoading(false);
    }
  };

  const searchArticles = async (term: string) => {
    setSearchLoading(true);
    try {
      const searchParam = term ? `?search=${encodeURIComponent(term)}` : '';
      const response = await fetch(`/api/admin/tags/${tagId}/search-articles${searchParam}`);

      if (!response.ok) {
        throw new Error('Failed to search articles');
      }

      const results = await response.json();
      setSearchResults(results);
    } catch (error) {
      console.error('Error searching articles:', error);
      setError('Failed to search articles');
    } finally {
      setSearchLoading(false);
    }
  };

  const addTagToArticle = async (articleId: string) => {
    try {
      const response = await fetch(`/api/admin/tags/${tagId}/add-to-article`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ articleId }),
      });

      if (!response.ok) {
        throw new Error('Failed to add tag to article');
      }

      // Refresh the tag articles and search results
      await fetchTagArticles();
      if (searchTerm) {
        await searchArticles(searchTerm);
      } else {
        await searchArticles('');
      }
    } catch (error) {
      console.error('Error adding tag to article:', error);
      setError('Failed to add tag to article');
    }
  };

  const handleSearchSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    searchArticles(searchTerm);
  };

  const openSearchModal = () => {
    setShowSearchModal(true);
    setSearchTerm('');
    setSearchResults([]);
    searchArticles(''); // Load all available articles
  };

  if (isLoadingRole) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-lg">Loading...</div>
      </div>
    );
  }

  if (!hasMinRole(Role.ADMIN)) {
    return null;
  }

  if (loading) {
    return (
      <div className="text-center py-8">
        <div className="text-lg">Loading tag articles...</div>
      </div>
    );
  }

  if (!data) {
    return (
      <div className="text-center py-8">
        <p className="text-red-600">Tag not found</p>
        <Link href="/admin/tags" className="mt-4 text-blue-600 hover:text-blue-800">
          Back to tags
        </Link>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center space-x-4">
          <Link
            href="/admin/tags"
            className="flex items-center text-gray-600 hover:text-gray-900"
          >
            <ArrowLeft className="h-5 w-5 mr-2" />
            Back to Tags
          </Link>
          <div className="flex items-center space-x-2">
            {data.tag.color && (
              <div
                className="w-4 h-4 rounded-full"
                style={{ backgroundColor: data.tag.color }}
              />
            )}
            <h1 className="text-3xl font-bold">#{data.tag.tagName}</h1>
          </div>
        </div>
        <button
          onClick={openSearchModal}
          className="flex items-center space-x-2 bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-md transition-colors"
        >
          <Plus className="h-4 w-4" />
          <span>Add Articles</span>
        </button>
      </div>

      {data.tag.description && (
        <p className="text-gray-600 mb-6">{data.tag.description}</p>
      )}

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      {/* Articles List */}
      <div className="bg-white shadow-md rounded-lg overflow-hidden">
        <div className="px-6 py-4 border-b border-gray-200">
          <h2 className="text-lg font-medium">
            Articles with this tag ({data.articles.length})
          </h2>
        </div>

        {data.articles.length === 0 ? (
          <div className="text-center py-8 text-gray-500">
            No articles found with this tag.
          </div>
        ) : (
          <div className="divide-y divide-gray-200">
            {data.articles.map((article) => (
              <div key={article.id} className="px-6 py-4 hover:bg-gray-50">
                <div className="flex items-center justify-between">
                  <div className="flex-1">
                    <h3 className="text-lg font-medium text-gray-900 mb-1">
                      <Link
                        href={`/articles/${article.slug}`}
                        className="hover:text-blue-600"
                        target="_blank"
                      >
                        {article.title}
                      </Link>
                    </h3>
                    <div className="flex items-center space-x-4 text-sm text-gray-500">
                      <span>{article.category?.categoryName || 'Uncategorized'}</span>
                      <span>{new Date(article.createdAt).toLocaleDateString()}</span>
                      <span>{article._count.likes} likes</span>
                      <span>{article._count.comments} comments</span>
                      <span className={`px-2 py-1 rounded-full text-xs ${
                        article.isContentGenerated 
                          ? 'bg-green-100 text-green-800' 
                          : 'bg-yellow-100 text-yellow-800'
                      }`}>
                        {article.isContentGenerated ? 'Published' : 'Draft'}
                      </span>
                    </div>
                  </div>
                  <div className="flex items-center space-x-2">
                    <Link
                      href={`/admin/articles/${article.id}/edit`}
                      className="text-indigo-600 hover:text-indigo-900 text-sm"
                    >
                      Edit
                    </Link>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Search Modal */}
      {showSearchModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-4xl max-h-[80vh] overflow-hidden">
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-xl font-semibold">
                Add Articles to #{data.tag.tagName}
              </h2>
              <button
                onClick={() => setShowSearchModal(false)}
                className="text-gray-400 hover:text-gray-600"
              >
                <X className="h-6 w-6" />
              </button>
            </div>

            <form onSubmit={handleSearchSubmit} className="mb-4">
              <div className="flex space-x-2">
                <div className="flex-1 relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
                  <input
                    type="text"
                    placeholder="Search articles..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  />
                </div>
                <button
                  type="submit"
                  disabled={searchLoading}
                  className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-md transition-colors disabled:opacity-50"
                >
                  {searchLoading ? 'Searching...' : 'Search'}
                </button>
              </div>
            </form>

            <div className="overflow-y-auto max-h-96 border border-gray-200 rounded-md">
              {searchResults.length === 0 ? (
                <div className="text-center py-8 text-gray-500">
                  {searchLoading ? 'Searching...' : 'No articles found without this tag.'}
                </div>
              ) : (
                <div className="divide-y divide-gray-200">
                  {searchResults.map((article) => (
                    <div key={article.id} className="px-4 py-3 hover:bg-gray-50">
                      <div className="flex items-center justify-between">
                        <div className="flex-1">
                          <h4 className="font-medium text-gray-900">{article.title}</h4>
                          <div className="flex items-center space-x-3 text-sm text-gray-500 mt-1">
                            <span>{article.category?.categoryName || 'Uncategorized'}</span>
                            <span>{new Date(article.createdAt).toLocaleDateString()}</span>
                          </div>
                        </div>
                        <button
                          onClick={() => addTagToArticle(article.id)}
                          className="bg-green-600 hover:bg-green-700 text-white px-3 py-1 rounded text-sm transition-colors"
                        >
                          Add Tag
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

'use client';

import { useEffect, useState } from 'react';
import { useRouter } from 'next/navigation';
import Link from 'next/link';
import { useAuth } from '@/hooks/use-auth';
import { Role } from '@prisma/client';

interface Tag {
  tagId: string;
  tagName: string;
  description: string | null;
  color: string | null;
  createdAt: string;
  updatedAt: string;
  _count: {
    articles: number;
  };
}

export default function AdminTagsPage() {
  const router = useRouter();
  const { hasMinRole, isLoadingRole } = useAuth();
  const [tags, setTags] = useState<Tag[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [editingTag, setEditingTag] = useState<Tag | null>(null);
  const [formData, setFormData] = useState({
    tagName: '',
    description: '',
    color: ''
  });

  const fetchTags = async () => {
    try {
      const searchParam = searchTerm ? `?search=${encodeURIComponent(searchTerm)}` : '';
      const response = await fetch(`/api/admin/tags${searchParam}`);

      if (!response.ok) {
        throw new Error('Failed to fetch tags');
      }

      const data = await response.json();
      setTags(data);
    } catch (error) {
      console.error('Error fetching tags:', error);
      setError('Failed to fetch tags');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTags();
  }, [searchTerm]);

  useEffect(() => {
    if (!isLoadingRole && !hasMinRole(Role.ADMIN)) {
      router.push('/dashboard');
    }
  }, [isLoadingRole, hasMinRole, router]);

  const handleCreateTag = async (e: React.FormEvent) => {
    e.preventDefault();
    setError(null);

    try {
      const response = await fetch('/api/admin/tags', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to create tag');
      }

      await fetchTags();
      setShowCreateForm(false);
      setFormData({ tagName: '', description: '', color: '' });
    } catch (error) {
      console.error('Error creating tag:', error);
      setError(error instanceof Error ? error.message : 'Failed to create tag');
    }
  };

  const handleUpdateTag = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!editingTag) return;

    setError(null);

    try {
      const response = await fetch(`/api/admin/tags/${editingTag.tagId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to update tag');
      }

      await fetchTags();
      setEditingTag(null);
      setFormData({ tagName: '', description: '', color: '' });
    } catch (error) {
      console.error('Error updating tag:', error);
      setError(error instanceof Error ? error.message : 'Failed to update tag');
    }
  };

  const handleDeleteTag = async (tagId: string, tagName: string) => {
    if (!confirm(`Are you sure you want to delete the tag "${tagName}"? This will remove it from all articles.`)) {
      return;
    }

    try {
      const response = await fetch(`/api/admin/tags/${tagId}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to delete tag');
      }

      await fetchTags();
    } catch (error) {
      console.error('Error deleting tag:', error);
      setError(error instanceof Error ? error.message : 'Failed to delete tag');
    }
  };

  const startEdit = (tag: Tag) => {
    setEditingTag(tag);
    setFormData({
      tagName: tag.tagName,
      description: tag.description || '',
      color: tag.color || ''
    });
    setShowCreateForm(false);
  };

  const cancelEdit = () => {
    setEditingTag(null);
    setShowCreateForm(false);
    setFormData({ tagName: '', description: '', color: '' });
  };

  if (isLoadingRole) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-lg">Loading...</div>
      </div>
    );
  }

  if (!hasMinRole(Role.ADMIN)) {
    return null;
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Tag Management</h1>
        <button
          onClick={() => {
            setShowCreateForm(true);
            setEditingTag(null);
            setFormData({ tagName: '', description: '', color: '' });
          }}
          className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-md transition-colors"
        >
          Create New Tag
        </button>
      </div>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      {/* Search */}
      <div className="mb-6">
        <input
          type="text"
          placeholder="Search tags..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="w-full px-4 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
        />
      </div>

      {/* Create/Edit Form */}
      {(showCreateForm || editingTag) && (
        <div className="bg-gray-50 p-6 rounded-lg mb-6">
          <h2 className="text-xl font-semibold mb-4">
            {editingTag ? 'Edit Tag' : 'Create New Tag'}
          </h2>
          <form onSubmit={editingTag ? handleUpdateTag : handleCreateTag} className="space-y-4">
            <div>
              <label htmlFor="tagName" className="block text-sm font-medium text-gray-700 mb-1">
                Tag Name *
              </label>
              <input
                type="text"
                id="tagName"
                value={formData.tagName}
                onChange={(e) => setFormData({ ...formData, tagName: e.target.value })}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                required
              />
            </div>
            <div>
              <label htmlFor="description" className="block text-sm font-medium text-gray-700 mb-1">
                Description
              </label>
              <textarea
                id="description"
                value={formData.description}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                rows={3}
              />
            </div>
            <div>
              <label htmlFor="color" className="block text-sm font-medium text-gray-700 mb-1">
                Color (Hex)
              </label>
              <input
                type="color"
                id="color"
                value={formData.color || '#3B82F6'}
                onChange={(e) => setFormData({ ...formData, color: e.target.value })}
                className="w-20 h-10 border border-gray-300 rounded-md"
              />
            </div>
            <div className="flex space-x-3">
              <button
                type="submit"
                className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-md transition-colors"
              >
                {editingTag ? 'Update Tag' : 'Create Tag'}
              </button>
              <button
                type="button"
                onClick={cancelEdit}
                className="bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded-md transition-colors"
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Tags List */}
      {loading ? (
        <div className="text-center py-8">
          <div className="text-lg">Loading tags...</div>
        </div>
      ) : (
        <div className="bg-white shadow-md rounded-lg overflow-hidden">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Tag
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Description
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Articles
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Created
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {tags.map((tag) => (
                <tr key={tag.tagId}>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="flex items-center">
                      {tag.color && (
                        <div
                          className="w-4 h-4 rounded-full mr-3"
                          style={{ backgroundColor: tag.color }}
                        />
                      )}
                      <div className="text-sm font-medium text-gray-900">{tag.tagName}</div>
                    </div>
                  </td>
                  <td className="px-6 py-4">
                    <div className="text-sm text-gray-900 max-w-xs truncate">
                      {tag.description || '-'}
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm text-gray-900">{tag._count.articles}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm text-gray-900">
                      {new Date(tag.createdAt).toLocaleDateString()}
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                    <Link
                      href={`/admin/tags/${tag.tagId}/articles`}
                      className="text-blue-600 hover:text-blue-900 mr-4"
                    >
                      View Articles
                    </Link>
                    <button
                      onClick={() => startEdit(tag)}
                      className="text-indigo-600 hover:text-indigo-900 mr-4"
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDeleteTag(tag.tagId, tag.tagName)}
                      className="text-red-600 hover:text-red-900"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          {tags.length === 0 && !loading && (
            <div className="text-center py-8 text-gray-500">
              {searchTerm ? 'No tags found matching your search.' : 'No tags created yet.'}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

"use client";

import { useState, useEffect } from "react";
import { useAuth } from "@/hooks/use-auth";
import { Role } from "@prisma/client";
import { Search, Shield, Edit2, Users } from "lucide-react";
import { useRouter } from "next/navigation";

type User = {
  clerkUserId: string;
  email: string;
  username: string | null;
  firstName: string | null;
  lastName: string | null;
  imageUrl: string | null;
  role: Role;
  subscriptionTier: string;
  subscriptionStatus: string;
  appSpecificCreatedAt: string;
  lastLoginToApp: string | null;
};

export default function AdminUsersPage() {
  const { userRole, isLoadingRole, hasMinRole } = useAuth();
  const router = useRouter();
  const [users, setUsers] = useState<User[]>([]);
  const [loading, setLoading] = useState(true);
  const [search, setSearch] = useState("");
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [editingUserId, setEditingUserId] = useState<string | null>(null);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    console.log("AdminUsersPage - isLoadingRole:", isLoadingRole);
    console.log("AdminUsersPage - userRole:", userRole);
    console.log("AdminUsersPage - hasMinRole(ADMIN):", hasMinRole(Role.ADMIN));

    if (!isLoadingRole && !hasMinRole(Role.ADMIN)) {
      router.push("/dashboard");
    }
  }, [userRole, isLoadingRole, hasMinRole, router]);

  useEffect(() => {
    if (!isLoadingRole && hasMinRole(Role.ADMIN)) {
      fetchUsers();
    }
  }, [page, search, isLoadingRole]);

  const fetchUsers = async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams({
        page: page.toString(),
        limit: "20",
        ...(search && { search }),
      });

      const response = await fetch(`/api/admin/users?${params}`);
      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.error || `Failed to fetch users: ${response.status}`);
      }

      const data = await response.json();
      setUsers(data.users);
      setTotalPages(data.pagination.totalPages);
      setError(null);
    } catch (error) {
      console.error("Error fetching users:", error);
      setError(error instanceof Error ? error.message : "Failed to fetch users");
    } finally {
      setLoading(false);
    }
  };

  const updateUserRole = async (userId: string, newRole: Role) => {
    try {
      const response = await fetch(`/api/admin/users/${userId}/role`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ role: newRole }),
      });

      if (!response.ok) {
        const error = await response.json();
        alert(error.error || "Failed to update role");
        return;
      }

      // Update local state
      setUsers(users.map(user =>
        user.clerkUserId === userId ? { ...user, role: newRole } : user
      ));
      setEditingUserId(null);
    } catch (error) {
      console.error("Error updating role:", error);
      alert("Failed to update role");
    }
  };

  const getRoleBadgeColor = (role: Role) => {
    switch (role) {
      case Role.ADMIN:
        return "bg-red-100 text-red-800";
      case Role.MODERATOR:
        return "bg-purple-100 text-purple-800";
      case Role.EDITOR:
        return "bg-blue-100 text-blue-800";
      default:
        return "bg-gray-100 text-gray-800";
    }
  };

  if (isLoadingRole || !hasMinRole(Role.ADMIN)) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 flex items-center gap-2">
          <Shield className="h-8 w-8 text-blue-600" />
          User Management
        </h1>
        <p className="mt-2 text-gray-600">
          Manage user roles and permissions
        </p>
      </div>

      <div className="mb-6">
        <div className="relative">
          <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-5 w-5 text-gray-400" />
          <input
            type="text"
            placeholder="Search by email, username, or name..."
            value={search}
            onChange={(e) => {
              setSearch(e.target.value);
              setPage(1);
            }}
            className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
          />
        </div>
      </div>

      {error && (
        <div className="mb-4 bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded">
          Error: {error}
        </div>
      )}

      {loading ? (
        <div className="flex justify-center py-12">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
        </div>
      ) : error ? (
        <div className="text-center py-8">
          <button
            onClick={() => fetchUsers()}
            className="text-blue-600 hover:text-blue-800"
          >
            Try again
          </button>
        </div>
      ) : (
        <div className="bg-white shadow-sm rounded-lg overflow-hidden">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  User
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Role
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Subscription
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Joined
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Last Login
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {users.map((user) => (
                <tr key={user.clerkUserId} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="flex items-center">
                      {user.imageUrl ? (
                        <img
                          className="h-10 w-10 rounded-full"
                          src={user.imageUrl}
                          alt=""
                        />
                      ) : (
                        <div className="h-10 w-10 rounded-full bg-gray-200 flex items-center justify-center">
                          <Users className="h-5 w-5 text-gray-500" />
                        </div>
                      )}
                      <div className="ml-4">
                        <div className="text-sm font-medium text-gray-900">
                          {user.firstName || user.lastName
                            ? `${user.firstName || ""} ${user.lastName || ""}`
                            : user.username || "No name"}
                        </div>
                        <div className="text-sm text-gray-500">{user.email}</div>
                      </div>
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    {editingUserId === user.clerkUserId ? (
                      <select
                        value={user.role}
                        onChange={(e) => updateUserRole(user.clerkUserId, e.target.value as Role)}
                        onBlur={() => setEditingUserId(null)}
                        className="text-sm border border-gray-300 rounded px-2 py-1 focus:ring-2 focus:ring-blue-500"
                        autoFocus
                      >
                        {Object.values(Role).map((role) => (
                          <option key={role} value={role}>
                            {role}
                          </option>
                        ))}
                      </select>
                    ) : (
                      <div className="flex items-center gap-2">
                        <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getRoleBadgeColor(user.role)}`}>
                          {user.role}
                        </span>
                        <button
                          onClick={() => setEditingUserId(user.clerkUserId)}
                          className="text-gray-400 hover:text-gray-600"
