import { NextResponse } from "next/server";
import { auth } from "@clerk/nextjs/server";
import { prisma } from "@/lib/prisma";
import { Role } from "@prisma/client";

export async function PUT(
  request: Request,
  { params }: { params: Promise<{ typeId: string }> }
) {
  const { typeId } = await params;

  try {
    const { userId } = await auth();
    if (!userId) {
      return NextResponse.json(
        { error: "Unauthorized" },
        { status: 401 }
      );
    }

    const user = await prisma.user.findUnique({
      where: { clerkUserId: userId },
    });

    if (!user || user.role !== Role.ADMIN) {
      return NextResponse.json(
        { error: "Forbidden" },
        { status: 403 }
      );
    }

    const body = await request.json();
    const updateData: any = {};

    // Only update provided fields
    if (body.displayName !== undefined) updateData.displayName = body.displayName;
    if (body.description !== undefined) updateData.description = body.description;
    if (body.defaultModelId !== undefined) updateData.defaultModelId = body.defaultModelId;

    const interactionType = await prisma.aIInteractionType.update({
      where: { typeId },
      data: updateData,
      include: {
        defaultModel: {
          select: {
            modelId: true,
            displayName: true,
            provider: true,
            isActive: true
          }
        }
      }
    });

    return NextResponse.json(interactionType);
  } catch (error) {
    console.error("Error updating AI interaction type:", error);
    return NextResponse.json(
      { error: "Failed to update AI interaction type" },
      { status: 500 }
    );
  }
}

export async function DELETE(
  request: Request,
  { params }: { params: Promise<{ typeId: string }> }
) {
  const { typeId } = await params;

  try {
    const { userId } = await auth();
    if (!userId) {
      return NextResponse.json(
        { error: "Unauthorized" },
        { status: 401 }
      );
    }

    const user = await prisma.user.findUnique({
      where: { clerkUserId: userId },
    });

    if (!user || user.role !== Role.ADMIN) {
      return NextResponse.json(
        { error: "Forbidden" },
        { status: 403 }
      );
    }

    // Check if this interaction type has any interactions
    const usageCount = await prisma.aIInteraction.count({
      where: { interactionTypeId: typeId }
    });

    if (usageCount > 0) {
      return NextResponse.json(
        { error: "Cannot delete interaction type with existing interactions" },
        { status: 400 }
      );
    }

    await prisma.aIInteractionType.delete({
      where: { typeId }
    });

    return NextResponse.json({ message: "Interaction type deleted successfully" });
  } catch (error) {
    console.error("Error deleting AI interaction type:", error);
    return NextResponse.json(
      { error: "Failed to delete AI interaction type" },
      { status: 500 }
    );
  }
}

import { NextResponse } from "next/server";
import { auth } from "@clerk/nextjs/server";
import { prisma } from "@/lib/prisma";
import { Role } from "@prisma/client";
import { aiService } from "@/lib/ai-service";

export async function PUT(
  request: Request,
  { params }: { params: Promise<{ modelId: string }> }
) {
  const { modelId } = await params;

  try {
    const { userId } = await auth();
    if (!userId) {
      return NextResponse.json(
        { error: "Unauthorized" },
        { status: 401 }
      );
    }

    const user = await prisma.user.findUnique({
      where: { clerkUserId: userId },
    });

    if (!user || user.role !== Role.ADMIN) {
      return NextResponse.json(
        { error: "Forbidden" },
        { status: 403 }
      );
    }

    const body = await request.json();
    const updateData: any = {};

    // Only update provided fields
    if (body.displayName !== undefined) updateData.displayName = body.displayName;
    if (body.description !== undefined) updateData.description = body.description;
    if (body.inputTokenCostPer1M !== undefined) updateData.inputTokenCostPer1M = parseFloat(body.inputTokenCostPer1M);
    if (body.outputTokenCostPer1M !== undefined) updateData.outputTokenCostPer1M = parseFloat(body.outputTokenCostPer1M);
    if (body.maxTokens !== undefined) updateData.maxTokens = body.maxTokens ? parseInt(body.maxTokens) : null;
    if (body.isActive !== undefined) updateData.isActive = body.isActive;
    if (body.isDefault !== undefined) updateData.isDefault = body.isDefault;
    if (body.apiKey !== undefined) updateData.apiKey = body.apiKey; // Will be encrypted in aiService

    // If setting as default, unset other defaults
    if (body.isDefault) {
      await prisma.aIModel.updateMany({
        where: { 
          modelId: { not: modelId },
          isDefault: true 
        },
        data: { isDefault: false }
      });
    }

    const model = await aiService.updateModel(modelId, updateData);

    return NextResponse.json(model);
  } catch (error) {
    console.error("Error updating AI model:", error);
    return NextResponse.json(
      { error: "Failed to update AI model" },
      { status: 500 }
    );
  }
}

export async function DELETE(
  request: Request,
  { params }: { params: Promise<{ modelId: string }> }
) {
  const { modelId } = await params;

  try {
    const { userId } = await auth();
    if (!userId) {
      return NextResponse.json(
        { error: "Unauthorized" },
        { status: 401 }
      );
    }

    const user = await prisma.user.findUnique({
      where: { clerkUserId: userId },
    });

    if (!user || user.role !== Role.ADMIN) {
      return NextResponse.json(
        { error: "Forbidden" },
        { status: 403 }
      );
    }

    // Check if this model is being used by any interaction types
    const usageCount = await prisma.aIInteractionType.count({
      where: { defaultModelId: modelId }
    });

    if (usageCount > 0) {
      return NextResponse.json(
        { error: "Cannot delete model that is assigned to interaction types" },
        { status: 400 }
      );
    }

    await aiService.deleteModel(modelId);

    return NextResponse.json({ message: "Model deleted successfully" });
  } catch (error) {
    console.error("Error deleting AI model:", error);
    return NextResponse.json(
      { error: "Failed to delete AI model" },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { auth } from '@clerk/nextjs/server';
import { prisma } from '@/lib/prisma';
import { hasRole } from '@/lib/auth';

export async function GET(
  request: NextRequest,
  { params }: { params: Promise<{ articleId: string }> }
) {
  try {
    const { userId } = await auth();
    if (!userId) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    // Get user and check admin role
    const user = await prisma.user.findUnique({
      where: { clerkUserId: userId },
      select: { role: true }
    });

    if (!user) {
      return NextResponse.json({ error: 'User not found' }, { status: 404 });
    }

    const isAdmin = hasRole(user.role, ['ADMIN', 'MODERATOR']);
    if (!isAdmin) {
      return NextResponse.json({ error: 'Forbidden' }, { status: 403 });
    }

    // Await params in Next.js 15
    const { articleId } = await params;

    // Get search params
    const searchParams = request.nextUrl.searchParams;
    const includeInactive = searchParams.get('includeInactive') === 'true';
    const limit = parseInt(searchParams.get('limit') || '20');
    const offset = parseInt(searchParams.get('offset') || '0');

    // Build where clause
    const where: any = { articleId };
    if (!includeInactive) {
      where.isActive = true;
    }

    // Get changes with pagination
    const [changes, total] = await Promise.all([
      prisma.articleChangeHistory.findMany({
        where,
        orderBy: { createdAt: 'desc' },
        take: limit,
        skip: offset,
        include: {
          user: {
            select: {
              clerkUserId: true,
              email: true,
              firstName: true,
              lastName: true,
              imageUrl: true,
            },
          },
          suggestion: {
            select: {
              suggestionId: true,
              suggestionType: true,
              suggestionDetails: true,
              isApproved: true,
              processedAt: true,
            },
          },
          rollbackUser: {
            select: {
              clerkUserId: true,
              email: true,
              firstName: true,
              lastName: true,
            },
          },
        },
      }),
      prisma.articleChangeHistory.count({ where }),
    ]);

    return NextResponse.json({
      changes,
      pagination: {
        total,
        limit,
        offset,
        hasMore: offset + limit < total,
      },
    });
  } catch (error) {
    console.error('Error fetching article changes:', error);
    return NextResponse.json(
      { error: 'Failed to fetch article changes' },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from "next/server";
import { requireMinRole } from "@/lib/auth";
import { Role } from "@prisma/client";
import prisma from "@/lib/prisma";

export async function GET(request: NextRequest) {
  try {
    await requireMinRole(Role.EDITOR);

    const searchParams = request.nextUrl.searchParams;
    const page = parseInt(searchParams.get("page") || "1");
    const limit = 20;
    const search = searchParams.get("search") || "";
    const filter = searchParams.get("filter") || "all";

    const where: any = {};

    if (search) {
      where.OR = [
        { articleTitle: { contains: search, mode: "insensitive" } },
        { articleSlug: { contains: search, mode: "insensitive" } },
      ];
    }

    if (filter === "flagged") {
      where.isFlagged = true;
    } else if (filter === "generated") {
      where.isContentGenerated = true;
    }

    const [articles, totalCount] = await Promise.all([
      prisma.article.findMany({
        where,
        skip: (page - 1) * limit,
        take: limit,
        include: {
          categories: {
            include: {
              category: {
                select: { categoryName: true },
              },
            },
          },
          _count: {
            select: {
              likes: true,
              comments: true,
            },
          },
        },
        orderBy: { updatedAt: "desc" },
      }),
      prisma.article.count({ where }),
    ]);

    return NextResponse.json({
      articles,
      totalPages: Math.ceil(totalCount / limit),
      currentPage: page,
      totalCount,
    });
  } catch (error) {
    console.error("Error fetching articles:", error);
    return NextResponse.json(
      { error: "Failed to fetch articles" },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { auth } from '@clerk/nextjs/server';
import prisma from '@/lib/prisma';
import { Role } from '@prisma/client';

export async function GET(request: NextRequest) {
  try {
    const { userId } = await auth();
    if (!userId) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const user = await prisma.user.findUnique({
      where: { clerkUserId: userId },
    });

    if (!user || user.role !== Role.ADMIN) {
      return NextResponse.json({ error: 'Forbidden' }, { status: 403 });
    }

    const categories = await prisma.category.findMany({
      include: {
        _count: {
          select: { articles: true },
        },
      },
      orderBy: { categoryName: 'asc' },
    });

    return NextResponse.json(categories);
  } catch (error) {
    console.error('Error fetching categories:', error);
    return NextResponse.json(
      { error: 'Internal Server Error' },
      { status: 500 }
    );
  }
}

export async function POST(request: NextRequest) {
  try {
    const { userId } = await auth();
    if (!userId) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const user = await prisma.user.findUnique({
      where: { clerkUserId: userId },
    });

    if (!user || user.role !== Role.ADMIN) {
      return NextResponse.json({ error: 'Forbidden' }, { status: 403 });
    }

    const { categoryName, description } = await request.json();

    if (!categoryName || !categoryName.trim()) {
      return NextResponse.json(
        { error: 'Category name is required' },
        { status: 400 }
      );
    }

    const existingCategory = await prisma.category.findFirst({
      where: { categoryName: categoryName.trim() },
    });

    if (existingCategory) {
      return NextResponse.json(
        { error: 'Category with this name already exists' },
        { status: 400 }
      );
    }

    const category = await prisma.category.create({
      data: {
        categoryName: categoryName.trim(),
        description: description?.trim() || null,
      },
      include: {
        _count: {
          select: { articles: true },
        },
      },
    });

    return NextResponse.json(category);
  } catch (error) {
    console.error('Error creating category:', error);
    return NextResponse.json(
      { error: 'Internal Server Error' },
      { status: 500 }
    );
  }
}

