      where.isActive = true;
    }
    if (changeType) {
      where.changeType = changeType;
    }
    if (userIdFilter) {
      where.clerkUserId = userIdFilter;
    }

    console.log('Admin changes API - where clause:', where);
    console.log('Admin changes API - limit/offset:', limit, offset);

    // Get changes with pagination
    const [changes, total] = await Promise.all([
      prisma.articleChangeHistory.findMany({
        where,
        orderBy: { createdAt: 'desc' },
        take: limit,
        skip: offset,
        include: {
          article: {
            select: {
              articleId: true,
              articleTitle: true,
              articleSlug: true,
            },
          },
          user: {
            select: {
              clerkUserId: true,
              email: true,
              firstName: true,
              lastName: true,
              imageUrl: true,
            },
          },
          suggestion: {
            select: {
              suggestionId: true,
              suggestionType: true,
              suggestionDetails: true,
              isApproved: true,
            },
          },
          rollbackUser: {
            select: {
              clerkUserId: true,
              email: true,
              firstName: true,
              lastName: true,
            },
          },
        },
      }),
      prisma.articleChangeHistory.count({ where }),
    ]);

    console.log('Admin changes API - found changes:', changes.length, 'total:', total);

    // Get summary statistics
    const stats = await prisma.articleChangeHistory.groupBy({
      by: ['changeType'],
      _count: {
        id: true,
      },
      where: where.isActive !== undefined ? { isActive: where.isActive } : {},
    });

    return NextResponse.json({
      changes,
      stats: stats.reduce((acc, stat) => {
        acc[stat.changeType] = stat._count.id;
        return acc;
      }, {} as Record<string, number>),
      pagination: {
        total,
        limit,
        offset,
        hasMore: offset + limit < total,
      },
    });
  } catch (error) {
    console.error('Error fetching changes:', error);

    // Provide more detailed error information
    const errorMessage = error instanceof Error ? error.message : 'Unknown error';
    const errorDetails = {
      error: 'Failed to fetch changes',
      message: errorMessage,
      stack: error instanceof Error ? error.stack : undefined,
    };

    return NextResponse.json(errorDetails, { status: 500 });
  }
}

import { NextRequest, NextResponse } from "next/server";
import { auth } from "@clerk/nextjs/server";
import { prisma } from "@/lib/prisma";

export async function GET(request: NextRequest) {
  try {
    const { userId } = await auth();
    if (!userId) {
      return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
    }

    const user = await prisma.user.findUnique({
      where: { clerkUserId: userId },
      select: { role: true },
    });

    if (!user || (user.role !== "ADMIN" && user.role !== "MODERATOR")) {
      return NextResponse.json({ error: "Forbidden" }, { status: 403 });
    }

    const searchParams = request.nextUrl.searchParams;
    const page = parseInt(searchParams.get("page") || "1");
    const limit = parseInt(searchParams.get("limit") || "20");
    const status = searchParams.get("status") || undefined;
    const templateId = searchParams.get("templateId") || undefined;
    const to = searchParams.get("to") || undefined;

    const skip = (page - 1) * limit;

    const where: any = {};
    if (status) where.status = status;
    if (templateId) where.templateId = templateId;
    if (to) where.to = { contains: to, mode: "insensitive" };

    const [logs, total] = await Promise.all([
      prisma.emailLog.findMany({
        where,
        orderBy: { sentAt: "desc" },
        skip,
        take: limit,
        include: {
          template: {
            select: {
              templateName: true,
              templateKey: true,
            },
          },
        },
      }),
      prisma.emailLog.count({ where }),
    ]);

    return NextResponse.json({
      logs,
      pagination: {
        page,
        limit,
        total,
        pages: Math.ceil(total / limit),
      },
    });
  } catch (error) {
    console.error("Error fetching email logs:", error);
    return NextResponse.json(
      { error: "Failed to fetch email logs" },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from "next/server";
import { requireMinRole } from "@/lib/auth";
import { Role } from "@prisma/client";
import prisma from "@/lib/prisma";

export async function GET(request: NextRequest) {
  try {
    await requireMinRole(Role.MODERATOR);

    const searchParams = request.nextUrl.searchParams;
    const type = searchParams.get("type") || "all";

    const items: any[] = [];

    if (type === "all" || type === "articles") {
      const flaggedArticles = await prisma.article.findMany({
        where: { isFlagged: true },
        include: {
          createdBy: {
            select: { username: true, email: true },
          },
          flaggedBy: {
            select: { username: true, email: true },
          },
        },
        orderBy: { flaggedAt: "desc" },
      });

      items.push(...flaggedArticles.map(article => ({
        id: article.articleId,
        type: "article" as const,
        content: article.contentHtml?.substring(0, 300) || "",
        title: article.articleTitle,
        flaggedAt: article.flaggedAt,
        flagReason: article.flagReason,
        flaggedBy: article.flaggedBy || { username: null, email: "Unknown" },
        author: article.createdBy || { username: null, email: "Unknown" },
      })));
    }

    if (type === "all" || type === "comments") {
      const flaggedComments = await prisma.comment.findMany({
        where: { isFlagged: true },
        include: {
          user: {
            select: { username: true, email: true },
          },
          flaggedBy: {
            select: { username: true, email: true },
          },
          article: {
            select: {
              articleId: true,
              articleTitle: true,
              articleSlug: true,
            },
          },
        },
        orderBy: { flaggedAt: "desc" },
      });

      items.push(...flaggedComments.map(comment => ({
        id: comment.commentId,
        type: "comment" as const,
        content: comment.content,
        flaggedAt: comment.flaggedAt,
        flagReason: comment.flagReason,
        flaggedBy: comment.flaggedBy || { username: null, email: "Unknown" },
        author: comment.user,
        articleInfo: comment.article,
      })));
    }

    // Sort by flaggedAt date
    items.sort((a, b) => {
      const dateA = a.flaggedAt ? new Date(a.flaggedAt).getTime() : 0;
      const dateB = b.flaggedAt ? new Date(b.flaggedAt).getTime() : 0;
      return dateB - dateA;
    });

    return NextResponse.json({ items });
  } catch (error) {
    console.error("Error fetching flagged content:", error);
    return NextResponse.json(
      { error: "Failed to fetch flagged content" },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from 'next/server';
