import { NextRequest, NextResponse } from "next/server";
import { requireMinRole, getAuthUser } from "@/lib/auth";
import { prisma } from "@/lib/prisma";
import { Role } from "@prisma/client";

export async function PATCH(
  req: NextRequest,
  { params }: { params: Promise<{ userId: string }> }
) {
  try {
    const authUser = await requireMinRole(Role.ADMIN);
    const { userId } = await params;
    const { role } = await req.json();

    // Validate role
    if (!Object.values(Role).includes(role)) {
      return NextResponse.json(
        { error: "Invalid role" },
        { status: 400 }
      );
    }

    // Prevent self-demotion
    if (authUser.clerkUserId === userId && role !== Role.ADMIN) {
      return NextResponse.json(
        { error: "Cannot change your own role from admin" },
        { status: 400 }
      );
    }

    // Update user role
    const updatedUser = await prisma.user.update({
      where: { clerkUserId: userId },
      data: { role },
      select: {
        clerkUserId: true,
        email: true,
        role: true,
      },
    });

    return NextResponse.json({
      message: "Role updated successfully",
      user: updatedUser,
    });
  } catch (error) {
    console.error("Error updating user role:", error);
    if (error instanceof Error) {
      if (error.message.includes("Forbidden")) {
        return NextResponse.json(
          { error: "Forbidden: Admin access required" },
          { status: 403 }
        );
      }
      if (error.message.includes("Record to update not found")) {
        return NextResponse.json(
          { error: "User not found" },
          { status: 404 }
        );
      }
    }
    return NextResponse.json(
      { error: "Internal server error" },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from "next/server";
import { getAuth } from "@clerk/nextjs/server";
import { prisma } from "@/lib/prisma";

export async function POST(
  req: NextRequest,
  { params }: { params: Promise<{ groupId: string }> }
) {
  const { userId } = await getAuth(req);
  const { groupId } = await params;

  if (!userId) {
    return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
  }

  try {
    const { articleId } = await req.json();

    if (!articleId || typeof articleId !== "string") {
      return NextResponse.json(
        { error: "Invalid article ID" },
        { status: 400 }
      );
    }

    const group = await prisma.articleGroup.findFirst({
      where: {
        groupId,
        clerkUserId: userId,
      },
    });

    if (!group) {
      return NextResponse.json(
        { error: "Group not found" },
        { status: 404 }
      );
    }

    const article = await prisma.article.findUnique({
      where: { articleId },
    });

    if (!article) {
      return NextResponse.json(
        { error: "Article not found" },
        { status: 404 }
      );
    }

    const groupArticle = await prisma.articleGroupArticle.create({
      data: {
        groupId,
        articleId,
      },
    });

    return NextResponse.json(groupArticle);
  } catch (error: any) {
    if (error?.code === "P2002") {
      return NextResponse.json(
        { error: "Article already in group" },
        { status: 409 }
      );
    }

    console.error("Error adding article to group:", error);
    return NextResponse.json(
      { error: "Failed to add article to group" },
      { status: 500 }
    );
  }
}

export async function DELETE(
  req: NextRequest,
  { params }: { params: Promise<{ groupId: string }> }
) {
  const { userId } = await getAuth(req);
  const { groupId } = await params;
  const { searchParams } = new URL(req.url);
  const articleId = searchParams.get("articleId");

  if (!userId) {
