    // Get existing examples to ensure variety
    const existingExamples = await prisma.interactiveExample.findMany({
      where: { articleId },
      select: { scenarioOrQuestionText: true }
    });

    const existingQuestions = existingExamples.map(e => e.scenarioOrQuestionText);

    // Generate examples using AI service
    console.log('Generating examples with AI...');

    // Get the first category name (articles can have multiple categories)
    const categoryName = article.categories[0]?.category.categoryName || 'General';

    const response = await aiService.generateInteractiveExamples(
      article.articleTitle,
      categoryName,
      existingQuestions,
      userId
    );

    // Save generated examples
    const createdExamples = [];
    for (const example of response.examples || []) {
      // Validate and format options for multiple choice questions
      let formattedOptions = undefined;
      if (example.question_type === 'multiple_choice' && example.options_json) {
        // Ensure options have the correct format
        formattedOptions = example.options_json.map((opt, index) => {
          if (typeof opt === 'string') {
            // If options are strings, convert to proper format
            return { id: String.fromCharCode(97 + index), text: opt };
          }
          // Ensure id and text exist
          return {
            id: opt.id || String.fromCharCode(97 + index),
            text: opt.text || 'Option ' + (index + 1)
          };
        });
      }

      const created = await prisma.interactiveExample.create({
        data: {
          articleId,
          questionType: example.question_type.toUpperCase().replace(' ', '_') as "MULTIPLE_CHOICE" | "TEXT_INPUT" | "COMMAND_LINE",
          scenarioOrQuestionText: example.scenario_or_question_text,
          optionsJson: formattedOptions,
          correctAnswerDescription: example.correct_answer_description,
          aiMarkingPromptHint: example.ai_marking_prompt_hint
        }
      });
      createdExamples.push(created);
    }

    return NextResponse.json({
      message: "Examples generated successfully",
      examples: createdExamples
    });

  } catch (error) {
    console.error("Error generating examples:", error);
    return NextResponse.json(
      { error: "Failed to generate examples" },
      { status: 500 }
    );
  }
}

import { NextResponse } from "next/server";
import { auth } from "@clerk/nextjs/server";
import { prisma } from "@/lib/prisma";
import { aiService } from "@/lib/ai-service";
import { checkSubscription } from "@/lib/subscription-check";

export async function POST(
  request: Request,
  { params }: { params: Promise<{ articleId: string }> }
) {
  const { articleId } = await params;
  try {
    const { userId } = await auth();
    if (!userId) {
      return NextResponse.json(
        { error: "Unauthorized" },
        { status: 401 }
      );
    }

    // Check subscription status
    const subscription = await checkSubscription(userId);

    if (!subscription.permissions.canGenerateContent) {
      return NextResponse.json(
        { error: "Subscription required", message: "Please subscribe to generate article content" },
        { status: 403 }
      );
    }

    const article = await prisma.article.findUnique({
      where: { articleId },
      include: { 
        categories: {
          include: {
            category: true
          }
        }
      }
    });

    if (!article) {
      return NextResponse.json(
        { error: "Article not found" },
        { status: 404 }
      );
    }

    if (article.isContentGenerated && article.contentHtml) {
      return NextResponse.json({
        message: "Content already generated",
        article
      });
    }

    // Find primary category or use first category
    const primaryCategory = article.categories.find(ac => ac.isPrimary)?.category 
      || article.categories[0]?.category;

    if (!primaryCategory) {
      return NextResponse.json(
        { error: "Article has no categories" },
        { status: 400 }
      );
    }

    // Generate content using AI service
    console.log('Generating content with AI...');

    const result = await aiService.generateArticleContent(
      article.articleTitle,
      primaryCategory.categoryName,
      userId
    );

    const generatedContent = result.content;

    // Get existing tags for AI selection
    const existingTags = await prisma.tag.findMany({
      select: {
        tagId: true,
        tagName: true,
        description: true
      },
      orderBy: { tagName: 'asc' }
    });

    // Generate tag suggestions using AI
    console.log('Generating tag suggestions...');
    const tagSelection = await aiService.selectAndCreateTags(
      article.articleTitle,
      primaryCategory.categoryName,
      existingTags,
      userId
    );

    // Create new tags first
    const createdTags = [];
    for (const newTag of tagSelection.new_tags) {
      try {
        const tag = await prisma.tag.create({
          data: {
            tagName: newTag.tagName,
            description: newTag.description || null,
            color: newTag.color || null,
          }
        });
        createdTags.push(tag);
      } catch (error) {
        // If tag already exists, find it
        const existingTag = await prisma.tag.findUnique({
          where: { tagName: newTag.tagName }
        });
        if (existingTag) {
          createdTags.push(existingTag);
        }
      }
    }

    // Collect all tag IDs to assign to the article
    const allTagIds = [
      ...tagSelection.existing_tags, // Selected existing tag IDs
      ...createdTags.map(tag => tag.tagId) // New tag IDs
    ];

    // Update the article with generated content and tags
    const updatedArticle = await prisma.article.update({
      where: { articleId },
      data: {
        contentHtml: generatedContent,
        isContentGenerated: true,
        updatedAt: new Date()
      },
      include: { 
        categories: {
          include: {
            category: true
          }
        },
        tags: {
          include: {
            tag: true
          }
        }
      }
    });

    // Create article-tag relationships
    for (const tagId of allTagIds) {
      await prisma.articleTag.create({
        data: {
          articleId: article.articleId,
          tagId: tagId
        }
      }).catch(() => {}); // Ignore duplicates
    }

    // Fetch the final article with tags to return
    const finalArticle = await prisma.article.findUnique({
      where: { articleId },
      include: { 
        categories: {
          include: {
            category: true
          }
        },
        tags: {
          include: {
            tag: true
          }
        }
      }
    });

    return NextResponse.json({
      message: "Content generated successfully",
      article: finalArticle,
      tagsCreated: createdTags.length,
      tagsAssigned: allTagIds.length
    });

  } catch (error) {
    console.error("Error generating content:", error);
    return NextResponse.json(
      { error: "Failed to generate content" },
      { status: 500 }
    );
  }
}

import { auth } from "@clerk/nextjs/server";
import { NextRequest, NextResponse } from "next/server";
import { prisma } from "@/lib/prisma";

export async function POST(
  request: NextRequest,
  context: { params: Promise<{ articleId: string }> }
) {
  try {
    const { userId } = await auth();
    const { articleId } = await context.params;

    if (!userId) {
      return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
    }

    // Check if article exists
    const article = await prisma.article.findUnique({
      where: { articleId }
    });

    if (!article) {
      return NextResponse.json({ error: "Article not found" }, { status: 404 });
    }

    // Ensure user exists in the database first
    const user = await prisma.user.findUnique({
      where: { clerkUserId: userId }
    });

    if (!user) {
      console.error(`User not found in database: ${userId}`);
      return NextResponse.json({ error: "User not found" }, { status: 404 });
    }

    // Upsert the article view - update viewedAt if user has already viewed this article
    const view = await prisma.userArticleView.upsert({
      where: {
        clerkUserId_articleId: {
          clerkUserId: userId,
          articleId: articleId
        }
      },
      update: {
        viewedAt: new Date()
      },
      create: {
        clerkUserId: userId,
        articleId: articleId
      }
    });

    console.log(`Article view tracked: User ${userId} viewed article ${articleId}`);

    return NextResponse.json({ success: true });
  } catch (error) {
    console.error("Article view tracking error:", error);
    return NextResponse.json(
      { error: "Failed to track article view" },
      { status: 500 }
    );
  }
}

import { NextResponse } from "next/server";
import { currentUser } from "@clerk/nextjs/server";
import { prisma } from "@/lib/prisma";
import { slugify } from "@/lib/utils";

export async function GET(request: Request) {
  try {
    const { searchParams } = new URL(request.url);
    const categoryId = searchParams.get('categoryId');
    const isContentGenerated = searchParams.get('isContentGenerated');
    const slug = searchParams.get('slug');

    const where: any = {};

    if (slug) {
      where.articleSlug = slug;
    }

    if (categoryId) {
      where.categories = {
        some: {
          categoryId: categoryId
        }
      };
    }

    if (isContentGenerated !== null) {
      where.isContentGenerated = isContentGenerated === 'true';
    }

    const articles = await prisma.article.findMany({
      where,
      include: {
        categories: {
          include: {
            category: true
          }
        },
        createdBy: true,
      },
      orderBy: { createdAt: 'desc' }
    });

    return NextResponse.json(articles);
  } catch (error) {
    console.error("Error fetching articles:", error);
    return NextResponse.json(
      { error: "Failed to fetch articles" },
      { status: 500 }
    );
  }
}

export async function POST(request: Request) {
  try {
    const user = await currentUser();
    const userId = user?.id;
    const body = await request.json();
    const { categoryIds, primaryCategoryId, articleTitle, contentHtml } = body;

    if (!categoryIds || categoryIds.length === 0 || !articleTitle) {
      return NextResponse.json(
        { error: "At least one category ID and article title are required" },
        { status: 400 }
      );
    }

    const articleSlug = slugify(articleTitle);

    const existingArticle = await prisma.article.findUnique({
      where: { articleSlug }
    });

    if (existingArticle) {
      return NextResponse.json(
        { error: "Article with this title already exists" },
        { status: 409 }
      );
    }

    const article = await prisma.article.create({
      data: {
        articleTitle,
        articleSlug,
        contentHtml,
        isContentGenerated: !!contentHtml,
        createdByClerkUserId: userId || null,
      }
    });

    // Create article-category relationships
    for (const categoryId of categoryIds) {
      await prisma.articleCategory.create({
        data: {
          articleId: article.articleId,
          categoryId: categoryId,
          isPrimary: categoryId === (primaryCategoryId || categoryIds[0])
        }
      });
    }

    // Fetch complete article with categories
    const completeArticle = await prisma.article.findUnique({
      where: { articleId: article.articleId },
      include: {
        categories: {
          include: {
            category: true
          }
        },
        createdBy: true,
      }
    });

    return NextResponse.json(completeArticle, { status: 201 });
  } catch (error) {
    console.error("Error creating article:", error);
    return NextResponse.json(
      { error: "Failed to create article" },
      { status: 500 }
    );
  }
}

import { NextResponse } from "next/server";
import { auth } from "@clerk/nextjs/server";
import { prisma } from "@/lib/prisma";
import { aiService, type MarkingResponse } from "@/lib/ai-service";

export async function POST(
  request: Request,
  { params }: { params: Promise<{ exampleId: string }> }
) {
  const { exampleId } = await params;
  try {
    const { userId } = await auth();
    if (!userId) {
      return NextResponse.json(
        { error: "Unauthorized" },
        { status: 401 }
      );
    }

    const body = await request.json();
    const { userAnswer } = body;

    if (!userAnswer || userAnswer.trim().length === 0) {
      return NextResponse.json(
        { error: "Answer is required" },
        { status: 400 }
      );
    }

    // Get the example with its correct answer
    const example = await prisma.interactiveExample.findUnique({
      where: { exampleId }
    });

    if (!example) {
      return NextResponse.json(
        { error: "Example not found" },
        { status: 404 }
      );
    }

    // For multiple choice, we'll use AI to determine correctness
    // The correct answer handling will be done by the AI marking system

    // Use AI service to mark the answer
    console.log('Marking answer with AI...');

    const markingResult = await aiService.markUserAnswer(
      example.scenarioOrQuestionText,
      userAnswer,
      example.questionType,
      example.aiMarkingPromptHint || undefined,
      userId
    );

