      hasStripeCustomer: !!user?.stripeCustomerId,
    });
  } catch (error) {
    console.error('Subscription status error:', error);
    return NextResponse.json(
      { error: 'Failed to get subscription status' },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { auth, currentUser } from '@clerk/nextjs/server';
import { prisma } from '@/lib/prisma';

export async function POST(req: NextRequest) {
  try {
    const { userId } = await auth();

    if (!userId) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    // Get current user from Clerk
    const clerkUser = await currentUser();

    if (!clerkUser) {
      return NextResponse.json({ error: 'User not found in Clerk' }, { status: 404 });
    }

    const email = clerkUser.emailAddresses[0]?.emailAddress;

    if (!email) {
      return NextResponse.json({ error: 'No email address found' }, { status: 400 });
    }

    // Upsert user in database
    const user = await prisma.user.upsert({
      where: { clerkUserId: userId },
      update: {
        email,
        username: clerkUser.username || null,
        firstName: clerkUser.firstName || null,
        lastName: clerkUser.lastName || null,
        imageUrl: clerkUser.imageUrl || null,
        lastLoginToApp: new Date(),
      },
      create: {
        clerkUserId: userId,
        email,
        username: clerkUser.username || null,
        firstName: clerkUser.firstName || null,
        lastName: clerkUser.lastName || null,
        imageUrl: clerkUser.imageUrl || null,
      },
    });

    console.log(`âœ… User ${userId} manually synced with email: ${email}`);

    return NextResponse.json({ 
      message: 'User synced successfully',
      user: {
        id: user.clerkUserId,
        email: user.email,
        subscriptionTier: user.subscriptionTier,
      }
    });
  } catch (error) {
    console.error('Manual sync error:', error);
    return NextResponse.json(
      { error: 'Failed to sync user' },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { headers } from 'next/headers';
import Stripe from 'stripe';
import { stripe } from '@/lib/stripe';
import { prisma } from '@/lib/prisma';
import { SubscriptionTier, SubscriptionStatus } from '@prisma/client';
import { emails } from '@/lib/email-service';

const webhookSecret = process.env.STRIPE_WEBHOOK_SECRET!;

async function getOrCreateUser(customerId: string) {
  if (!stripe) {
    throw new Error('Stripe is not initialized');
  }

  const customer = await stripe.customers.retrieve(customerId) as Stripe.Customer;

  if (!customer.metadata.clerkUserId) {
    throw new Error('Customer missing clerkUserId in metadata');
  }

  // Ensure user exists in database
  const user = await prisma.user.findUnique({
    where: { clerkUserId: customer.metadata.clerkUserId },
  });

  if (!user) {
    throw new Error(`User not found for clerkUserId: ${customer.metadata.clerkUserId}`);
  }

  // Update user with Stripe customer ID if not already set
  if (!user.stripeCustomerId) {
    await prisma.user.update({
      where: { clerkUserId: customer.metadata.clerkUserId },
      data: { stripeCustomerId: customerId },
    });
  }

  return user;
}

function mapPriceToTier(priceAmount: number): SubscriptionTier {
  const standardPrice = Number(process.env.STRIPE_STANDARD_PRICE_MONTHLY) || 8;
  const maxPrice = Number(process.env.STRIPE_MAX_PRICE_MONTHLY) || 14;

  // Convert from cents to dollars
  const amount = priceAmount / 100;

  if (amount === standardPrice) return 'STANDARD';
  if (amount === maxPrice) return 'MAX';

  // Default to standard if price doesn't match
  console.warn(`Unknown price amount: ${amount}, defaulting to STANDARD tier`);
  return 'STANDARD';
}

function mapStripeStatus(status: string): SubscriptionStatus {
  switch (status) {
    case 'active':
      return 'ACTIVE';
    case 'canceled':
      return 'CANCELLED';
    case 'past_due':
      return 'PAST_DUE';
    default:
      return 'INACTIVE';
  }
}

export async function POST(req: NextRequest) {
  const body = await req.text();
  const headersList = await headers();
  const signature = headersList.get('stripe-signature');

  if (!signature) {
    return NextResponse.json({ error: 'No signature' }, { status: 400 });
  }

  if (!stripe) {
    return NextResponse.json({ error: 'Stripe is not initialized' }, { status: 500 });
  }

  let event: Stripe.Event;

  try {
    event = stripe.webhooks.constructEvent(body, signature, webhookSecret);
  } catch (err) {
    console.error('Webhook signature verification failed:', err);
    return NextResponse.json(
      { error: 'Invalid signature' },
      { status: 400 }
    );
  }

  try {
    switch (event.type) {
      case 'customer.subscription.created':
      case 'customer.subscription.updated': {
        const subscription = event.data.object as Stripe.Subscription;
        console.log(`Processing ${event.type} for subscription ${subscription.id} with status ${subscription.status}`);

        const user = await getOrCreateUser(subscription.customer as string);

        const priceItem = subscription.items.data[0];
        const tier = mapPriceToTier(priceItem.price.unit_amount || 0);
        const status = mapStripeStatus(subscription.status);

        console.log(`Subscription details - Tier: ${tier}, Status: ${status}, Period End: ${subscription.current_period_end}`);

        // Update user subscription
        const periodEnd = subscription.current_period_end 
          ? new Date(subscription.current_period_end * 1000)
          : null;

        // Validate the date
        if (periodEnd && isNaN(periodEnd.getTime())) {
          console.error('Invalid subscription period end date:', subscription.current_period_end);
          throw new Error('Invalid subscription period end date');
        }

        const updatedUser = await prisma.user.update({
          where: { clerkUserId: user.clerkUserId },
          data: {
            subscriptionId: subscription.id,
            subscriptionTier: tier,
            subscriptionStatus: status,
            subscriptionCurrentPeriodEnd: periodEnd,
          },
        });

        // Create history record
        await prisma.subscriptionHistory.create({
          data: {
            clerkUserId: user.clerkUserId,
            subscriptionId: subscription.id,
            eventType: event.type.replace('customer.subscription.', ''),
            previousTier: user.subscriptionTier,
            newTier: tier,
            previousStatus: user.subscriptionStatus,
            newStatus: status,
            amount: priceItem.price.unit_amount,
            currency: priceItem.price.currency,
            stripeEventId: event.id,
            metadata: event.data.object,
          },
        });

        // Send subscription confirmation email for new active subscriptions
        if (event.type === 'customer.subscription.created' && status === 'ACTIVE') {
          try {
            await emails.sendSubscriptionConfirmation(
              user.email,
              user.firstName || "User",
              tier,
              priceItem.price.unit_amount || 0
            );
            console.log(`Subscription confirmation email sent to ${user.email}`);
          } catch (emailError) {
            console.error(`Failed to send subscription confirmation to ${user.email}:`, emailError);
          }
        }

        break;
      }

      case 'customer.subscription.deleted': {
        const subscription = event.data.object as Stripe.Subscription;
        const user = await getOrCreateUser(subscription.customer as string);

        // Update user subscription to cancelled/free
        const cancelPeriodEnd = subscription.current_period_end 
          ? new Date(subscription.current_period_end * 1000)
          : null;

        await prisma.user.update({
          where: { clerkUserId: user.clerkUserId },
          data: {
            subscriptionTier: 'FREE',
            subscriptionStatus: 'CANCELLED',
            subscriptionCurrentPeriodEnd: cancelPeriodEnd,
          },
        });

        // Create history record
        await prisma.subscriptionHistory.create({
          data: {
            clerkUserId: user.clerkUserId,
            subscriptionId: subscription.id,
            eventType: 'deleted',
            previousTier: user.subscriptionTier,
            newTier: 'FREE',
            previousStatus: user.subscriptionStatus,
            newStatus: 'CANCELLED',
            stripeEventId: event.id,
            metadata: event.data.object,
          },
        });

        break;
      }

      case 'invoice.payment_succeeded': {
        const invoice = event.data.object as Stripe.Invoice;

        if (invoice.subscription && stripe) {
          const subscription = await stripe.subscriptions.retrieve(
            invoice.subscription as string
          );
          const user = await getOrCreateUser(subscription.customer as string);

          // Update subscription end date
          const renewalPeriodEnd = subscription.current_period_end 
            ? new Date(subscription.current_period_end * 1000)
            : null;

          await prisma.user.update({
            where: { clerkUserId: user.clerkUserId },
            data: {
              subscriptionCurrentPeriodEnd: renewalPeriodEnd,
            },
          });
        }

        break;
      }

      case 'invoice.payment_failed': {
        const invoice = event.data.object as Stripe.Invoice;

        if (invoice.subscription && stripe) {
          const subscription = await stripe.subscriptions.retrieve(
            invoice.subscription as string
          );
          const user = await getOrCreateUser(subscription.customer as string);

          // Update subscription status to past due
          await prisma.user.update({
            where: { clerkUserId: user.clerkUserId },
            data: {
              subscriptionStatus: 'PAST_DUE',
            },
          });

          // Create history record
          await prisma.subscriptionHistory.create({
            data: {
              clerkUserId: user.clerkUserId,
              subscriptionId: subscription.id,
              eventType: 'payment_failed',
              previousTier: user.subscriptionTier,
              newTier: user.subscriptionTier,
              previousStatus: user.subscriptionStatus,
              newStatus: 'PAST_DUE',
              stripeEventId: event.id,
              metadata: event.data.object,
            },
          });
        }

        break;
      }
    }

    return NextResponse.json({ received: true });
  } catch (error) {
    console.error('Webhook handler error:', error);
    return NextResponse.json(
      { error: 'Webhook handler failed' },
      { status: 500 }
    );
  }
}

"use client";

import { useState, useEffect } from "react";
import { useUser } from "@clerk/nextjs";
import { useRouter } from "next/navigation";
import { useSubscription } from "@/hooks/use-subscription";
import { Loader2, BookOpen, Sparkles, CreditCard, MoreVertical, BookmarkPlus, Check, Plus, X, Flag } from "lucide-react";
import Link from "next/link";
import InteractiveExamples from "./interactive-examples";
import MarkdownViewer from "@/components/markdown-viewer";
import CommentsList from "@/components/comments/comments-list";
import LikeButton from "@/components/like-button";
import { ArticleSuggestionFormInline } from "@/components/article-suggestion-form-inline";
import { ArticleChangeHistory } from "@/components/article-change-history";
import { FloatingActionMenu } from "@/components/floating-action-menu";

interface Article {
  articleId: string;
  articleTitle: string;
  articleSlug: string;
  contentHtml: string | null;
  isContentGenerated: boolean;
  isFlagged: boolean;
  categories: Array<{
    category: {
      categoryId: string;
      categoryName: string;
    };
  }>;
  stream: {
    streamId: string;
    streamName: string;
    channel: {
      channelId: string;
      channelName: string;
    };
  } | null;
  createdBy: {
    username: string | null;
  } | null;
  tags: Array<{
    tag: {
      tagId: string;
      tagName: string;
      description: string | null;
      color: string | null;
    };
  }>;
}

interface ArticleContentProps {
  article: Article;
}

export default function ArticleContent({ article: initialArticle }: ArticleContentProps) {
  const [article, setArticle] = useState(initialArticle);
  const [generating, setGenerating] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [subscriptionError, setSubscriptionError] = useState(false);
  const [showSuggestionModal, setShowSuggestionModal] = useState(false);
  const [focusedExampleId, setFocusedExampleId] = useState<string | null>(null);
  const { isSignedIn } = useUser();

  useEffect(() => {
    if (!article.isContentGenerated && !article.contentHtml && isSignedIn) {
      generateContent();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [article.isContentGenerated, article.contentHtml, isSignedIn]);

  useEffect(() => {
    // Track article view when component mounts and user is signed in
    if (isSignedIn && article.articleId) {
      trackArticleView();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [isSignedIn, article.articleId]);

  const trackArticleView = async () => {
    try {
      const response = await fetch(`/api/articles/${article.articleId}/view`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
      });

      if (!response.ok) {
        const data = await response.json();
        console.error("Failed to track article view:", response.status, data);
      } else {
        console.log("Article view tracked successfully");
      }
    } catch (err) {
      console.error("Failed to track article view:", err);
    }
  };

  const generateContent = async () => {
    setGenerating(true);
    setError(null);

    try {
      const response = await fetch(`/api/articles/${article.articleId}/generate`, {
        method: "POST",
      });

      if (!response.ok) {
        const data = await response.json();
        if (response.status === 403 && data.error === "Subscription required") {
          setSubscriptionError(true);
          throw new Error(data.message || "Subscription required");
        }
        throw new Error(data.error || "Failed to generate content");
      }

      const data = await response.json();
      setArticle(data.article);
    } catch (err) {
      setError(err instanceof Error ? err.message : "An error occurred");
    } finally {
      setGenerating(false);
    }
  };

  return (
    <>
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      {/* Breadcrumb */}
      <nav className="mb-8">
        <ol className="flex items-center space-x-2 text-sm text-gray-600">
          <li>
            <Link href="/" className="hover:text-gray-900">
              Home
            </Link>
          </li>
          <li>/</li>
          {article.stream ? (
            <>
              <li>
                <Link href="/channels" className="hover:text-gray-900">
                  Channels
                </Link>
              </li>
              <li>/</li>
              <li>
                <Link 
                  href={`/channels#${article.stream.channel.channelId}`} 
                  className="hover:text-gray-900"
                >
                  {article.stream.channel.channelName}
                </Link>
              </li>
              <li>/</li>
              <li>
                <Link 
                  href={`/search/v2?channel=${article.stream.channel.channelId}&stream=${article.stream.streamId}`} 
                  className="hover:text-gray-900"
                >
                  {article.stream.streamName}
                </Link>
              </li>
            </>
          ) : article.categories && article.categories.length > 0 ? (
            <>
              <li>
                <Link href="/categories" className="hover:text-gray-900">
                  Categories
                </Link>
              </li>
              <li>/</li>
              <li>
                <Link 
                  href={`/categories/${article.categories[0].category.categoryId}`} 
                  className="hover:text-gray-900"
                >
                  {article.categories[0].category.categoryName}
                </Link>
              </li>
            </>
          ) : null}
          <li>/</li>
          <li className="text-gray-900 font-medium">{article.articleTitle}</li>
        </ol>
      </nav>

      {/* Article Header */}
      <header className="mb-8">
        <h1 className="text-4xl font-bold text-gray-900 mb-4">
          {article.articleTitle}
        </h1>
        <div className="flex items-center justify-between">
          <div className="flex items-center text-gray-600">
            <BookOpen className="h-5 w-5 mr-2" />
            <span>
              {article.stream 
                ? `${article.stream.channel.channelName} / ${article.stream.streamName}`
                : article.categories && article.categories.length > 0 
                  ? (
                    <div className="flex flex-wrap gap-2">
                      {article.categories.map((c, index) => (
                        <span key={c.category.categoryId}>
                          <Link 
                            href={`/categories/${c.category.categoryId}`}
                            className="text-blue-600 hover:text-blue-800 hover:underline"
                          >
                            {c.category.categoryName}
                          </Link>
                          {index < article.categories.length - 1 && <span className="text-gray-600">,</span>}
                        </span>
                      ))}
                    </div>
                  )
                  : 'Uncategorized'
              }
            </span>
          </div>
          <div className="flex items-center gap-2">
            <LikeButton articleId={article.articleId} iconOnly />
            {article.contentHtml && (
              <MoreOptionsDropdown
                articleId={article.articleId}
                articleTitle={article.articleTitle}
                isFlagged={article.isFlagged}
                onShowSuggestion={() => setShowSuggestionModal(true)}
