interface ListManagementModalProps {
  articleId: string;
  articleTitle: string;
  isOpen: boolean;
  onClose: () => void;
}

function ListManagementModal({ articleId, articleTitle, isOpen, onClose }: ListManagementModalProps) {
  const [lists, setLists] = useState<any[]>([]);
  const [articleLists, setArticleLists] = useState<Set<string>>(new Set());
  const [isLoading, setIsLoading] = useState(false);
  const [showNewListForm, setShowNewListForm] = useState(false);
  const [newListName, setNewListName] = useState("");
  const { isSignedIn } = useUser();

  useEffect(() => {
    if (isOpen && isSignedIn) {
      fetchLists();
    }
  }, [isOpen, isSignedIn]);

  const fetchLists = async () => {
    setIsLoading(true);
    try {
      const [listsResponse, articleListsResponse] = await Promise.all([
        fetch("/api/lists"),
        fetch(`/api/articles/${articleId}/lists`)
      ]);

      if (listsResponse.ok) {
        const data = await listsResponse.json();
        setLists(data);
      }

      if (articleListsResponse.ok) {
        const data = await articleListsResponse.json();
        setArticleLists(new Set(data.listIds || []));
      }
    } catch (error) {
      console.error("Error fetching lists:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleToggleList = async (listId: string) => {
    try {
      const isInList = articleLists.has(listId);
      const url = isInList 
        ? `/api/lists/${listId}/items?articleId=${articleId}`
        : `/api/lists/${listId}/items`;

      const response = await fetch(url, {
        method: isInList ? "DELETE" : "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: isInList ? undefined : JSON.stringify({ articleId }),
      });

      if (response.ok) {
        if (isInList) {
          setArticleLists(prev => {
            const newSet = new Set(prev);
            newSet.delete(listId);
            return newSet;
          });
        } else {
          setArticleLists(prev => new Set([...prev, listId]));
        }
      }
    } catch (error) {
      console.error("Error toggling list:", error);
    }
  };

  const handleCreateNewList = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!newListName.trim()) return;

    try {
      const response = await fetch("/api/lists", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ listName: newListName }),
      });

      if (response.ok) {
        const newList = await response.json();
        await handleToggleList(newList.listId);
        setNewListName("");
        setShowNewListForm(false);
        fetchLists();
      }
    } catch (error) {
      console.error("Error creating list:", error);
    }
  };

  if (!isSignedIn) {
    return null;
  }

  return (
    <>
      {isOpen && (
        <div 
          className="fixed inset-0 flex items-center justify-center z-50 p-4" 
          style={{ backgroundColor: 'rgba(0, 0, 0, 0.3)' }}
        >
          <div className="bg-white rounded-lg max-w-md w-full max-h-[80vh] overflow-hidden flex flex-col">
            <div className="p-4 border-b">
              <h2 className="text-xl font-semibold">Manage Lists</h2>
              <p className="text-sm text-gray-600 mt-1">Add &ldquo;{articleTitle}&rdquo; to your lists</p>
            </div>

            <div className="flex-1 overflow-y-auto p-4">
              {isLoading ? (
                <div className="flex items-center justify-center py-8">
                  <Loader2 className="h-6 w-6 animate-spin text-gray-500" />
                </div>
              ) : (
                <>
                  <div className="space-y-1">
                    {lists.map((list) => (
                      <button
                        key={list.listId}
                        onClick={() => handleToggleList(list.listId)}
                        className="w-full text-left p-2 rounded-lg border hover:bg-gray-50 flex items-center justify-between group"
                      >
                        <div>
                          <p className="font-medium text-sm">{list.listName}</p>
                          {list.description && (
                            <p className="text-xs text-gray-600">{list.description}</p>
                          )}
                        </div>
                        <div className="flex items-center gap-2">
                          {articleLists.has(list.listId) ? (
                            <>
                              <Check className="h-4 w-4 text-green-600" />
                              <X className="h-4 w-4 text-red-600 opacity-0 group-hover:opacity-100 transition-opacity" />
                            </>
                          ) : (
                            <Plus className="h-4 w-4 text-gray-400 group-hover:text-gray-600" />
                          )}
                        </div>
                      </button>
                    ))}
                  </div>

                  {!showNewListForm ? (
                    <button
                      onClick={() => setShowNewListForm(true)}
                      className="w-full mt-3 p-2 rounded-lg border-2 border-dashed border-gray-300 hover:border-gray-400 flex items-center justify-center text-gray-600 hover:text-gray-700"
                    >
                      <Plus className="h-4 w-4 mr-2" />
                      Create new list
                    </button>
                  ) : (
                    <form onSubmit={handleCreateNewList} className="mt-3 p-2 border rounded-lg">
                      <input
                        type="text"
                        value={newListName}
                        onChange={(e) => setNewListName(e.target.value)}
                        placeholder="List name"
                        className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
                        autoFocus
                      />
                      <div className="flex gap-2 mt-2">
                        <button
                          type="submit"
                          className="flex-1 px-3 py-1.5 bg-blue-600 text-white rounded-md hover:bg-blue-700 text-sm"
                        >
                          Create
                        </button>
                        <button
                          type="button"
                          onClick={() => {
                            setShowNewListForm(false);
                            setNewListName("");
                          }}
                          className="flex-1 px-3 py-1.5 bg-gray-200 text-gray-700 rounded-md hover:bg-gray-300 text-sm"
                        >
                          Cancel
                        </button>
                      </div>
                    </form>
                  )}
                </>
              )}
            </div>

            <div className="p-4 border-t space-y-2">
              <Link
                href="/dashboard/lists"
                onClick={onClose}
                className="block w-full px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 text-center text-sm"
              >
                Manage All Lists
              </Link>
              <button
                onClick={onClose}
                className="w-full px-4 py-2 bg-gray-200 text-gray-700 rounded-md hover:bg-gray-300 text-sm"
              >
                Done
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

interface SuggestionModalProps {
  articleId: string;
  isOpen: boolean;
  onClose: () => void;
}

interface FlagModalProps {
  articleId: string;
  isFlagged: boolean;
  isOpen: boolean;
  onClose: () => void;
}

function FlagModal({ articleId, isOpen, onClose }: FlagModalProps) {
  const { isSignedIn } = useUser();
  const [loading, setLoading] = useState(false);
  const [flagReason, setFlagReason] = useState("");

  async function submitFlag() {
    try {
      setLoading(true);
      const response = await fetch(`/api/articles/${articleId}/flag`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ flagReason }),
      });

      if (!response.ok) throw new Error("Failed to flag content");

      onClose();
      setFlagReason("");
      // Refresh the page to show the flagged status
      window.location.reload();
    } catch (error) {
      console.error("Error flagging content:", error);
      alert("Failed to flag content");
    } finally {
      setLoading(false);
    }
  }

  if (!isOpen || !isSignedIn) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg max-w-md w-full p-6">
        <h3 className="text-lg font-semibold mb-4">Flag Content</h3>
        <p className="text-gray-600 mb-4">
          Please provide a reason for flagging this content:
        </p>
        <textarea
          value={flagReason}
          onChange={(e) => setFlagReason(e.target.value)}
          placeholder="Explain why this content is inappropriate..."
          className="w-full rounded-md border border-gray-300 px-3 py-2 mb-4"
          rows={4}
          required
        />
        <div className="flex justify-end gap-2">
          <button
            onClick={() => {
              onClose();
              setFlagReason("");
            }}
            className="px-4 py-2 text-gray-700 hover:text-gray-900"
          >
            Cancel
          </button>
          <button
            onClick={submitFlag}
            disabled={loading || !flagReason.trim()}
            className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 disabled:opacity-50"
          >
            {loading ? "Submitting..." : "Submit Flag"}
          </button>
        </div>
      </div>
    </div>
  );
}

function SuggestionModal({ articleId, isOpen, onClose }: SuggestionModalProps) {
  const { user } = useUser();
  const { isSubscribed, isLoadingSubscription } = useSubscription();
  const [suggestionType, setSuggestionType] = useState('');
  const [suggestionDetails, setSuggestionDetails] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [result, setResult] = useState<{ success: boolean; message: string } | null>(null);
  const [hasFreshResponse, setHasFreshResponse] = useState(false);

  const suggestionTypes = [
    { value: 'CONTENT_ADDITION', label: 'Add missing content' },
    { value: 'CONTENT_CORRECTION', label: 'Correct an error' },
    { value: 'GRAMMAR_SPELLING', label: 'Fix grammar/spelling' },
    { value: 'CODE_IMPROVEMENT', label: 'Improve code example' },
    { value: 'CLARITY_IMPROVEMENT', label: 'Improve clarity' },
    { value: 'EXAMPLE_ADDITION', label: 'Add an example' },
    { value: 'LINK_UPDATE', label: 'Update a link' },
    { value: 'OTHER', label: 'Other improvement' },
  ];

  const fullReset = () => {
    setSuggestionType('');
    setSuggestionDetails('');
    setResult(null);
    setHasFreshResponse(false);
    setIsSubmitting(false);
  };

  const handleClose = () => {
    onClose();
    fullReset();
  };

  const handleSubmit = async () => {
    setIsSubmitting(true);

    if (!suggestionType || !suggestionDetails.trim()) {
      setResult({ success: false, message: 'Please select a suggestion type and provide details.' });
      setHasFreshResponse(true);
      setIsSubmitting(false);
      return;
    }
    setResult(null);

    try {
      const response = await fetch(`/api/articles/${articleId}/suggest`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ suggestionType, suggestionDetails }),
      });

      const data = await response.json();

      if (response.ok) {
        if (data.suggestion && typeof data.suggestion.isApproved === 'boolean') {
          setResult({
            success: data.suggestion.isApproved,
            message: data.suggestion.isApproved 
              ? `Great! Your suggestion has been approved and applied. You now have ${data.approvedSuggestionsCount} approved suggestions! Please refresh your browser to see the changes.`
              : data.message || data.suggestion.rejectionReason || 'Your suggestion was not approved at this time. It has been saved for review.',
          });
        } else {
          setResult({ success: false, message: data.message || 'Received an unexpected response from the server.' });
        }
      } else {
        setResult({ success: false, message: data.message || data.error || `Request failed with status ${response.status}.` });
      }
      setHasFreshResponse(true);

    } catch (err) {
      setResult({
        success: false,
        message: err instanceof Error ? err.message : 'Failed to submit suggestion',
      });
      setHasFreshResponse(true);
    } finally {
      setIsSubmitting(false);
    }
  };

  if (!isOpen || !user) return null;

  return (
    <div 
      className="fixed inset-0 flex items-center justify-center p-4"
      style={{ 
        backgroundColor: 'rgba(0, 0, 0, 0.3)', 
        zIndex: 9999
      }}
      onClick={(e) => {
        if (e.target === e.currentTarget) {
          handleClose();
        }
      }}
    >
      <div 
        className="bg-white rounded-lg max-w-md w-full max-h-[80vh] overflow-hidden flex flex-col shadow-xl"
        onClick={(e) => e.stopPropagation()}
      >
        <div className="p-6 border-b flex justify-between items-center">
          <div>
            <h2 className="text-xl font-semibold">Suggest an Improvement</h2>
            <p className="text-sm text-gray-600 mt-1">
              Your feedback helps us improve our content.
            </p>
          </div>
          <button
            onClick={handleClose}
            className="text-gray-400 hover:text-gray-600"
          >
            <X className="h-6 w-6" />
          </button>
        </div>

        <div className="flex-1 overflow-y-auto p-6">
          {!isSubscribed && !isLoadingSubscription ? (
            <div className="text-center">
              <CreditCard className="mx-auto h-12 w-12 text-blue-600 mb-4" />
              <h3 className="text-lg font-medium text-gray-900 mb-2">
                Subscription Required
              </h3>
              <p className="text-gray-600 mb-4">
                Article suggestions are available to subscribed users. Upgrade your plan to suggest improvements.
              </p>
              <div className="flex gap-2">
                <button
                  onClick={handleClose}
                  className="flex-1 px-4 py-2 bg-gray-200 text-gray-700 rounded-md hover:bg-gray-300"
                >
                  Cancel
                </button>
                <Link href="/pricing" className="flex-1">
                  <button className="w-full px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700">
                    View Plans
                  </button>
                </Link>
              </div>
            </div>
          ) : hasFreshResponse && result ? (
            <div>
              <div className={`p-3 rounded-md text-sm mb-4 ${result.success ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'}`}>
                {result.message}
              </div>
              <button
                onClick={handleClose}
                className="w-full px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
              >
                OK
              </button>
            </div>
          ) : (
            <div className="space-y-4">
              <div>
                <label htmlFor="suggestion-type" className="block text-sm font-medium text-gray-700 mb-2">
                  Type
                </label>
                <select
                  id="suggestion-type"
                  value={suggestionType}
                  onChange={(e) => setSuggestionType(e.target.value)}
                  disabled={isSubmitting}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="">Select improvement type</option>
                  {suggestionTypes.map((type) => (
                    <option key={type.value} value={type.value}>
                      {type.label}
                    </option>
                  ))}
                </select>
              </div>

              <div>
                <label htmlFor="suggestion-details" className="block text-sm font-medium text-gray-700 mb-2">
                  Details
                </label>
                <textarea
                  id="suggestion-details"
                  placeholder="Please provide specific details about your suggestion..."
                  value={suggestionDetails}
                  onChange={(e) => setSuggestionDetails(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 min-h-[120px]"
                  disabled={isSubmitting}
                />
              </div>

              <div className="flex gap-2">
                <button
                  onClick={handleClose}
                  disabled={isSubmitting}
                  className="flex-1 px-4 py-2 bg-gray-200 text-gray-700 rounded-md hover:bg-gray-300 disabled:opacity-50"
                >
                  Cancel
                </button>
                <button
                  onClick={handleSubmit}
                  disabled={isSubmitting}
                  className="flex-1 px-4 py-2 bg-orange-600 text-white rounded-md hover:bg-orange-700 disabled:bg-gray-400"
                >
                  {isSubmitting ? 'Submitting...' : 'Submit Suggestion'}
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

"use client";

import { useState, useEffect, useCallback } from "react";
import { Loader2, RefreshCw, CheckCircle, XCircle, Lightbulb, MessageCircle } from "lucide-react";

interface Option {
  id: string;
  text: string;
}

interface Example {
  exampleId: string;
  questionType: "MULTIPLE_CHOICE" | "TEXT_INPUT" | "COMMAND_LINE";
  scenarioOrQuestionText: string;
  optionsJson: any;
  correctAnswerDescription: string;
}

interface InteractiveExamplesProps {
  articleId: string;
  onFocusedExampleChange?: (exampleId: string | null) => void;
}

export default function InteractiveExamples({ articleId, onFocusedExampleChange }: InteractiveExamplesProps) {
  const [examples, setExamples] = useState<Example[]>([]);
  const [loading, setLoading] = useState(true);
  const [generating, setGenerating] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [answers, setAnswers] = useState<Record<string, string>>({});
  const [submissions, setSubmissions] = useState<Record<string, {
    isCorrect: boolean;
    feedback: string;
    correctAnswerDescription: string;
  }>>({});
  const [submitting, setSubmitting] = useState<string | null>(null);
  const [focusedExampleId, setFocusedExampleId] = useState<string | undefined>(undefined);

  // Notify parent of focused example changes
  useEffect(() => {
    onFocusedExampleChange?.(focusedExampleId || null);
  }, [focusedExampleId, onFocusedExampleChange]);

  const fetchExamples = useCallback(async () => {
    try {
      const response = await fetch(`/api/articles/${articleId}/examples`);
      if (!response.ok) throw new Error("Failed to fetch examples");
      const data = await response.json();
      console.log("Fetched examples:", data);
      setExamples(data);
    } catch (err) {
      setError(err instanceof Error ? err.message : "An error occurred");
    } finally {
      setLoading(false);
    }
  }, [articleId]);

  useEffect(() => {
    fetchExamples();
  }, [fetchExamples]);

  const generateExamples = async () => {
    setGenerating(true);
    setError(null);

    try {
      const response = await fetch(`/api/articles/${articleId}/examples`, {
        method: "POST",
      });
      if (!response.ok) throw new Error("Failed to generate examples");
      const data = await response.json();
      setExamples([...examples, ...data.examples]);
    } catch (err) {
      setError(err instanceof Error ? err.message : "An error occurred");
    } finally {
      setGenerating(false);
    }
  };

  const submitAnswer = async (exampleId: string) => {
    const userAnswer = answers[exampleId];
    if (!userAnswer || userAnswer.trim().length === 0) return;

    setSubmitting(exampleId);
    setError(null);

    try {
      const response = await fetch(`/api/examples/${exampleId}/submit`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ userAnswer }),
      });

      if (!response.ok) throw new Error("Failed to submit answer");

      const data = await response.json();
      setSubmissions({
        ...submissions,
        [exampleId]: {
          isCorrect: data.isCorrect,
          feedback: data.feedback,
          correctAnswerDescription: data.correctAnswerDescription,
        },
      });
    } catch (err) {
      setError(err instanceof Error ? err.message : "An error occurred");
    } finally {
      setSubmitting(null);
    }
  };

  const renderExample = (example: Example) => {
    const submitted = submissions[example.exampleId];
    const isSubmitting = submitting === example.exampleId;

    return (
      <div key={example.exampleId} className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
        <div className="mb-4">
          <div className="flex items-start justify-between mb-2">
            <h3 className="text-lg font-semibold text-gray-900">
              {example.questionType === "MULTIPLE_CHOICE" && "Multiple Choice"}
              {example.questionType === "TEXT_INPUT" && "Text Answer"}
              {example.questionType === "COMMAND_LINE" && "Command Line"}
            </h3>
            <div className="flex items-center gap-2">
              {submitted && (
                <div className="flex items-center">
                  {submitted.isCorrect ? (
                    <CheckCircle className="h-5 w-5 text-green-600" />
                  ) : (
                    <XCircle className="h-5 w-5 text-red-600" />
                  )}
                </div>
              )}
              <button
                onClick={() => setFocusedExampleId(focusedExampleId === example.exampleId ? undefined : example.exampleId)}
                className="text-gray-500 hover:text-gray-700 transition-colors"
                title="Ask AI about this question"
              >
                <MessageCircle className="h-5 w-5" />
              </button>
            </div>
          </div>
          <p className="text-gray-700">{example.scenarioOrQuestionText}</p>
        </div>

        {example.questionType === "MULTIPLE_CHOICE" && example.optionsJson && (
          <div className="space-y-2 mb-4">
            {(() => {
              // Parse optionsJson if it's a string or ensure it's properly formatted
              let options: Option[] = [];
              try {
                if (typeof example.optionsJson === 'string') {
                  options = JSON.parse(example.optionsJson);
                } else if (Array.isArray(example.optionsJson)) {
                  options = example.optionsJson;
                } else {
                  console.error('Invalid optionsJson format:', example.optionsJson);
                  return null;
                }
              } catch (error) {
                console.error('Error parsing optionsJson:', error);
                return null;
              }

              return options.map((option) => (
                <label
                  key={option.id}
                  className="flex items-center space-x-3 p-3 rounded-lg border border-gray-200 hover:bg-gray-50 cursor-pointer"
                >
                  <input
                    type="radio"
                    name={`question-${example.exampleId}`}
                    value={option.text}
                    onChange={(e) => setAnswers({ ...answers, [example.exampleId]: e.target.value })}
                    disabled={!!submitted}
                    checked={answers[example.exampleId] === option.text}
                    className="h-4 w-4 text-blue-600"
                  />
                  <span className="text-gray-700">{option.text}</span>
                </label>
              ));
            })()}
          </div>
        )}

        {(example.questionType === "TEXT_INPUT" || example.questionType === "COMMAND_LINE") && (
          <div className="mb-4">
            <input
              type="text"
              value={answers[example.exampleId] || ""}
              onChange={(e) => setAnswers({ ...answers, [example.exampleId]: e.target.value })}
              disabled={!!submitted}
              placeholder={example.questionType === "COMMAND_LINE" ? "Enter command..." : "Enter your answer..."}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 font-mono"
            />
          </div>
        )}

        {!submitted && (
          <button
            onClick={() => submitAnswer(example.exampleId)}
            disabled={!answers[example.exampleId] || isSubmitting}
            className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-blue-600 hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {isSubmitting ? (
              <>
                <Loader2 className="animate-spin h-4 w-4 mr-2" />
                Checking...
              </>
            ) : (
              "Submit Answer"
            )}
          </button>
        )}

        {submitted && (
          <div className="mt-4 space-y-3">
            <div className={`p-4 rounded-lg ${submitted.isCorrect ? "bg-green-50" : "bg-red-50"}`}>
              <p className={`text-sm ${submitted.isCorrect ? "text-green-800" : "text-red-800"}`}>
                {submitted.feedback}
              </p>
            </div>
            <div className="p-4 bg-blue-50 rounded-lg">
              <div className="flex items-start">
                <Lightbulb className="h-5 w-5 text-blue-600 mr-2 flex-shrink-0 mt-0.5" />
                <div>
                  <p className="text-sm font-medium text-blue-900 mb-1">Explanation:</p>
                  <p className="text-sm text-blue-800">{submitted.correctAnswerDescription}</p>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    );
  };

  if (loading) {
    return (
      <div className="flex justify-center py-8">
        <Loader2 className="h-8 w-8 animate-spin text-blue-600" />
      </div>
    );
  }

  return (
    <>
      <section className="border-t pt-12">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-2xl font-bold text-gray-900">Test Your Knowledge</h2>
          <button
            onClick={generateExamples}
            disabled={generating}
            className="inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 disabled:opacity-50"
          >
            {generating ? (
              <>
                <Loader2 className="animate-spin h-4 w-4 mr-2" />
                Generating...
              </>
            ) : (
              <>
                <RefreshCw className="h-4 w-4 mr-2" />
                Generate More Examples
              </>
            )}
          </button>
        </div>

      {error && (
        <div className="mb-6 bg-red-50 border border-red-200 rounded-lg p-4">
          <p className="text-red-800">Error: {error}</p>
        </div>
      )}

      {examples.length === 0 ? (
        <div className="text-center py-12 bg-gray-50 rounded-lg">
          <p className="text-gray-600 mb-4">No examples yet. Generate some to test your knowledge!</p>
          <button
            onClick={generateExamples}
            disabled={generating}
            className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-blue-600 hover:bg-blue-700"
          >
            Generate Examples
          </button>
        </div>
      ) : (
        <div className="space-y-6">
          {examples.map(renderExample)}
        </div>
      )}
    </section>
    </>
  );
}

