    if (!confirm("Remove this article from the list?")) return;

    try {
      const response = await fetch(`/api/lists/${listId}/items?articleId=${articleId}`, {
        method: "DELETE",
      });

      if (response.ok) {
        fetchList();
      }
    } catch (error) {
      console.error("Error removing item:", error);
