              <span className="flex items-center gap-1">
                {list.isPublic ? <Globe className="h-4 w-4" /> : <Lock className="h-4 w-4" />}
                {list.isPublic ? "Public" : "Private"}
              </span>
              <span>{list.items.length} articles</span>
              {list.user.username && (
                <span>by {list.user.username}</span>
