import { auth } from "@clerk/nextjs/server";
import { redirect } from "next/navigation";
import { Clock, Calendar, Flame, TrendingUp, ArrowLeft } from "lucide-react";
import Link from "next/link";
import { prisma } from "@/lib/prisma";

interface StreakData {
  currentStreak: number;
  longestStreak: number;
  activeDays: Set<string>;
  totalActiveDays: number;
  streakHistory: Array<{
    date: string;
    hasActivity: boolean;
    questionsAnswered: number;
  }>;
}

async function getUserStreakData(userId: string): Promise<StreakData> {
  try {
    const userResponses = await prisma.userResponse.findMany({
      where: { clerkUserId: userId },
      orderBy: { submittedAt: 'desc' }
    });

    // Get last 30 days
    const today = new Date();
    const thirtyDaysAgo = new Date(today);
    thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 29);

    // Create array of last 30 days
    const days = [];
    for (let i = 29; i >= 0; i--) {
      const date = new Date(today);
      date.setDate(date.getDate() - i);
      days.push({
        date: date.toDateString(),
        hasActivity: false,
        questionsAnswered: 0
      });
    }

    // Count activity per day
    const dailyActivity = new Map<string, number>();
    userResponses.forEach(response => {
      const dateStr = response.submittedAt.toDateString();
      dailyActivity.set(dateStr, (dailyActivity.get(dateStr) || 0) + 1);
    });

    // Mark days with activity
    days.forEach(day => {
      const count = dailyActivity.get(day.date) || 0;
      day.hasActivity = count > 0;
      day.questionsAnswered = count;
    });

    // Calculate current streak (from today backwards)
    let currentStreak = 0;
    for (let i = days.length - 1; i >= 0; i--) {
      if (days[i].hasActivity) {
        currentStreak++;
      } else {
        break;
      }
    }

    // Calculate longest streak in the 30-day period
    let longestStreak = 0;
    let tempStreak = 0;
    days.forEach(day => {
      if (day.hasActivity) {
        tempStreak++;
        longestStreak = Math.max(longestStreak, tempStreak);
      } else {
        tempStreak = 0;
      }
    });

    // Get all unique active days
    const activeDays = new Set<string>();
    userResponses.forEach(response => {
      activeDays.add(response.submittedAt.toDateString());
    });

    return {
      currentStreak,
      longestStreak,
      activeDays,
      totalActiveDays: activeDays.size,
      streakHistory: days
    };

  } catch (error) {
    console.error('Error fetching streak data:', error);
    return {
      currentStreak: 0,
      longestStreak: 0,
      activeDays: new Set(),
      totalActiveDays: 0,
      streakHistory: []
    };
  }
}

