"use client";

import { useState, useEffect } from "react";
import { useRouter, useSearchParams } from "next/navigation";
import Link from "next/link";
import { Search, Loader2, Folder, FileText, Sparkles } from "lucide-react";

interface SearchResults {
  query: string;
  categories: Array<{
    categoryId: string;
    categoryName: string;
    description: string | null;
  }>;
  articles: Array<{
    articleId: string;
    articleTitle: string;
    articleSlug: string;
    isContentGenerated: boolean;
    categories: Array<{
      category: {
        categoryName: string;
      };
    }>;
    tags: Array<{
      tag: {
        tagId: string;
        tagName: string;
        description: string | null;
        color: string | null;
      };
    }>;
  }>;
  aiSuggestions: {
    newCategoriesAdded: number;
    newArticlesAdded: number;
  };
  pagination?: {
    page: number;
    limit: number;
    total: number;
    totalPages: number;
    hasNextPage: boolean;
    hasPreviousPage: boolean;
  };
}

export default function SearchPage() {
  const router = useRouter();
  const searchParams = useSearchParams();
  const [query, setQuery] = useState("");
  const [results, setResults] = useState<SearchResults | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [isRestoringFromURL, setIsRestoringFromURL] = useState(false);

  // Initialize state from URL parameters on component mount
  useEffect(() => {
    const urlQuery = searchParams.get('q');
    const urlPage = searchParams.get('page');

    if (urlQuery) {
      setQuery(urlQuery);
      const pageNum = urlPage ? parseInt(urlPage, 10) : 1;
      setCurrentPage(pageNum);

      // Show loading state immediately when restoring from URL
      setIsRestoringFromURL(true);
      setLoading(true);
      setError(null);

      // Perform search if we have a query from URL
      performSearch(urlQuery, pageNum);
    }
  }, [searchParams]);

  const performSearch = async (searchQuery: string, page: number = 1) => {
    if (!searchQuery.trim()) return;

    setLoading(true);
    setError(null);

    try {
      const response = await fetch("/api/search", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ query: searchQuery, page, limit: 20 }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "Search failed");
      }

      const data = await response.json();
      console.log("Search results:", data);
      setResults(data);
    } catch (err) {
      console.error("Search error:", err);
      setError(err instanceof Error ? err.message : "An error occurred");
    } finally {
      setLoading(false);
      setIsRestoringFromURL(false);
    }
  };

  const updateURL = (searchQuery: string, page: number = 1) => {
    const params = new URLSearchParams();
    params.set('q', searchQuery);
    if (page > 1) {
      params.set('page', page.toString());
    }
    router.replace(`/search?${params.toString()}`, { scroll: false });
  };

  const handleSearch = async (e: React.FormEvent, page: number = 1) => {
    e.preventDefault();
    if (!query.trim()) return;

    setCurrentPage(page);
    updateURL(query, page);
    await performSearch(query, page);
  };

  const handlePageChange = (page: number) => {
    if (results && query) {
      setCurrentPage(page);
      updateURL(query, page);
      performSearch(query, page);
    }
  };

  return (
    <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
      <div className="text-center mb-12">
        <h1 className="text-4xl font-bold text-gray-900 mb-4">
          What would you like to learn today?
        </h1>
        <p className="text-lg text-gray-600">
          Search for any IT topic and we&apos;ll find or create the perfect learning resources for you
        </p>
      </div>

      <form onSubmit={handleSearch} className="mb-12">
        <div className="relative">
          <input
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="Search for Linux commands, cloud services, programming concepts..."
            className="w-full px-4 py-3 pl-12 pr-32 text-gray-900 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            disabled={loading}
          />
          <Search className="absolute left-4 top-3.5 h-5 w-5 text-gray-400" />
          <button
            type="submit"
            disabled={loading || !query.trim()}
            className="absolute right-2 top-2 px-4 py-1.5 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
          >
            {loading ? (
              <Loader2 className="h-5 w-5 animate-spin" />
            ) : (
              "Search"
            )}
          </button>
        </div>
      </form>

      {error && (
        <div className="mb-8 bg-red-50 border border-red-200 rounded-lg p-4">
          <p className="text-red-800">Error: {error}</p>
        </div>
      )}

      {/* Loading state when restoring from URL */}
      {isRestoringFromURL && (
        <div className="mb-8 bg-blue-50 border border-blue-200 rounded-lg p-4">
          <div className="flex items-center">
            <Loader2 className="h-5 w-5 text-blue-600 mr-2 animate-spin" />
            <p className="text-blue-800">Restoring your search results...</p>
          </div>
        </div>
      )}

      {/* Regular loading state for new searches */}
      {loading && !isRestoringFromURL && !results && (
        <div className="mb-8 bg-gray-50 border border-gray-200 rounded-lg p-4">
          <div className="flex items-center">
            <Loader2 className="h-5 w-5 text-gray-600 mr-2 animate-spin" />
            <p className="text-gray-700">Searching...</p>
          </div>
        </div>
      )}

      {results && (
        <div className="space-y-8">
          {results.aiSuggestions.newCategoriesAdded > 0 || results.aiSuggestions.newArticlesAdded > 0 ? (
            <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
              <div className="flex items-center">
                <Sparkles className="h-5 w-5 text-blue-600 mr-2" />
                <p className="text-blue-800">
                  AI enhanced your search: Added {results.aiSuggestions.newCategoriesAdded} new categories 
                  and {results.aiSuggestions.newArticlesAdded} new article topics!
                </p>
              </div>
            </div>
          ) : null}

          {results.categories.length > 0 && (
            <section>
              <h2 className="text-xl font-semibold text-gray-900 mb-4 flex items-center">
                <Folder className="h-5 w-5 mr-2" />
                Categories
              </h2>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {results.categories.map((category) => (
                  <Link
                    key={category.categoryId}
                    href={`/categories/${category.categoryId}`}
                    className="block p-4 bg-white rounded-lg shadow-sm border border-gray-200 hover:shadow-md transition-shadow"
                  >
                    <h3 className="font-medium text-gray-900">{category.categoryName}</h3>
                    {category.description && (
                      <p className="mt-1 text-sm text-gray-600 line-clamp-2">
                        {category.description}
                      </p>
                    )}
                  </Link>
                ))}
              </div>
            </section>
          )}

          {results.articles.length > 0 && (
            <section>
              <h2 className="text-xl font-semibold text-gray-900 mb-4 flex items-center">
                <FileText className="h-5 w-5 mr-2" />
                Articles
              </h2>
              <div className="space-y-4">
                {results.articles.map((article) => (
                  <div
                    key={article.articleId}
                    className="p-4 bg-white rounded-lg shadow-sm border border-gray-200 hover:shadow-md transition-shadow"
                  >
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <Link
                          href={`/articles/${article.articleSlug}`}
                          className="hover:text-blue-600 transition-colors"
                        >
                          <h3 className="font-medium text-gray-900 hover:text-blue-600">
                            {article.articleTitle}
                          </h3>
                        </Link>
                        <p className="mt-1 text-sm text-gray-600">
                          in {article.categories?.map(c => c.category.categoryName).join(', ') || 'No category'}
                        </p>
                        {/* Tags */}
                        {article.tags && article.tags.length > 0 && (
                          <div className="mt-2 flex flex-wrap gap-1">
                            {article.tags.map(({ tag }) => (
                              <Link
                                key={tag.tagId}
                                href={`/search?q=${encodeURIComponent(`#${tag.tagName}`)}`}
                                className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium text-white hover:opacity-80 transition-opacity"
                                style={{ 
                                  backgroundColor: tag.color || '#3B82F6',
                                }}
                                title={tag.description ? `${tag.description} - Click to find more articles with this tag` : `Click to find more articles with #${tag.tagName}`}
                              >
                                #{tag.tagName}
                              </Link>
                            ))}
                          </div>
                        )}
                      </div>
                      {!article.isContentGenerated && (
                        <span className="ml-4 text-xs text-amber-600 bg-amber-50 px-2 py-1 rounded">
                          Content pending
                        </span>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </section>
          )}

          {results.categories.length === 0 && results.articles.length === 0 && (
            <div className="text-center py-12">
              <p className="text-gray-600">
                No results found for &ldquo;{results.query}&rdquo;. Try searching for a different topic!
              </p>
            </div>
          )}

          {/* Pagination */}
          {results.pagination && results.pagination.totalPages > 1 && (
            <div className="mt-8 flex items-center justify-between">
              <div className="text-sm text-gray-600">
                Showing {((results.pagination.page - 1) * results.pagination.limit) + 1}-
                {Math.min(results.pagination.page * results.pagination.limit, results.pagination.total)} of {results.pagination.total} results
              </div>
              <div className="flex space-x-2">
                <button
                  onClick={() => handlePageChange(results.pagination!.page - 1)}
                  disabled={!results.pagination.hasPreviousPage || loading}
                  className="px-3 py-1 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50 hover:cursor-pointer disabled:opacity-50 disabled:cursor-not-allowed disabled:hover:bg-white transition-colors"
                >
                  Previous
                </button>

                {/* Page numbers */}
                <div className="flex space-x-1">
                  {Array.from({ length: Math.min(5, results.pagination.totalPages) }, (_, i) => {
                    let pageNum;
                    if (results.pagination!.totalPages <= 5) {
                      pageNum = i + 1;
                    } else if (results.pagination!.page <= 3) {
                      pageNum = i + 1;
                    } else if (results.pagination!.page >= results.pagination!.totalPages - 2) {
                      pageNum = results.pagination!.totalPages - 4 + i;
                    } else {
                      pageNum = results.pagination!.page - 2 + i;
                    }

                    return (
                      <button
                        key={pageNum}
                        onClick={() => handlePageChange(pageNum)}
                        disabled={loading}
                        className={`px-3 py-1 text-sm font-medium border rounded-md transition-colors ${
                          pageNum === results.pagination!.page
                            ? 'bg-blue-600 text-white border-blue-600 hover:bg-blue-700 hover:cursor-pointer'
                            : 'text-gray-700 bg-white border-gray-300 hover:bg-gray-50 hover:cursor-pointer'
                        } disabled:opacity-50 disabled:cursor-not-allowed`}
                      >
                        {pageNum}
                      </button>
                    );
                  })}
                </div>

                <button
                  onClick={() => handlePageChange(results.pagination!.page + 1)}
                  disabled={!results.pagination.hasNextPage || loading}
                  className="px-3 py-1 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50 hover:cursor-pointer disabled:opacity-50 disabled:cursor-not-allowed disabled:hover:bg-white transition-colors"
                >
                  Next
                </button>
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { History, User, Calendar, ChevronDown, ChevronUp } from 'lucide-react';
import { formatDistanceToNow, format } from 'date-fns';

interface Change {
  id: string;
  changeType: string;
  description: string;
  createdAt: string;
  user: {
    firstName: string | null;
    lastName: string | null;
    imageUrl: string | null;
  };
  suggestion: {
    suggestionType: string;
  } | null;
}

interface ArticleInfo {
  articleTitle: string;
  createdAt: string;
  updatedAt: string;
}

interface ArticleChangeHistoryProps {
  articleId: string;
}

export function ArticleChangeHistory({ articleId }: ArticleChangeHistoryProps) {
  const [changes, setChanges] = useState<Change[]>([]);
  const [article, setArticle] = useState<ArticleInfo | null>(null);
  const [loading, setLoading] = useState(true);
  const [isExpanded, setIsExpanded] = useState(false);

  useEffect(() => {
    loadChangeHistory();
  }, [articleId]);

  const loadChangeHistory = async () => {
    try {
      setLoading(true);
      const response = await fetch(`/api/articles/${articleId}/changes`);
      if (!response.ok) throw new Error('Failed to load change history');

      const data = await response.json();
      setChanges(data.changes);
      setArticle(data.article);
    } catch (error) {
      console.error('Error loading change history:', error);
    } finally {
      setLoading(false);
    }
  };

  const getUserName = (user: Change['user']) => {
    if (user.firstName || user.lastName) {
      return `${user.firstName || ''} ${user.lastName || ''}`.trim();
    }
    return 'Anonymous';
  };

  const getChangeTypeColor = (type: string) => {
    switch (type) {
      case 'suggestion':
        return 'bg-blue-100 text-blue-800';
      case 'rollback':
        return 'bg-orange-100 text-orange-800';
      case 'admin_edit':
        return 'bg-purple-100 text-purple-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  if (loading || !article || changes.length === 0) {
    return null;
  }

  const displayChanges = isExpanded ? changes : changes.slice(0, 3);

  return (
    <Card className="mt-8">
      <CardHeader>
        <CardTitle className="flex items-center gap-2 text-lg">
          <History className="h-5 w-5" />
          Article History
        </CardTitle>
        <CardDescription>
          This article has been improved {changes.length} time{changes.length !== 1 ? 's' : ''} by our community
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-3">
        <div className="text-sm text-muted-foreground mb-4">
          <p>
            Created {format(new Date(article.createdAt), 'MMM d, yyyy')} â€¢ 
            Last updated {formatDistanceToNow(new Date(article.updatedAt), { addSuffix: true })}
          </p>
        </div>

        <div className="space-y-3">
          {displayChanges.map((change) => (
            <div
              key={change.id}
              className="flex items-start gap-3 pb-3 border-b last:border-0 last:pb-0"
            >
              {change.user.imageUrl ? (
                <img
                  src={change.user.imageUrl}
                  alt={getUserName(change.user)}
                  className="h-8 w-8 rounded-full"
                />
              ) : (
                <div className="h-8 w-8 rounded-full bg-gray-200 flex items-center justify-center">
                  <User className="h-4 w-4 text-gray-500" />
                </div>
              )}

              <div className="flex-1 space-y-1">
                <div className="flex items-center gap-2 flex-wrap">
                  <span className="font-medium text-sm">
                    {getUserName(change.user)}
                  </span>
                  <Badge 
                    variant="secondary" 
                    className={`text-xs ${getChangeTypeColor(change.changeType)}`}
                  >
                    {change.suggestion ? change.suggestion.suggestionType : change.changeType}
                  </Badge>
                </div>

                <p className="text-sm text-muted-foreground">
                  {change.description}
                </p>

                <div className="flex items-center gap-1 text-xs text-muted-foreground">
                  <Calendar className="h-3 w-3" />
                  {formatDistanceToNow(new Date(change.createdAt), { addSuffix: true })}
                </div>
              </div>
            </div>
          ))}
        </div>

        {changes.length > 3 && (
          <Button
            variant="ghost"
            size="sm"
            onClick={() => setIsExpanded(!isExpanded)}
            className="w-full"
          >
            {isExpanded ? (
              <>
                <ChevronUp className="h-4 w-4 mr-1" />
                Show Less
              </>
            ) : (
              <>
                <ChevronDown className="h-4 w-4 mr-1" />
                Show {changes.length - 3} More Changes
              </>
            )}
          </Button>
        )}
      </CardContent>
    </Card>
  );
}

"use client";

import { useEffect, useState } from "react";
import { usePathname, useRouter } from "next/navigation";
import { useUser } from "@clerk/nextjs";
import {
  useFloating,
  autoUpdate,
  flip,
  offset,
  shift,
  useHover,
  useFocus,
  useDismiss,
  useRole,
  useInteractions,
  FloatingPortal,
} from "@floating-ui/react";
import { FolderOpen, Plus, Trash2, X, ChevronRight, Loader2 } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { toast } from "sonner";

interface ArticleGroup {
  groupId: string;
  name: string;
  articles: {
    id: string;
    scrollPosition: number;
    lastViewedAt: string | null;
    article: {
      articleId: string;
      articleTitle: string;
      articleSlug: string;
    };
  }[];
}

export function ArticleGroupsButton() {
  const { isSignedIn, isLoaded } = useUser();
  const [isOpen, setIsOpen] = useState(false);
  const [groups, setGroups] = useState<ArticleGroup[]>([]);
  const [loading, setLoading] = useState(false);
  const [creating, setCreating] = useState(false);
  const [newGroupName, setNewGroupName] = useState("");
  const [showNewGroupInput, setShowNewGroupInput] = useState(false);
  const [expandedGroup, setExpandedGroup] = useState<string | null>(null);
  const pathname = usePathname();
  const router = useRouter();
  const [currentArticleId, setCurrentArticleId] = useState<string | null>(null);

  // Extract article slug from pathname - this will update when pathname changes
  const articleMatch = pathname.match(/^\/articles\/([^\/]+)/);
  const currentArticleSlug = articleMatch ? decodeURIComponent(articleMatch[1]) : null;

  const { refs, floatingStyles, context } = useFloating({
    open: isOpen,
    onOpenChange: setIsOpen,
    middleware: [offset(10), flip(), shift()],
    whileElementsMounted: autoUpdate,
    placement: "top-start",
  });

  const hover = useHover(context, { 
    move: false,
    delay: { open: 100, close: 300 } // Add delay to prevent immediate closing
  });
  const focus = useFocus(context);
  const dismiss = useDismiss(context);
  const role = useRole(context);

  const { getReferenceProps, getFloatingProps } = useInteractions([
    hover,
    focus,
    dismiss,
    role,
  ]);

  // Fetch current article ID if on article page
  useEffect(() => {
    if (currentArticleSlug) {
      setCurrentArticleId(null); // Reset while loading
      fetch(`/api/articles?slug=${currentArticleSlug}`)
        .then((res) => res.json())
        .then((data) => {
          if (data && data.length > 0) {
            const articleId = data[0].articleId;
            setCurrentArticleId(articleId);

            // Check if we have a stored scroll position for this article
            const storedScrollPosition = sessionStorage.getItem(`article-scroll-${articleId}`);
            if (storedScrollPosition) {
              const scrollPos = parseInt(storedScrollPosition, 10);
              sessionStorage.removeItem(`article-scroll-${articleId}`);

              // Wait for the page to render, then scroll
              const attemptScroll = (attempts = 0) => {
                if (attempts > 20) return; // Give up after 2 seconds

                // Check if the page has content and is ready
                if (document.body.scrollHeight > window.innerHeight) {
                  window.scrollTo({ top: scrollPos, behavior: "instant" });

                  // Update last viewed time in the group
                  groups.forEach((group) => {
                    const article = group.articles.find((a) => a.article.articleId === articleId);
                    if (article) {
                      fetch(`/api/article-groups/${group.groupId}/articles/${articleId}/scroll`, {
                        method: "PATCH",
                        headers: { "Content-Type": "application/json" },
                        body: JSON.stringify({ scrollPosition: scrollPos }),
                      });
                    }
                  });
                } else {
                  // Try again in 100ms
                  setTimeout(() => attemptScroll(attempts + 1), 100);
                }
              };

              // Start attempting to scroll after a short delay
              setTimeout(() => attemptScroll(), 100);
            }
          }
        })
        .catch((error) => console.error("Error fetching article:", error));
    } else {
      setCurrentArticleId(null);
    }
  }, [pathname, currentArticleSlug, groups]); // Depend on pathname to ensure updates

  // Fetch article groups
  useEffect(() => {
    if (isOpen) {
      fetchGroups();
    }
  }, [isOpen]);

  const fetchGroups = async () => {
    setLoading(true);
    try {
      const response = await fetch("/api/article-groups");
      if (!response.ok) throw new Error("Failed to fetch groups");
      const data = await response.json();
      setGroups(data);
    } catch (error) {
      toast.error("Failed to load article groups");
    } finally {
      setLoading(false);
    }
  };

  const createGroup = async () => {
    if (!newGroupName.trim() || creating) return;

    if (!isSignedIn) {
      toast.error("Please sign in to create article groups");
      return;
    }

    setCreating(true);
    try {
      const response = await fetch("/api/article-groups", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name: newGroupName.trim() }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "Failed to create group");
      }

      const newGroup = await response.json();

      await fetchGroups();
      setNewGroupName("");
      setShowNewGroupInput(false);
      toast.success("Group created successfully");
    } catch (error) {
      console.error("Error creating group:", error);
      toast.error(error instanceof Error ? error.message : "Failed to create group");
    } finally {
      setCreating(false);
    }
  };

  const deleteGroup = async (groupId: string) => {
    try {
      const response = await fetch(`/api/article-groups/${groupId}`, {
        method: "DELETE",
      });

      if (!response.ok) throw new Error("Failed to delete group");

      await fetchGroups();
      toast.success("Group deleted successfully");
    } catch (error) {
      toast.error("Failed to delete group");
    }
  };

  const addCurrentArticleToGroup = async (groupId: string) => {
    if (!currentArticleId) {
      toast.error("No article selected");
      return;
    }

    try {
      const response = await fetch(`/api/article-groups/${groupId}/articles`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ articleId: currentArticleId }),
      });

      if (response.status === 409) {
        toast.info("Article already in group");
        return;
      }

      if (!response.ok) throw new Error("Failed to add article");

      await fetchGroups();
      toast.success("Article added to group");
    } catch (error) {
      toast.error("Failed to add article to group");
    }
  };

  const removeArticleFromGroup = async (groupId: string, articleId: string) => {
    try {
      const response = await fetch(
        `/api/article-groups/${groupId}/articles?articleId=${articleId}`,
        { method: "DELETE" }
      );

      if (!response.ok) throw new Error("Failed to remove article");

      await fetchGroups();
      toast.success("Article removed from group");
    } catch (error) {
      toast.error("Failed to remove article from group");
    }
  };

  const saveScrollPosition = async (groupId: string, articleId: string) => {
    const scrollPosition = window.scrollY;

    try {
      await fetch(`/api/article-groups/${groupId}/articles/${articleId}/scroll`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ scrollPosition }),
      });
    } catch (error) {
      console.error("Failed to save scroll position:", error);
    }
  };

  const navigateToArticle = async (
    groupId: string,
    articleId: string,
    articleSlug: string,
    scrollPosition: number
  ) => {
    // Save current scroll position if on an article page
    if (currentArticleId && currentArticleId !== articleId) {
      const currentGroup = groups.find((g) =>
        g.articles.some((a) => a.article.articleId === currentArticleId)
      );
      if (currentGroup) {
        await saveScrollPosition(currentGroup.groupId, currentArticleId);
      }
    }

    // Store scroll position in sessionStorage before navigation
    sessionStorage.setItem(`article-scroll-${articleId}`, scrollPosition.toString());

    // Navigate to the article
    router.push(`/articles/${articleSlug}`);
  };

  // Don't render until auth is loaded
  if (!isLoaded || !isSignedIn) {
    return null;
  }

  return (
    <>
      <Button
        ref={refs.setReference}
        className="fixed bottom-5 right-20 z-50 h-12 w-12 rounded-full shadow-lg hover:shadow-xl transition-all bg-white dark:bg-gray-800 text-gray-700 dark:text-gray-200 hover:bg-gray-50 dark:hover:bg-gray-700 border border-gray-200 dark:border-gray-600"
        size="icon"
        {...getReferenceProps()}
        title="Article Groups"
      >
        <FolderOpen className="h-5 w-5" />
      </Button>

      {isOpen && (
        <FloatingPortal>
          <div
            ref={refs.setFloating}
            style={floatingStyles}
            {...getFloatingProps()}
            className="z-50 w-80 bg-white dark:bg-gray-900 rounded-lg shadow-xl border border-gray-200 dark:border-gray-700 p-4"
          >
            <div className="flex items-center justify-between mb-4">
              <h3 className="font-semibold text-lg text-gray-100">Article Groups</h3>
              <Button
                size="icon"
                variant="ghost"
                onClick={() => setShowNewGroupInput(!showNewGroupInput)}
                className="hover:bg-gray-100 dark:hover:bg-gray-800"
                title="Create new group"
              >
                <Plus className="h-4 w-4 text-gray-600 dark:text-gray-400" />
              </Button>
            </div>

            {showNewGroupInput && (
              <form onSubmit={(e) => { e.preventDefault(); createGroup(); }} className="mb-4 flex gap-2">
                <Input
                  placeholder="Group name..."
                  value={newGroupName}
                  onChange={(e) => setNewGroupName(e.target.value)}
                  className="flex-1"
                  autoFocus
                />
                <Button 
                  size="sm" 
                  type="submit"
                  disabled={!newGroupName.trim() || creating}
                  className="bg-blue-600 hover:bg-blue-700 text-white disabled:opacity-50"
                >
                  {creating ? (
                    <Loader2 className="h-4 w-4 animate-spin" />
                  ) : (
                    "Add"
                  )}
                </Button>
                <Button
                  size="sm"
                  type="button"
                  variant="ghost"
                  onClick={() => {
                    setShowNewGroupInput(false);
                    setNewGroupName("");
                  }}
                  className="hover:bg-gray-100 dark:hover:bg-gray-800"
                >
                  <X className="h-4 w-4" />
                </Button>
              </form>
            )}

            {loading ? (
              <div className="text-center py-8 text-gray-500 dark:text-gray-400">Loading...</div>
            ) : groups.length === 0 ? (
              <div className="text-center py-8 text-gray-500 dark:text-gray-400">
                <FolderOpen className="h-12 w-12 mx-auto mb-3 text-gray-300 dark:text-gray-600" />
                <p className="text-sm">No groups yet.</p>
                <p className="text-sm">Click the + button to create one!</p>
              </div>
            ) : (
              <div className="space-y-2 max-h-96 overflow-y-auto">
                {groups.map((group) => (
                  <GroupItem
                    key={group.groupId}
                    group={group}
                    currentArticleId={currentArticleId}
                    currentArticleSlug={currentArticleSlug}
                    onDelete={deleteGroup}
                    onAddCurrentArticle={addCurrentArticleToGroup}
                    onRemoveArticle={removeArticleFromGroup}
                    onNavigateToArticle={navigateToArticle}
                    isExpanded={expandedGroup === group.groupId}
                    onToggleExpand={(groupId) => setExpandedGroup(expandedGroup === groupId ? null : groupId)}
                  />
                ))}
              </div>
            )}
          </div>
        </FloatingPortal>
      )}
    </>
  );
}

interface GroupItemProps {
  group: ArticleGroup;
  currentArticleId: string | null;
  currentArticleSlug: string | null;
  onDelete: (groupId: string) => void;
  onAddCurrentArticle: (groupId: string) => void;
  onRemoveArticle: (groupId: string, articleId: string) => void;
  onNavigateToArticle: (
    groupId: string,
    articleId: string,
    articleSlug: string,
    scrollPosition: number
  ) => void;
  isExpanded: boolean;
  onToggleExpand: (groupId: string) => void;
}

function GroupItem({
  group,
  currentArticleId,
  currentArticleSlug,
  onDelete,
  onAddCurrentArticle,
  onRemoveArticle,
  onNavigateToArticle,
  isExpanded,
  onToggleExpand,
}: GroupItemProps) {
  return (
    <div className="border border-gray-200 dark:border-gray-700 rounded-lg overflow-hidden">
      <div
        className="flex items-center justify-between p-3 hover:bg-gray-100 dark:hover:bg-gray-800 cursor-pointer group transition-colors"
        onClick={() => onToggleExpand(group.groupId)}
      >
        <div className="flex items-center gap-2 flex-1 min-w-0">
          <ChevronRight 
            className={`h-4 w-4 text-gray-400 dark:text-gray-500 flex-shrink-0 transition-transform duration-200 ${
              isExpanded ? 'rotate-90' : ''
            }`} 
          />
          <span className="truncate text-gray-900 dark:text-gray-100">{group.name}</span>
          <span className="text-xs text-gray-500 dark:text-gray-400 flex-shrink-0">
            ({group.articles.length})
          </span>
        </div>
        <div className="flex gap-1">
          {currentArticleId && (
            <Button
              size="icon"
              variant="ghost"
              className="h-7 w-7 hover:bg-gray-200 dark:hover:bg-gray-700 text-blue-600 dark:text-blue-400"
              onClick={(e) => {
                e.stopPropagation();
                onAddCurrentArticle(group.groupId);
              }}
              title="Add current article to this group"
            >
              <Plus className="h-3 w-3" />
            </Button>
          )}
          <Button
            size="icon"
            variant="ghost"
            className="h-7 w-7 text-red-500 hover:text-red-600 hover:bg-red-50 dark:hover:bg-red-900/20 opacity-0 group-hover:opacity-100 transition-opacity"
            onClick={(e) => {
              e.stopPropagation();
              onDelete(group.groupId);
            }}
            title="Delete this group"
          >
            <Trash2 className="h-3 w-3" />
          </Button>
        </div>
      </div>

      {isExpanded && group.articles.length > 0 && (
        <div className="border-t border-gray-200 dark:border-gray-700 bg-gray-50 dark:bg-gray-800/50">
          <div className="p-2 space-y-1 max-h-64 overflow-y-auto">
            {group.articles.map((item) => {
              const isCurrentArticle = item.article.articleId === currentArticleId || 
                                     (currentArticleSlug && item.article.articleSlug === currentArticleSlug);
              return (
                <div
                  key={item.id}
                  className={`flex items-center justify-between p-2 rounded-lg cursor-pointer group/article transition-colors ${
                    isCurrentArticle 
                      ? 'bg-blue-100 dark:bg-blue-900/30 border border-blue-200 dark:border-blue-800' 
                      : 'hover:bg-white dark:hover:bg-gray-800'
                  }`}
                  onClick={() =>
                    onNavigateToArticle(
                      group.groupId,
                      item.article.articleId,
                      item.article.articleSlug,
                      item.scrollPosition
                    )
                  }
                >
                  <span className={`text-sm truncate flex-1 min-w-0 pl-6 pr-2 ${
                    isCurrentArticle 
                      ? 'text-blue-700 dark:text-blue-300 font-medium' 
                      : 'text-gray-700 dark:text-gray-300'
                  }`}>
                    {item.article.articleTitle}
                  </span>
                <button
                  className="h-7 w-7 flex items-center justify-center text-gray-100 hover:text-red-300 hover:bg-red-900/30 rounded transition-colors ml-2 flex-shrink-0 border border-gray-400 bg-gray-700/50"
                  onClick={(e) => {
                    e.stopPropagation();
                    onRemoveArticle(group.groupId, item.article.articleId);
                  }}
                  title="Remove from group"
                  type="button"
                >
                  <X className="h-4 w-4" />
                </button>
              </div>
              );
            })}
          </div>
        </div>
      )}

      {isExpanded && group.articles.length === 0 && (
        <div className="border-t border-gray-200 dark:border-gray-700 bg-gray-50 dark:bg-gray-800/50 p-4 text-center text-sm text-gray-500 dark:text-gray-400">
          No articles in this group yet
        </div>
      )}
    </div>
  );
}

'use client';

import { useState, useEffect, useRef } from 'react';
import { useAuth } from '@clerk/nextjs';
import { ChatMessage } from './chat-message';
import { Send, MessageCircle, X, Loader2, Lock } from 'lucide-react';
import { useRouter } from 'next/navigation';

interface ChatInterfaceProps {
  articleId: string;
  currentExampleId?: string;
}

interface Message {
  messageId: string;
  role: 'USER' | 'ASSISTANT';
  content: string;
  createdAt: string;
  exampleId?: string | null;
}

export function ChatInterface({ articleId, currentExampleId }: ChatInterfaceProps) {
  const { isSignedIn } = useAuth();
  const router = useRouter();
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState<Message[]>([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isFetchingHistory, setIsFetchingHistory] = useState(false);
  const [subscriptionStatus, setSubscriptionStatus] = useState<{ canUseAI: boolean; loading: boolean }>({ canUseAI: false, loading: true });
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLTextAreaElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    if (isOpen && isSignedIn) {
      checkSubscriptionStatus();
      fetchChatHistory();
    }
  }, [isOpen, isSignedIn]);

  const checkSubscriptionStatus = async () => {
    try {
      const response = await fetch('/api/subscription/status');
      if (response.ok) {
        const data = await response.json();
        const canUseAI = data.tier === 'STANDARD' || data.tier === 'MAX';
        setSubscriptionStatus({ canUseAI, loading: false });
      } else {
        setSubscriptionStatus({ canUseAI: false, loading: false });
      }
    } catch (error) {
      console.error('Error checking subscription status:', error);
      setSubscriptionStatus({ canUseAI: false, loading: false });
    }
  };

  const fetchChatHistory = async () => {
    setIsFetchingHistory(true);
    try {
      const response = await fetch(`/api/articles/${articleId}/chat`);
      if (response.ok) {
        const data = await response.json();
        setMessages(data);
      }
    } catch (error) {
      console.error('Error fetching chat history:', error);
    } finally {
      setIsFetchingHistory(false);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!input.trim() || isLoading || !isSignedIn) return;

    const userInput = input.trim();
    setInput('');

    // Create temporary user message
    const tempUserMessage: Message = {
      messageId: `temp-user-${Date.now()}`,
      role: 'USER',
      content: userInput,
      createdAt: new Date().toISOString(),
      exampleId: currentExampleId,
    };

    // Add user message immediately
    setMessages(prev => [...prev, tempUserMessage]);
    setIsLoading(true);

    try {
      const response = await fetch(`/api/articles/${articleId}/chat`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          content: userInput,
          exampleId: currentExampleId,
        }),
      });

      if (response.ok) {
        const { userMessage, assistantMessage } = await response.json();
        // Replace temp message with real messages
        setMessages(prev => {
          // Remove the temporary user message
          const filtered = prev.filter(msg => msg.messageId !== tempUserMessage.messageId);
          // Add the real messages
          return [...filtered, userMessage, assistantMessage];
        });
      } else {
        // Remove temp message on error
        setMessages(prev => prev.filter(msg => msg.messageId !== tempUserMessage.messageId));

        // Check if it's a subscription error
        if (response.status === 403) {
          setSubscriptionStatus({ canUseAI: false, loading: false });
        }

        const errorData = await response.json();
        console.error('Failed to send message:', errorData.error);
      }
    } catch (error) {
      // Remove temp message on error
      setMessages(prev => prev.filter(msg => msg.messageId !== tempUserMessage.messageId));
      console.error('Error sending message:', error);
    } finally {
      setIsLoading(false);
      inputRef.current?.focus();
    }
  };

  if (!isSignedIn) {
    return null;
  }

  return (
    <>
      {/* Floating chat button */}
      <button
        onClick={() => setIsOpen(true)}
        className="fixed bottom-4 right-4 bg-blue-600 hover:bg-blue-700 text-white rounded-full p-4 shadow-lg transition-all duration-200 z-40"
        aria-label="Open AI chat"
      >
        <MessageCircle className="h-6 w-6" />
      </button>

      {/* Chat interface */}
      {isOpen && (
        <div className="fixed bottom-0 right-0 w-full sm:w-96 h-[600px] bg-white dark:bg-gray-900 shadow-2xl rounded-t-lg sm:rounded-tl-lg border border-gray-200 dark:border-gray-700 z-50 flex flex-col">
          {/* Header */}
          <div className="flex items-center justify-between p-4 border-b border-gray-200 dark:border-gray-700">
            <div className="flex items-center gap-2">
              <MessageCircle className="h-5 w-5 text-blue-600" />
              <h3 className="font-semibold">AI Tutor</h3>
            </div>
            <button
              onClick={() => setIsOpen(false)}
              className="text-gray-500 hover:text-gray-700 dark:text-gray-400 dark:hover:text-gray-200"
              aria-label="Close chat"
            >
              <X className="h-5 w-5" />
            </button>
          </div>

          {/* Messages area */}
          <div className="flex-1 overflow-y-auto p-4">
            {subscriptionStatus.loading || isFetchingHistory ? (
              <div className="flex items-center justify-center h-full">
                <Loader2 className="h-6 w-6 animate-spin text-gray-400" />
              </div>
            ) : !subscriptionStatus.canUseAI ? (
              <div className="text-center mt-8 px-4">
                <Lock className="h-12 w-12 mx-auto mb-4 text-gray-400" />
                <h3 className="text-lg font-semibold mb-2">AI Chat is a Premium Feature</h3>
                <p className="text-sm text-gray-600 dark:text-gray-400 mb-6">
                  Upgrade to Standard or Max plan to get unlimited access to our AI tutor who can help you understand concepts and answer questions about the articles.
                </p>
                <button
                  onClick={() => router.push('/pricing')}
                  className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors"
                >
                  View Pricing Plans
                </button>
              </div>
            ) : messages.length === 0 ? (
              <div className="text-center text-gray-500 dark:text-gray-400 mt-8">
                <MessageCircle className="h-12 w-12 mx-auto mb-4 text-gray-300 dark:text-gray-600" />
                <p className="text-sm">Ask me anything about this article!</p>
                {currentExampleId && (
                  <p className="text-xs mt-2">I can help you understand this quiz question.</p>
                )}
              </div>
            ) : (
              <>
                {messages.map((message) => (
                  <ChatMessage
                    key={message.messageId}
                    role={message.role}
                    content={message.content}
                    createdAt={message.createdAt}
                  />
                ))}
                {isLoading && (
                  <div className="flex justify-start mb-4">
                    <div className="max-w-[70%] rounded-lg px-4 py-2 bg-gray-100 dark:bg-gray-800">
                      <div className="flex items-center gap-2">
                        <Loader2 className="h-4 w-4 animate-spin text-gray-500" />
                        <span className="text-sm text-gray-500">AI is thinking...</span>
                      </div>
                    </div>
                  </div>
                )}
                <div ref={messagesEndRef} />
              </>
            )}
          </div>

          {/* Input area */}
          {subscriptionStatus.canUseAI && (
            <form onSubmit={handleSubmit} className="p-4 border-t border-gray-200 dark:border-gray-700">
              <div className="flex gap-2">
                <textarea
                  ref={inputRef}
                  value={input}
                  onChange={(e) => setInput(e.target.value)}
                  onKeyDown={(e) => {
                    if (e.key === 'Enter' && !e.shiftKey) {
                      e.preventDefault();
                      handleSubmit(e);
                    }
                  }}
                  placeholder="Ask a question..."
                  className="flex-1 px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 dark:bg-gray-800 dark:text-white resize-none"
                  rows={1}
                  disabled={isLoading}
                />
                <button
                  type="submit"
                  disabled={!input.trim() || isLoading}
                  className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                >
                  {isLoading ? (
                    <Loader2 className="h-5 w-5 animate-spin" />
                  ) : (
                    <Send className="h-5 w-5" />
                  )}
                </button>
              </div>
            </form>
          )}
        </div>
      )}
    </>
  );
}

"use client";

import { useState, useEffect } from "react";
import { useUser } from "@clerk/nextjs";
import { MessageSquare, Loader2 } from "lucide-react";
import CommentForm from "./comment-form";
import Comment from "./comment";

interface CommentsListProps {
  articleId: string;
}

export default function CommentsList({ articleId }: CommentsListProps) {
  const { isSignedIn } = useUser();
  const [comments, setComments] = useState<any[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const fetchComments = async () => {
    try {
      const response = await fetch(`/api/articles/${articleId}/comments`);
      if (!response.ok) {
        throw new Error("Failed to fetch comments");
      }
      const data = await response.json();
      setComments(data);
    } catch (err) {
      setError(err instanceof Error ? err.message : "An error occurred");
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchComments();
  }, [articleId]);

  const handleCommentAdded = () => {
    fetchComments();
  };

  return (
    <div className="mt-12 border-t border-gray-200 pt-8">
      <div className="mb-6">
        <h2 className="text-2xl font-bold text-gray-900 flex items-center gap-2">
          <MessageSquare className="w-6 h-6" />
          Comments
        </h2>
      </div>

      {isSignedIn && (
        <div className="mb-8">
          <CommentForm
            articleId={articleId}
            onSuccess={handleCommentAdded}
          />
        </div>
      )}

      {!isSignedIn && (
        <div className="mb-6 p-4 bg-gray-50 rounded-lg text-center">
          <p className="text-sm text-gray-600">
            Please sign in to leave a comment.
          </p>
        </div>
      )}

      {isLoading ? (
        <div className="flex items-center justify-center py-8">
          <Loader2 className="w-6 h-6 animate-spin text-gray-400" />
        </div>
      ) : error ? (
        <div className="text-center py-8">
          <p className="text-sm text-red-600">{error}</p>
        </div>
      ) : comments.length === 0 ? (
        <div className="text-center py-8">
          <p className="text-gray-500">No comments yet. Be the first to comment!</p>
        </div>
      ) : (
        <div className="space-y-0">
          {comments.map((comment) => (
            <Comment
              key={comment.commentId}
              comment={comment}
              articleId={articleId}
              onCommentDeleted={handleCommentAdded}
            />
          ))}
        </div>
      )}
    </div>
  );
}

"use client";

import { useState, useEffect, useRef } from "react";
import { useAuth } from "@clerk/nextjs";
import { useUser } from "@clerk/nextjs";
import { usePathname, useRouter } from "next/navigation";
import {
  useFloating,
  autoUpdate,
  flip,
  offset,
  shift,
  useHover,
  useFocus,
  useDismiss,
  useRole,
  useInteractions,
  FloatingPortal,
} from "@floating-ui/react";
import { 
  MessageCircle, 
  FolderOpen, 
  Plus, 
  MoreVertical,
  Loader2,
  Lock,
  X,
  Send,
  Trash2,
  ChevronRight
} from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { toast } from "sonner";
import { ChatMessage } from "./chat/chat-message";

interface FloatingActionMenuProps {
  articleId: string;
  currentExampleId?: string;
}

interface Message {
  messageId: string;
  role: 'USER' | 'ASSISTANT';
  content: string;
  createdAt: string;
  exampleId?: string | null;
}

interface ArticleGroup {
  groupId: string;
  name: string;
  articles: {
    id: string;
    scrollPosition: number;
    lastViewedAt: string | null;
    article: {
      articleId: string;
      articleTitle: string;
      articleSlug: string;
    };
  }[];
}

type MenuAction = 'chat' | 'groups';

export function FloatingActionMenu({ articleId, currentExampleId }: FloatingActionMenuProps) {
  const { isSignedIn } = useAuth();
  const { isLoaded } = useUser();
  const pathname = usePathname();
  const router = useRouter();

  // Main menu state
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [activeAction, setActiveAction] = useState<MenuAction | null>(null);

  // Chat state
  const [isChatOpen, setIsChatOpen] = useState(false);
  const [messages, setMessages] = useState<Message[]>([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isFetchingHistory, setIsFetchingHistory] = useState(false);
  const [subscriptionStatus, setSubscriptionStatus] = useState<{ canUseAI: boolean; loading: boolean }>({ canUseAI: false, loading: true });
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLTextAreaElement>(null);

  // Groups state
  const [groups, setGroups] = useState<ArticleGroup[]>([]);
  const [groupsLoading, setGroupsLoading] = useState(false);
  const [creating, setCreating] = useState(false);
  const [newGroupName, setNewGroupName] = useState("");
  const [showNewGroupInput, setShowNewGroupInput] = useState(false);
  const [expandedGroup, setExpandedGroup] = useState<string | null>(null);
  const [currentArticleId, setCurrentArticleId] = useState<string | null>(null);

  // Extract article slug from pathname
  const articleMatch = pathname.match(/^\/articles\/([^\/]+)/);
  const currentArticleSlug = articleMatch ? decodeURIComponent(articleMatch[1]) : null;

  const { refs, floatingStyles, context } = useFloating({
    open: isMenuOpen,
    onOpenChange: setIsMenuOpen,
    middleware: [offset(10), flip(), shift()],
    whileElementsMounted: autoUpdate,
    placement: "top-end",
  });

  const hover = useHover(context, { 
    move: false,
    delay: { open: 100, close: 300 }
  });
  const focus = useFocus(context);
  const dismiss = useDismiss(context);
  const role = useRole(context);

  const { getReferenceProps, getFloatingProps } = useInteractions([
    hover,
    focus,
    dismiss,
    role,
  ]);


  // Chat functions
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    if (isChatOpen && isSignedIn) {
      checkSubscriptionStatus();
      fetchChatHistory();
    }
  }, [isChatOpen, isSignedIn]);

  const checkSubscriptionStatus = async () => {
    try {
      const response = await fetch('/api/subscription/status');
      if (response.ok) {
        const data = await response.json();
        const canUseAI = data.tier === 'STANDARD' || data.tier === 'MAX';
        setSubscriptionStatus({ canUseAI, loading: false });
      } else {
        setSubscriptionStatus({ canUseAI: false, loading: false });
      }
    } catch (error) {
      console.error('Error checking subscription status:', error);
      setSubscriptionStatus({ canUseAI: false, loading: false });
    }
  };

  const fetchChatHistory = async () => {
    setIsFetchingHistory(true);
    try {
      const response = await fetch(`/api/articles/${articleId}/chat`);
      if (response.ok) {
        const data = await response.json();
        setMessages(data);
      }
    } catch (error) {
      console.error('Error fetching chat history:', error);
    } finally {
      setIsFetchingHistory(false);
    }
  };

  const handleChatSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!input.trim() || isLoading || !isSignedIn) return;

    const userInput = input.trim();
    setInput('');

    const tempUserMessage: Message = {
      messageId: `temp-user-${Date.now()}`,
      role: 'USER',
      content: userInput,
      createdAt: new Date().toISOString(),
      exampleId: currentExampleId,
    };

    setMessages(prev => [...prev, tempUserMessage]);
    setIsLoading(true);

    try {
      const response = await fetch(`/api/articles/${articleId}/chat`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          content: userInput,
          exampleId: currentExampleId,
        }),
      });

      if (response.ok) {
        const { userMessage, assistantMessage } = await response.json();
        setMessages(prev => {
          const filtered = prev.filter(msg => msg.messageId !== tempUserMessage.messageId);
          return [...filtered, userMessage, assistantMessage];
        });
      } else {
        setMessages(prev => prev.filter(msg => msg.messageId !== tempUserMessage.messageId));

        if (response.status === 403) {
          setSubscriptionStatus({ canUseAI: false, loading: false });
        }

        const errorData = await response.json();
        console.error('Failed to send message:', errorData.error);
      }
    } catch (error) {
      setMessages(prev => prev.filter(msg => msg.messageId !== tempUserMessage.messageId));
      console.error('Error sending message:', error);
    } finally {
      setIsLoading(false);
      inputRef.current?.focus();
    }
  };

  // Groups functions
  useEffect(() => {
    if (currentArticleSlug) {
      setCurrentArticleId(null);
      fetch(`/api/articles?slug=${currentArticleSlug}`)
        .then((res) => res.json())
        .then((data) => {
          if (data && data.length > 0) {
            const articleId = data[0].articleId;
            setCurrentArticleId(articleId);

            const storedScrollPosition = sessionStorage.getItem(`article-scroll-${articleId}`);
            if (storedScrollPosition) {
              const scrollPos = parseInt(storedScrollPosition, 10);
              sessionStorage.removeItem(`article-scroll-${articleId}`);

              const attemptScroll = (attempts = 0) => {
                if (attempts > 20) return;

                if (document.body.scrollHeight > window.innerHeight) {
                  window.scrollTo({ top: scrollPos, behavior: "instant" });

                  groups.forEach((group) => {
                    const article = group.articles.find((a) => a.article.articleId === articleId);
                    if (article) {
                      fetch(`/api/article-groups/${group.groupId}/articles/${articleId}/scroll`, {
                        method: "PATCH",
                        headers: { "Content-Type": "application/json" },
                        body: JSON.stringify({ scrollPosition: scrollPos }),
                      });
                    }
                  });
                } else {
                  setTimeout(() => attemptScroll(attempts + 1), 100);
                }
              };

              setTimeout(() => attemptScroll(), 100);
            }
          }
        })
        .catch((error) => console.error("Error fetching article:", error));
    } else {
      setCurrentArticleId(null);
    }
  }, [pathname, currentArticleSlug, groups]);

  const fetchGroups = async () => {
    setGroupsLoading(true);
    try {
      const response = await fetch("/api/article-groups");
      if (!response.ok) throw new Error("Failed to fetch groups");
      const data = await response.json();
      setGroups(data);
    } catch (error) {
      toast.error("Failed to load article groups");
    } finally {
      setGroupsLoading(false);
    }
  };

  const createGroup = async () => {
    if (!newGroupName.trim() || creating) return;

    if (!isSignedIn) {
      toast.error("Please sign in to create article groups");
      return;
    }

    setCreating(true);
    try {
      const response = await fetch("/api/article-groups", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name: newGroupName.trim() }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "Failed to create group");
      }

      await fetchGroups();
      setNewGroupName("");
      setShowNewGroupInput(false);
      toast.success("Group created successfully");
    } catch (error) {
      console.error("Error creating group:", error);
      toast.error(error instanceof Error ? error.message : "Failed to create group");
    } finally {
      setCreating(false);
    }
  };

  const deleteGroup = async (groupId: string) => {
    try {
      const response = await fetch(`/api/article-groups/${groupId}`, {
        method: "DELETE",
      });

      if (!response.ok) throw new Error("Failed to delete group");

      await fetchGroups();
      toast.success("Group deleted successfully");
    } catch (error) {
      toast.error("Failed to delete group");
    }
  };

  const addCurrentArticleToGroup = async (groupId: string) => {
    if (!currentArticleId) {
      toast.error("No article selected");
      return;
    }

    try {
      const response = await fetch(`/api/article-groups/${groupId}/articles`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ articleId: currentArticleId }),
      });

      if (response.status === 409) {
        toast.info("Article already in group");
        return;
      }

      if (!response.ok) throw new Error("Failed to add article");

      await fetchGroups();
      toast.success("Article added to group");
    } catch (error) {
      toast.error("Failed to add article to group");
    }
  };

  const removeArticleFromGroup = async (groupId: string, articleId: string) => {
    try {
      const response = await fetch(
        `/api/article-groups/${groupId}/articles?articleId=${articleId}`,
        { method: "DELETE" }
      );

      if (!response.ok) throw new Error("Failed to remove article");

      await fetchGroups();
      toast.success("Article removed from group");
    } catch (error) {
      toast.error("Failed to remove article from group");
    }
  };

  const saveScrollPosition = async (groupId: string, articleId: string) => {
    const scrollPosition = window.scrollY;

    try {
      await fetch(`/api/article-groups/${groupId}/articles/${articleId}/scroll`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ scrollPosition }),
      });
    } catch (error) {
      console.error("Failed to save scroll position:", error);
    }
  };

  const navigateToArticle = async (
    groupId: string,
    articleId: string,
    articleSlug: string,
    scrollPosition: number
  ) => {
    if (currentArticleId && currentArticleId !== articleId) {
      const currentGroup = groups.find((g) =>
        g.articles.some((a) => a.article.articleId === currentArticleId)
      );
      if (currentGroup) {
        await saveScrollPosition(currentGroup.groupId, currentArticleId);
      }
    }

    sessionStorage.setItem(`article-scroll-${articleId}`, scrollPosition.toString());
    router.push(`/articles/${articleSlug}`);
  };

  // Menu action handlers
  const handleMenuAction = (action: MenuAction) => {
    setActiveAction(action);
    setIsMenuOpen(false);

    if (action === 'chat') {
      setIsChatOpen(true);
    } else if (action === 'groups') {
      fetchGroups();
    }
  };

  const closeAllActions = () => {
    setActiveAction(null);
    setIsChatOpen(false);
    setShowNewGroupInput(false);
    setExpandedGroup(null);
  };

  if (!isLoaded || !isSignedIn) {
    return null;
  }

  return (
    <>
      {/* Floating multi-action button */}
      <Button
        ref={refs.setReference}
        className="fixed bottom-4 right-4 z-40 h-14 w-14 rounded-full shadow-lg hover:shadow-xl transition-all duration-300 bg-black hover:bg-gray-800 text-white border-0"
        size="icon"
        {...getReferenceProps()}
        title="Actions Menu"
      >
        <MoreVertical className="h-6 w-6" />
      </Button>

      {/* Menu popup */}
      {isMenuOpen && (
        <FloatingPortal>
          <div
            ref={refs.setFloating}
            style={floatingStyles}
            {...getFloatingProps()}
            className="z-50 bg-white dark:bg-gray-900 rounded-lg shadow-xl border border-gray-200 dark:border-gray-700 overflow-hidden"
          >
            <div className="p-2 space-y-1">
              <Button
                onClick={() => handleMenuAction('chat')}
                className="w-full justify-start gap-3 text-left bg-transparent hover:bg-gray-100 dark:hover:bg-gray-800 text-gray-700 dark:text-gray-200 border-0 transition-colors"
                size="sm"
              >
                <MessageCircle className="h-4 w-4" />
                AI Chat
              </Button>
              <Button
                onClick={() => handleMenuAction('groups')}
                className="w-full justify-start gap-3 text-left bg-transparent hover:bg-gray-100 dark:hover:bg-gray-800 text-gray-700 dark:text-gray-200 border-0 transition-colors"
                size="sm"
              >
                <FolderOpen className="h-4 w-4" />
                Article Groups
              </Button>
            </div>
          </div>
        </FloatingPortal>
      )}

      {/* Chat interface */}
      {isChatOpen && (
        <div className="fixed bottom-0 right-0 w-full sm:w-96 h-[600px] bg-white dark:bg-gray-900 shadow-2xl rounded-t-lg sm:rounded-tl-lg border border-gray-200 dark:border-gray-700 z-50 flex flex-col">
          <div className="flex items-center justify-between p-4 border-b border-gray-200 dark:border-gray-700">
            <div className="flex items-center gap-2">
              <MessageCircle className="h-5 w-5 text-gray-700 dark:text-gray-200" />
              <h3 className="font-semibold">AI Tutor</h3>
            </div>
            <button
              onClick={() => setIsChatOpen(false)}
              className="text-gray-500 hover:text-gray-700 dark:text-gray-400 dark:hover:text-gray-200"
              aria-label="Close chat"
            >
              <X className="h-5 w-5" />
            </button>
          </div>

          <div className="flex-1 overflow-y-auto p-4">
            {subscriptionStatus.loading || isFetchingHistory ? (
              <div className="flex items-center justify-center h-full">
                <Loader2 className="h-6 w-6 animate-spin text-gray-400" />
              </div>
            ) : !subscriptionStatus.canUseAI ? (
              <div className="text-center mt-8 px-4">
                <Lock className="h-12 w-12 mx-auto mb-4 text-gray-400" />
                <h3 className="text-lg font-semibold mb-2">AI Chat is a Premium Feature</h3>
                <p className="text-sm text-gray-600 dark:text-gray-400 mb-6">
                  Upgrade to Standard or Max plan to get unlimited access to our AI tutor who can help you understand concepts and answer questions about the articles.
                </p>
                <button
                  onClick={() => router.push('/pricing')}
                  className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors"
                >
                  View Pricing Plans
                </button>
              </div>
            ) : messages.length === 0 ? (
              <div className="text-center text-gray-500 dark:text-gray-400 mt-8">
                <MessageCircle className="h-12 w-12 mx-auto mb-4 text-gray-300 dark:text-gray-600" />
                <p className="text-sm">Ask me anything about this article!</p>
                {currentExampleId && (
                  <p className="text-xs mt-2">I can help you understand this quiz question.</p>
                )}
              </div>
            ) : (
              <>
                {messages.map((message) => (
                  <ChatMessage
                    key={message.messageId}
                    role={message.role}
                    content={message.content}
                    createdAt={message.createdAt}
                  />
                ))}
                {isLoading && (
                  <div className="flex justify-start mb-4">
                    <div className="max-w-[70%] rounded-lg px-4 py-2 bg-gray-100 dark:bg-gray-800">
                      <div className="flex items-center gap-2">
                        <Loader2 className="h-4 w-4 animate-spin text-gray-500" />
                        <span className="text-sm text-gray-500">AI is thinking...</span>
                      </div>
                    </div>
                  </div>
                )}
                <div ref={messagesEndRef} />
              </>
            )}
          </div>

          {subscriptionStatus.canUseAI && (
            <form onSubmit={handleChatSubmit} className="p-4 border-t border-gray-200 dark:border-gray-700">
              <div className="flex gap-2">
                <textarea
                  ref={inputRef}
                  value={input}
                  onChange={(e) => setInput(e.target.value)}
                  onKeyDown={(e) => {
                    if (e.key === 'Enter' && !e.shiftKey) {
                      e.preventDefault();
                      handleChatSubmit(e);
                    }
                  }}
                  placeholder="Ask a question..."
                  className="flex-1 px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 dark:bg-gray-800 dark:text-white resize-none"
                  rows={1}
                  disabled={isLoading}
                />
                <button
                  type="submit"
                  disabled={!input.trim() || isLoading}
                  className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                >
                  {isLoading ? (
                    <Loader2 className="h-5 w-5 animate-spin" />
                  ) : (
                    <Send className="h-5 w-5" />
                  )}
                </button>
              </div>
            </form>
          )}
        </div>
      )}

      {/* Groups interface */}
      {activeAction === 'groups' && (
        <div className="fixed bottom-0 right-0 w-full sm:w-96 h-[600px] bg-white dark:bg-gray-900 shadow-2xl rounded-t-lg sm:rounded-tl-lg border border-gray-200 dark:border-gray-700 z-50 flex flex-col">
          <div className="flex items-center justify-between p-4 border-b border-gray-200 dark:border-gray-700">
            <div className="flex items-center gap-2">
              <FolderOpen className="h-5 w-5 text-gray-700 dark:text-gray-200" />
              <h3 className="font-semibold text-gray-100">Article Groups</h3>
            </div>
            <div className="flex gap-2">
              <Button
                size="icon"
                variant="ghost"
                onClick={() => setShowNewGroupInput(!showNewGroupInput)}
                className="hover:bg-gray-100 dark:hover:bg-gray-800"
                title="Create new group"
              >
                <Plus className="h-4 w-4 text-gray-600 dark:text-gray-400" />
              </Button>
              <button
                onClick={closeAllActions}
                className="text-gray-500 hover:text-gray-700 dark:text-gray-400 dark:hover:text-gray-200"
                aria-label="Close groups"
              >
                <X className="h-5 w-5" />
              </button>
            </div>
          </div>

          <div className="flex-1 overflow-y-auto p-4">
            {showNewGroupInput && (
              <form onSubmit={(e) => { e.preventDefault(); createGroup(); }} className="mb-4 flex gap-2">
                <Input
                  placeholder="Group name..."
                  value={newGroupName}
                  onChange={(e) => setNewGroupName(e.target.value)}
                  className="flex-1"
                  autoFocus
                />
                <Button 
                  size="sm" 
                  type="submit"
                  disabled={!newGroupName.trim() || creating}
                  className="bg-gray-700 hover:bg-gray-800 dark:bg-gray-600 dark:hover:bg-gray-700 text-white disabled:opacity-50"
                >
                  {creating ? (
